(this["webpackJsonppersonal-site"]=this["webpackJsonppersonal-site"]||[]).push([[10],{174:function(n,e,s){"use strict";s.r(e);var t=s(0),r=s(48),c=(s(1),s(3)),i=s(53),A=s.n(i),a=s(21),o=function(n){var e=Object.assign({},n);return Object(t.jsx)(c.b,Object(r.a)({},e))};e.default=function(){return Object(t.jsx)(a.a,{title:"aram",description:"aram",children:Object(t.jsxs)("article",{className:"post markdown",id:"yes",children:[Object(t.jsx)("header",{children:Object(t.jsx)("div",{className:"title",children:Object(t.jsx)("h2",{"data-testid":"heading",children:Object(t.jsx)(c.b,{to:"/augustine",children:"ARAM ADC"})})})}),Object(t.jsx)(A.a,{source:"- \u203b \uc81c \uae00\uc5d0\uc11c ()\uc758 \ub73b\uc740 \ub450\uac00\uc9c0\uc785\ub2c8\ub2e4. \ucc28\uc120\uc73c\ub85c \uc120\ud0dd\ud560 \uc218 \uc788\ub294 \uc544\uc774\ud15c\uc774\uac70\ub098 \ub3d9\uc77c\ud55c \uc544\uc774\ud15c\uba85. ex. \ud06c\ub77c\ucf04(\ub3cc\ud48d) ex.\ubb34\ud55c\uc758\ub300\uac80(\uc778\ud53c)\n\u203b \uc81c \uae00\uc5d0\uc11c /\uc758 \ub73b\uc740 \uc120\ud0dd\uc758 \uc758\ubbf8\uc785\ub2c8\ub2e4. 1/2/3/4/5 \uc911 \uc0c1\ud669\ubcf4\uace0 \uc120\ud0dd\ud574\uc11c \ud558\ub098\ub97c \uace0\ub974\ub77c\ub294 \ub73b\uc785\ub2c8\ub2e4.\n\n- \ud611\uace1 \uc6d0\ub51c\uacfc \uce7c\ubc14\ub78c \uc6d0\ub51c\uc758 \ucc28\uc774\uc640 \uae30\ubcf8 \uac1c\ub150\n \n- \u203b \uce7c\ubc14\ub78c \uc6d0\ub51c\uc740 \ud68c\ubcf5 \uc2a4\ud3a0\uc744 \ub4e4\uc9c0 \uc54a\ub294\ub2e4. \uc815\ud654\uac00 \ucd5c\uc120\uc774\uba70, \ucc28\uc120\uc73c\ub85c \ud0c8\uc9c4\uc744 \ub4e4\uc5b4\uc904 \uc218 \uc788\ub2e4.\n\uce7c\ubc14\ub78c \ud53d\ucc3d \ub2e8\uacc4\uc5d0\uc11c \uc6d0\ub51c\uc774 \ud68c\ubcf5\ub4e4\uace0 \uc788\ub2e4? \uc77c\ub2e8 \uce7c\ubc14\ub78c \uc720\uc800\ub294 \uc544\ub2c8\uad6c\ub098 \ud558\uc2dc\uba74 \ub429\ub2c8\ub2e4.\n \n- \uce7c\ubc14\ub78c \ud68c\ubcf5 \uc2a4\ud3a0\uc740 \u2460\ud611\uace1 \ubc14\ud140\uc740 2\ub3002 \uc2f8\uc6c0\uc774\ub77c \ub77c\uc778\uc804 \ub2e8\uacc4\uc5d0\uc11c \ud790\ub7c9\uc774 2\ub3002 \uc2f8\uc6c0\uc5d0\uc11c \uc5c4\uccad\ub098\uac8c \ud070 \ubcc0\uc218\ub85c \uc791\uc6a9\ud558\uc9c0\ub9cc \uce7c\ubc14\ub78c\uc740 5\ub3005 \ub9f5\uc774\ub77c \ud790\ub7c9 \uc790\uccb4\uac00 \ud070 \ubcc0\uc218\ub85c \uc791\uc6a9\ud558\uc9c0 \uc54a\uc74c \u2461\uce7c\ubc14\ub78c\ub9f5\uc740 \ud611\uace1\uacfc \ub2e4\ub974\uac8c \ud0c0\uc778\uc5d0\uac8c \uc8fc\ub294 \ud790\ub7c9 \uc808\ubc18 \ub108\ud504 \uba39\uace0 \uc2dc\uc791\ud558\ubbc0\ub85c \ud0c0\uc778\uc5d0\uac8c \uc8fc\ub294 \ud790\ub7c9 \uc790\uccb4\uac00 \uc801\uc74c \u2462\uce7c\ubc14\ub78c\uc5d0\uc120 \ud611\uace1\ubcf4\ub2e4 \uce58\uc720\uac10\uc18c \uc544\uc774\ud15c\uc744 \ub354 \uc801\uadf9\uc801\uc73c\ub85c \uc0ac\uae30 \ub54c\ubb38\uc5d0 \uce58\uc720\uac10\uc18c\uc5d0 \ub610 \ud55c\ubc88 \uce74\uc6b4\ud130 \ub2f9\ud568 \u2463\uad50\uc804\uacfc \uc2f8\uc6c0\ub9cc \ud558\ub294 \uce7c\ubc14\ub78c\ub9f5\uc5d0\uc11c\ub294 \uc18c\ud658\uc0ac \uc8fc\ubb38\uc758 \ud68c\uc804\uc728\uc774 \uc911\uc694\ud55c\ub370 \uc810\uba78\uc744 \uc81c\uc678\ud55c \uac00\uc7a5 \uae34 \uc2a4\ud3a0 \ucfe8\ud0c0\uc784\uc744 \uac16\uace0 \uc788\uc74c \u2464\ud68c\ubcf5 \uc21c\uac04 \uc774\uc18d \uc99d\uac00\ub97c \uc774\uc6a9\ud55c \uac31\ud68c\ud53c\uac00 \uc5c6\uc74c \ub4f1 \uc5ec\ub7ec\uac00\uc9c0 \uc774\uc720\ub85c \uce7c\ubc14\ub78c \uc6d0\ub51c\uc740 \ud68c\ubcf5 \ub4dc\ub294\uac70 \uc544\ub2d9\ub2c8\ub2e4.\n \n- \uce7c\uc815\ud654\uac00 \uc5b4\ub824\uc6b4 \uc0ac\ub78c\uc740 \uc77c\ub2e8 \uce7c\uc815\ud654 \ubc18\uc751 \uc5f0\uc2b5\ud558\ub294 \uac83\uc744 \ucd94\ucc9c\ud558\uba70, \uc815\ub9d0 \ub098\ub294 \uc815\ud654 \ubabb\uc4f0\uaca0\ub2e4 \uc2f6\uc73c\uba74 \ud0c8\uc9c4\uc744 \uc4f0\uc138\uc694. \uce7c\uc815\ud654\ub294 \uc0ac\uc2e4 \ubc18\uc751\uc18d\ub3c4\ubcf4\ub2e4 \uba38\ub9ac\ub85c \uc0c1\ub300 CC\uae30 \ubb50 \uc788\ub294\uc9c0 \ubb58 \ub9de\uc73c\uba74 \uc815\ud654\ub97c \uc368\uc57c \ud560\uae4c \ubbf8\ub9ac \uc0dd\uac01\ud574\ub450\ub294\uac8c \uc911\uc694\ud569\ub2c8\ub2e4.\n \n-  \uadf8\ub9ac\uace0 \uc6b0\ub9ac\ud300\uc774 \ub3cc\uc9c4 \ucc54\ud504 \ub4e4\uc5b4\uc654\uc744 \ub54c \ub300\ucc98\ud560 \uc218 \uc5c6\ub294 \uc0c1\ud669(\uc55e\ub77c\uc7781 + \ub51c\ub7ec4 \uac19\uc740 \uc0c1\ud669)\uc774\uac70\ub098 \ud3c9\ud0c0\ud615 \uc6d0\ub51c\uc774 \ub9ce\uc544\uc838\uc11c \uc55e\ub77c\uc778\uc774 \ubd80\uc2e4\ud574\uc9c4 \uacbd\uc6b0\uc5d0\ub294 \ud0f1\ucee4 \ub354 \ubabb \uc7a1\ub294 \uc6d0\ub51c\uc774 \ud0c8\uc9c4\uc73c\ub85c \ubc14\uafd4\uc8fc\ub294\uac8c \uc88b\uc2b5\ub2c8\ub2e4. (ex \uc6b0\ub9ac\ud300\uc5d0 \ubca0\uc778\uacfc \uc9c4\uc774 \uc788\ub294\ub370 \uc55e\ub77c\uc778\uc774 \ubd80\uc2e4\ud558\ub2e4? \uc774\ub7ec\uba74 \ub458 \ub2e4 \uc815\ud654 \ub4e4\uace0 \uc788\ub294 \uac83\ubcf4\ub2e4 \uc9c4\uc774 \ud0c8\uc9c4\uc73c\ub85c \ubc14\uafd4\uc8fc\ub294\uac8c \uc88b\uc2b5\ub2c8\ub2e4.)\n \n\ub2e4\ub9cc, \uc77c\ubc18\uc801\uc778 \uc0c1\ud669\uc5d0 \ubd24\uc744 \ub54c\ub294 \uc6d0\ub51c\uc774 \uc815\ud654\ub97c \ub4dc\ub294 \uac83\uc774 \uac00\uc7a5 \uc88b\uc740 \uc120\ud0dd\uc774\uba70, \ud0c8\uc9c4\uc740 \uc6b0\ub9ac\ud300 \uc720\ud2f8 \uc11c\ud3ec\ud130\ub098 \ud3ec\ud0b9 \uacc4\uc5f4\uc758 \ucc54\ud504\uac00 \ub4e4\uc5b4\uc8fc\ub294 \uac83\uc774 \ubca0\uc2a4\ud2b8\uc785\ub2c8\ub2e4.\n \n\uc18c\ud658\uc0ac \uc8fc\ubb38(\uc2a4\ud3a0)\uc5d0\uc5d0 \uad00\ud55c \uc790\uc138\ud55c \ub0b4\uc6a9\uc740 [\uc785\ubb38-4] \uc2dc\uc98c11 \uce7c\ubc14\ub78c \uc2a4\ud3a0 \uc815\ub9ac\uae00\uc744 \ucc38\uace0\ud558\uc2dc\uae38 \ubc14\ub78d\ub2c8\ub2e4.\n \n \n\u203b \uce7c\ubc14\ub78c \uc6d0\ub51c\uc740 \uc2b9\uc804\ubcf4 \ub8ec\uc744 \ub4e4\uc9c0 \uc54a\ub294\ub2e4.\n\uce7c\ubc14\ub78c \uc6d0\ub51c \uc2b9\uc804\ubcf4\ub294 \ubc84\ub9ac\uc138\uc694. \ud611\uace1\uc5d0\uc11c\ub294 \uc2b9\uc804\ubcf4 \uc218\uce58\uac00 12%\uc774\uc9c0\ub9cc, \uce7c\ubc14\ub78c\uc740 \ud611\uace1\uc5d0 \ube44\ud574\uc11c \uc808\ubc18\uc778 6%\ubc16\uc5d0 \ub418\uc9c0 \uc54a\uc2b5\ub2c8\ub2e4. \ud53c\ud1b5 \uc790\uccb4\uac00 \uc801\uc740 \uc6d0\ub51c\uc5d0\uac8c \uc788\uc5b4 \uc2b9\uc804\ubcf4\ub294 \ud06c\uac8c \ub3c4\uc6c0\uc774 \ub418\uc9c0 \uc54a\uc2b5\ub2c8\ub2e4. \uc2b9\uc804\ubcf4 \ub4e4\uc5b4\ubcf4\uc2dc\uace0 \uc218\uce58 \ud55c\ubc88 \ubd10\ubcf4\uc138\uc694. \uad49\uc7a5\ud788 \uc801\uc2b5\ub2c8\ub2e4. \ud611\uace1\uc5d0\uc11c\ub294 \uc218\uce58\ub3c4 12%\ub85c \ub450\ubc30\uc774\uace0, \ud55c\ud2f1\uc73c\ub85c \uc0b4\uc544\ub0a8\ub290\ub0d0 \uc548\ub0a8\ub290\ub0d0\uac00 \ub354\ube14\ud0ac\uc774\ub098 \uc0dd\uc874\uc5d0 \ub3c4\uc6c0\uc774 \ub418\uae30\ub3c4 \ud558\uc9c0\ub9cc, \uce7c\ubc14\ub78c\uc740 \uadf8\ub807\uac8c \ud55c\ud2f1\uc73c\ub85c \uc0b4\uc544\ub0a8\uc544\ub3c4 \uadc0\ud658\uc774 \uc5c6\uc73c\ubbc0\ub85c \uacb0\uad6d \uc8fd\uc5b4\uc57c\ud558\uae30 \ub54c\ubb38\uc5d0 \ud611\uace1\uc5d0 \ube44\ud574\uc11c \uba54\ub9ac\ud2b8\uac00 \ub9ce\uc774 \ub5a8\uc5b4\uc9d1\ub2c8\ub2e4.\n \n\uc608\ub97c \ub4e4\uc5b4 \ud611\uace1 \ud2b8\ub9ac\uc2a4\ud0c0\ub098\ub294 \ub9c8\ub098\uc18c\ubaa8\uac00 \uc801\uace0 \ubab8\uc744 \ub4e4\uc774\ubbf8\ub294 \ucc54\ud504\ub77c \uc774 \ub77c\uc778\uc5d0\uc11c \uc2b9\uc804\ubcf4 \ud53d\ub960\uc774 \uc81c\uc77c \ub192\uc9c0\ub9cc, \uce7c\ubc14\ub78c \ud2b8\ud0c0\ub294 \ub2e8\uc5f0\ucf54 \uacfc\ub2e4\uce58\uc720 \ucc0d\ub294\uac8c \uc88b\uc2b5\ub2c8\ub2e4.\n \n\uce7c\ubc14\ub78c \uc6d0\ub51c\uc740 \uce68\ucc29or\uacfc\ub2e4\uce58\uc720 2\uac00\uc9c0 \uc911 \ud558\ub098\uc785\ub2c8\ub2e4. \uce7c\ubc14\ub78c \uc6d0\ub51c\uc740 \uc2b9\uc804\ubcf4 \ubc84\ub9ac\uc138\uc694.\n \n \n\u203b \uce7c\ubc14\ub78c \uc6d0\ub51c\uc740 \uae30\ubcf8\uc801\uc73c\ub85c \u2460\uc815\ubc00 + \uc9c0\ubc30, \u2461\uc9c0\ubc30 + \uc815\ubc00\uc774 \uae30\ubcf8\uc774\ub77c\uace0 \ubcf4\uba74 \ub41c\ub2e4.\n\ud611\uace1\uc5d0\uc11c\uc758 \uc6d0\ub51c\uc740 \uad76\uc8fc\ub9b0\uc0ac\ub0e5\uafbc\uc744 \ub4e4\uc5b4\uc8fc\ub294 \uacbd\uc6b0\uac00 \uc2dc\uc98c11 \ub4e4\uc5b4\uc640\uc11c \ub9ce\uc544\uc9c0\uae34 \ud588\uc9c0\ub9cc, \uc5ec\uc804\ud788 \ubcf4\uc870\ub8ec\uc5d0 \uc601\uac10\ucabd\ub3c4 \uad49\uc7a5\ud788 \ub9ce\uc774 \uc4f0\uc774\uace0 \uc788\uc2b5\ub2c8\ub2e4. \n \n\ud2b9\ud788 \uc694\uc0c8 \ud611\uace1\uc5d0\uc11c\ub294 \ubcf4\uc870\ub8ec\uc5d0 \ube44\uc2a4\ud0b7\ubc30\ub2ec + \uc6b0\uc8fc\uc801\ud1b5\ucc30\ub825\uc744 \ucc0d\uac70\ub098 \uc774\uc988\ub9ac\uc5bc\uac19\uc740 \uacbd\uc6b0\uc5d0\ub294 \ub9c8\ub098\uc21c\ud658\ud314\ucc0c \uae68\ub2ec\uc74c\uac19\uc740 \ub8ec\uc744 \ub9ce\uc774 \ucc0d\uc2b5\ub2c8\ub2e4.\n \n\ud558\uc9c0\ub9cc, \uce7c\ubc14\ub78c\uc5d0\uc11c\ub294 \ub77c\uc778\uc804\uc5d0 \ud798\uc744 \uc2e3\ub294 \ube44\uc2a4\ud0b7 \ubc30\ub2ec\uc774\ub098 \ub3cc\ud48d\uc774\ub098 \uc2a4\ud3a0 \ucfe8\uc744 \uc904\uc774\uae30 \uc704\ud55c \uc6b0\uc8fc\uc801\ud1b5\ucc30\ub825\uc744 \ucc0d\ub294 \uac83\ubcf4\ub2e4 \uad76\uc8fc\ub9b0\uc0ac\ub0e5\uafbc\uacfc \ud53c\uc758\ub9db\uc744 \ucc0d\uc5b4\uc8fc\uba74\uc11c \uc720\uc9c0\ub825\uc744 \ud0a4\uc6b0\ub294 \uac83\uc774 \ud6e8\uc52c \ub354 \ud070 \ub3c4\uc6c0\uc774 \ub429\ub2c8\ub2e4.\n \n\uce7c\ubc14\ub78c \ud3c9\ud0c0 \uc6d0\ub51c \uc911\uc5d0\uc11c \uc601\uac10\ucc0d\ub294 \ucc54\ud504\ub294 \uc560\uc26c(\ucf8c\uc18d\uc811\uadfc)\uacfc \uc138\ub098(\ube59\uacb0\uac15\ud654) \ub458 \ubc16\uc5d0 \uc5c6\uc2b5\ub2c8\ub2e4.\n\ubcf4\uc870\ub8ec \ub9c8\ubc95\ub3c4 \ud544\uc694\uc5c6\uc2b5\ub2c8\ub2e4. \uae68\ub2ec\uc74c \ub9c8\uc21c\ud314\ubcf4\ub2e4 \uad76\uc8fc\ub9b0\uc0ac\ub0e5\uafbc \ud53c\uc758\ub9db\uc774 \ub354 \uc88b\uc2b5\ub2c8\ub2e4.\n \n\uadf8\ub807\uae30 \ub54c\ubb38\uc5d0 \uce7c\ubc14\ub78c \uc6d0\ub51c\uc740 \uc815\ubc00+\uc9c0\ubc30, \uc9c0\ubc30+\uc815\ubc00\uc744 \ub4dc\ub294 \uac83\uc774 \uae30\ubcf8\uc785\ub2c8\ub2e4. \n \n \n\u203b \uce7c\ubc14\ub78c \uc6d0\ub51c\uc740 \uc218\ud638\uc790\uc758 \ub9dd\uce58\uc744 \uad6c\ub9e4\ud558\ub77c.\n\uadf8\ub3d9\uc548 \uc218\ud638\uc790\uc758\ubfd4\ud53c\ub9ac(=\ubfd4\ud53c\ub9ac)\ub294 \uce7c\ubc14\ub78c \uc870\uae08\ub9cc \ud560 \uc904 \uc544\ub294 \uc0ac\ub78c\uc774\uba74 \ud0f1\ucee4\ub098 \ube0c\ub8e8\uc800\ub3c4 \uc0ac\ub78c\ub4e4\uc774 \ub9ce\uc774 \uad6c\ub9e4\ud588\uc9c0\ub9cc, \uc218\ud638\uc790\uc758 \ub9dd\uce58 \uc778\uc2dd\uc740 \uce7c\ubc14\ub78c \uc870\uae08 \ud560 \uc904 \uc544\ub294 \uc0ac\ub78c\uc774\uc5b4\ub3c4 \ud638\ubd88\ud638\uac00 \uac08\ub838\uc2b5\ub2c8\ub2e4.\n \n\uc2dc\uc98c11\uc774 \ub418\uba74\uc11c \uc218\ud638\uc790 \uc2dc\ub9ac\uc988\uc758 \ud310\ub9e4 \uac00\uaca9 70%\ub85c \uc62c\ub77c\uac00\uace0, \uc804\uc124\uae09 \ub300\uc6b0\ub97c \ud574\uc8fc\uba74\uc11c \uc2e0\ud654 \uc635\uc158 \ud6a8\uacfc\ub3c4 \uc8fc\uae30 \ub54c\ubb38\uc5d0 \uc548\uac08 \uc774\uc720\uac00 \uc5c6\uc2b5\ub2c8\ub2e4. \uc774\uc81c \uc2e0\ud654\ud15c\uc5d0 BF\ub300\uac80\uc774 \ub4e4\uc5b4\uac00\ub294 \uac83\ub3c4 \uc544\ub2c8\ub77c\uc11c \uc608\uc804\ucc98\ub7fc 1300\uace8\ub4dc\ub97c \ubabb \ubaa8\uc73c\uba74 BF\ub300\uac80\uc744 \ubabb\uc0ac\uc11c \ud15c\ud2b8\ub9ac\uac00 \uaf2c\uc774\ub294 \ub2e8\uc810\ub3c4 \uc0ac\ub77c\uc84c\uc2b5\ub2c8\ub2e4. \n \n\ud54f\ube5b\uae38\uacfc \uad76\uc8fc\ub9b0\uc0ac\ub0e5\uafbc\uc744 \ucc0d\uc5c8\ub2e4\ud558\ub354\ub77c\ub3c4 \ucd08\ubc18\ubd80\ud130 \uc2a4\ud0dd\uc744 \uc313\uace0 \uc2dc\uc791\ud558\ub294\uac8c \uc544\ub2c8\uace0 \uc2a4\ud0dd\uc744 \uc313\ub294\ub370\ub3c4 \uc5b4\ub290\uc815\ub3c4 \uc2dc\uac04\uc774 \uac78\ub9bd\ub2c8\ub2e4. \ubc18\uba74, \uc218\ud638\uc790\uc758\ub9dd\uce58\ub294 \ubc14\ub85c \ud53c\ud76110%\ub97c \uc8fc\uae30 \ub54c\ubb38\uc5d0 \ucd08\ubc18 \uccb4\uac10\uc740 \uc815\ub9d0 \ud07d\ub2c8\ub2e4. 11.6\ud328\uce58\uc5d0\uc11c \ud54f\ube5b\uae38\uacfc \uad76\uc8fc\ub9b0\uc0ac\ub0e5\uafbc\uc758 \uc2a4\ud399\uc774 \ub354 \ub0b4\ub824\uac00\uba74\uc11c \uc218\ud638\uc790\uc758\ub9dd\uce58 \uccb4\uac10\uc740 \ub354\uc6b1 \ucee4\uc84c\uc2b5\ub2c8\ub2e4. \uadf8\ub9ac\uace0 \ub9dd\uce58\ub294 \ud6c4\ubc18\uc73c\ub85c \uac00\ub3c4 \uc789\uc5ec\ud654\ub418\uc9c0 \uc54a\uc2b5\ub2c8\ub2e4.\n \n\uc218\ud638\uc790\uc758\ub9dd\uce58\ub294 \uce7c\ubc14\ub78c\uc758 \ub3c4\ub780\uac80\uc785\ub2c8\ub2e4. \uc5ec\ub7ec\ubd84\ub4e4 \ud611\uace1 \uc6d0\ub51c\ud560 \ub54c \uc2e0\ud654\ud15c \ube68\ub9ac \ubf51\uaca0\ub2e4\uba74 \ub871\uc18c\ub4dc \uc2dc\uc791\ud558\uc9c0 \uc54a\uc796\uc544\uc694? \uce7c\ubc14\ub78c\uc5d0\uc11c\ub3c4 \ub9c8\ucc2c\uac00\uc9c0\uc785\ub2c8\ub2e4. \uc218\ud638\uc790\uc758\ub9dd\uce58 \uc0ac\uc138\uc694. (\uc694\uc0c8\ub294 \uba54\ud0c0\uc0c1 \ud611\uace1\uc5d0\uc11c \ub871\uc18c\ub4dc \uc2dc\uc791\uc774 \ube48\ubc88\ud574\uc84c\uc2b5\ub2c8\ub2e4\ub9cc, \ud1b5\uc0c1\uc801\uc73c\ub85c\ub294 \ub864 \ub300\ubd80\ubd84\uc758 \uc5ed\uc0ac\ub3d9\uc548 \uc548 \uc0ac\uc654\uc2b5\ub2c8\ub2e4.)\n \n5\uba85\uc774 \ubaa8\ub450 \uc218\ud638\uc790\ud15c\uc73c\ub85c \uc2dc\uc791\ud55c \uac83\uacfc 5\uba85 \ubaa8\ub450 \uc218\ud638\uc790\ud15c \uc5c6\ub294\uac70\ub791 \ubd99\ub294\ub2e4\uace0 \uc0dd\uac01\ud574\ubcf4\uc138\uc694. \uc218\ud638\uc790\ud15c\uc73c\ub85c \uc2dc\uc791\ud574\uc11c \uc88b\uc740 \uac00\uc131\ube44\ub85c \uc2a4\ub178\uc6b0\ubcfc \uad74\ub824\uc11c \uc774\ub4dd\uc744 \ucde8\ud558\uba74 \ub429\ub2c8\ub2e4.\n \nhttps://lolalytics.com/\n\ub2f9\uc7a5 \ub4e4\uc5b4\uac00\uc11c ARAM(\uce7c\ubc14\ub78c)\uc73c\ub85c \ub3cc\ub9ac\uace0 \uc62c\ub7ad\ud06cor\ud50c\ub808\ud2f0\ub118+ \ubcf8\uc778\uc774 \uc6d0\ud558\ub294 \uc870\uac74 \uac78\uace0 \uad81\uae08\ud55c \uc6d0\ub51c \uc544\ubb34\uac70\ub098 \uccd0\ubcf4\uace0 \uc2dc\uc791\uc544\uc774\ud15c \uc2b9\ub960 \ubd10\ubcf4\uc138\uc694. \ud45c\ubcf8\uc774 \uc801\uc5b4\uc11c \uc2b9\ub960\uc774 \ub192\uc740 \uac83\ub3c4 \uc544\ub2d9\ub2c8\ub2e4. \uc2dc\uc791 \uc544\uc774\ud15c \ud558\ub098\ub9cc\uc73c\ub85c \ub0b4 \uc6d0\ub51c\uc758 \uc2b9\ub960\uc774 \ucd5c\uc18c 3%\uc5d0\uc11c 6%\uae4c\uc9c0 \uc62c\ub77c\uac08 \uc218 \uc788\uc2b5\ub2c8\ub2e4.\n \n \n\u203b \uce7c\ubc14\ub78c \uc6d0\ub51c\uc740 \ud53c\ud761\uc744 \ud611\uace1\ubcf4\ub2e4 \ud6e8\uc52c \ub354 \uc2e0\uacbd\uc368\uc57c \ud55c\ub2e4.\n\ud611\uace1\ub9cc \ud558\ub2e4 \uc628 \ud611\uace1 \uc6d0\ub51c \uc720\uc800\ub4e4\uc774 \uce7c\ubc14\ub78c \uc6d0\ub51c\ub85c \ud50c\ub808\uc774\ud560 \ub54c \uac00\uc7a5 \uccb4\uac10\ub418\ub294 \uac83\uc785\ub2c8\ub2e4.\n \n\ud611\uace1\ub9cc \ud558\ub2e4\uac00 \uc885\uc885 \uce7c\ubc14\ub78c \ub3cc\ub9ac\ub294 \uc720\uc800\ub4e4\uc774\ub098 \uce7c\ubc14\ub78c\ub9cc \ud558\ub354\ub77c\ub3c4 \uac10\uc774 \ubcc4\ub85c \uc5c6\ub294 \uc0ac\ub78c\ub4e4\uc740 \uc6d0\ub51c\ub85c \ud53c\ud761\uc744 \ub108\ubb34 \ucc59\uae30\uc9c0 \uc54a\ub294 \ubaa8\uc2b5\uc744 \ubcf4\uc5ec\uc90d\ub2c8\ub2e4. ex) \ud54f\ube5b\uae38\ub3c4 \uc548\ucc0d\uace0, \uc218\ud638\uc790\uc758\ub9dd\uce58\ub3c4 \uad6c\ub9e4\ud558\uc9c0 \uc54a\uace0 \ubc14\ub85c \ud06c\ub77c\ucf04\uc774\ub098 \ub3cc\ud48d\uc744 \uc62c\ub9ac\uac70\ub098 \ubcf4\uc870\ub8ec\uc5d0 \uad76\uc8fc\ub9b0\uc0ac\ub0e5\uafbc\ub3c4 \ucc0d\uc9c0 \uc54a\ub294\ub370 \ubbfc\ucca9\ud568\uae4c\uc9c0 \ucc0d\uc5b4\uc900\ub2e4\ub4e0\uc9c0\uc694.\n \n\ud611\uace1\uc5d0\uc11c\ub294 \uadc0\ud658\uc774 \uc788\uae30 \ub54c\ubb38\uc5d0 \ud53c\ud761\uc774 \uc880 \uc801\ub354\ub77c\ub3c4 \ub2e4\ub978 \ubd80\ubd84\uc744 \ud22c\uc790\ud558\uc5ec \ud0ac\uc744 \ub0b4\uace0 \uc9d1\uc5d0 \uac00\uba74 \ub429\ub2c8\ub2e4.\n \n\uadf8\ub7ec\ub098, \uce7c\ubc14\ub78c\uc740 \uadf8\ub807\uac8c\ud574\uc11c \ud0ac\uc744 \ub0b8\ub2e4\ud558\ub354\ub77c\ub3c4 \ud53c\ud761\uc774 \uc801\uc73c\uba74 \uc5b4\ucc28\ud53c \uacb0\uad6d \uc8fd\uc5b4\uc57c \ud558\uae30 \ub54c\ubb38\uc5d0 \uc758\ubbf8\uac00 \uc5c6\uc2b5\ub2c8\ub2e4. \ud53c\ud761\uc774 \ub418\uc57c \uacb0\uad6d \ud55c\ud0c0\uc5d0\uc11c \uc774\uae30\uace0 \ub0a8\uaca8\uc9c4 \ud53c\ub97c \ud53c\ud761\uc73c\ub85c \uc7ac\uc815\ube44\ud558\uba74\uc11c \ud3ec\ud0d1 \uc5b5\uc81c\uae30 \ub125\uc11c\uc2a4\ub97c \uae54 \uc218 \uc788\uc2b5\ub2c8\ub2e4. \ud53c\ud761\uc774 \uc5c6\uc73c\uba74 \ubc00\uace0 \uc560\ub9e4\ud55c \ud53c\ub85c \ub0a8\uaca8\uc11c \uc788\ub2e4\uac00 \ub2e4\uc74c \ud55c\ud0c0\uc2dc \uc6d0\ub51c \ubc14\ub85c \uc8fd\uace0 \ub300\ud328\ud574\uc11c \ub354 \ud070 \ud53c\ud574\ub97c \uc785\uac70\ub098 \uc81c\uc555 \uace8\ub4dc \uc918\uc11c \uacb0\uad6d \ubcf4\uc5ec\uc9c0\ub294 \ud0ac\ub370\uc2a4\ub9cc \uc88b\uc744 \ubfd0 \uc774\uae30\ub294 \ub370\uc5d0 \uc4f8\ubaa8\uac00 \uc5c6\uc2b5\ub2c8\ub2e4.\n \n\uce7c\ubc14\ub78c \uc6d0\ub51c\uc740 \uc0b4\uc544\ub0a8\uc73c\uba74 \uacb0\uad6d \ub51c\uc744 \uc81c\uc77c \ub9ce\uc774 \ud560 \uc218 \uc788\uc2b5\ub2c8\ub2e4. \uce7c\ubc14\ub78c\uc5d0\uc11c \ubcf8\uc778\uc774 \uc6d0\ub51c \ud50c\ub808\uc774\ud560 \ub54c \ud0ac\ub3c4 \ub9ce\uc774 \uba39\uace0 \ub098\ub294 \uc798\ud558\ub294\uac70 \uac19\uc740\ub370, \uac8c\uc784\uc740 \uc9c0\ub294 \ud310\uc774 \ub9ce\ub2e4? \uadf8\ub7ec\uba74 \ub300\uccb4\ub85c \ud53c\ud761\uc744 \uc798 \uc548 \ub9de\ucd94\ub294 \uc0ac\ub78c\uc77c \ud655\ub960\uc774 \ub192\uc2b5\ub2c8\ub2e4. \uc720\uc9c0\ub825 \uc88b\uc740 \uc11c\ud3ec\ud305\uc774 \ubc1b\uccd0\uc8fc\ub294 \ud310\uc774 \uc544\ub2c8\uace0\uc120, \ud53c\ud761\uc740 \uc5b4\ub290\uc815\ub3c4 \ub9de\ucdb0\uc8fc\ub294\uac8c \uc88b\uc2b5\ub2c8\ub2e4.\n \n \n\u203b \uce58\uc720\uac10\uc18c \uc544\uc774\ud15c (\ucc98\ud615\uc778\uc758\ub300\uac80, \ud544\uba78\uc790\uc758\uc6b4\uba85)\n\uce58\uc720\uac10\uc18c(=\uce58\uac10)\ud15c\uc740 \uac00\uae09\uc801 \uc6d0\ub51c\ubcf4\ub2e4\ub294 \ud654\ud559\uacf5\ud559\ubd80\ud328\uae30, \ub9dd\uac01\uc758\uad6c(\u2192\ubaa8\ub810\ub85c), \ub364\ubd88\uc870\ub07c(\u2192\uac00\uc2dc\uac11\uc637) \uc0ac\uc904 \uc218 \uc788\ub294 \ucc54\ud504\ub4e4\uc774 \uc62c\ub824\uc8fc\uace0 \uc6d0\ub51c\uc740 \ube60\ub974\uac8c \ucf54\uc5b4\ud15c\uc744 \uc62c\ub824\uc8fc\ub294\uac8c \uc88b\uc2b5\ub2c8\ub2e4. \n \n\ud2b9\ud788 \uc6d0\ub51c\uc740 \ucf54\uc5b4 \uc544\uc774\ud15c\uc774 \ub098\uc62c\uc218\ub85d \uc2dc\ub108\uc9c0\uac00 \ub098\ub294 \ucc54\ud504\uc785\ub2c8\ub2e4. \uc911\uac04\uc5d0 \uce58\uac10\ud15c\uc744 \uc11e\uc73c\uba74 \uadf8\ub9cc\ud07c \ub51c\ub85c\uc2a4\uac00 \ucee4\uc9d1\ub2c8\ub2e4. \uac00\uae09\uc801 3-4\ucf54\uc5b4 \uc548\uc5d0 \ubb34\ud55c\uc758\ub300\uac80(=\uc778\ud53c)\ub97c \ub9de\ucd94\ub294 \uac83\uc774 \uc911\uc694\ud569\ub2c8\ub2e4.\n \n\uce58\uac10\uc740 \ub2e4\ub978 \ucc54\ud504\ub4e4\uc5d0\uac8c \ub9e1\uae30\uc138\uc694. \uc6b0\ub9ac\ud300\uc774 \uce58\uac10 \uac00\ub294\ub370\ub3c4 \uc6d0\ub51c\uc774 \uce58\uac10\ud15c\uc744 \uc62c\ub824\uc11c \ub51c\ub85c\uc2a4 \uad6c\uac04\uc744 \ub9cc\ub4e0\ub2e4\uba74 \uc6d0\ub51c \uc798\ubabb\uc785\ub2c8\ub2e4.\n \n\ub2e4\ub9cc, \ube14\ub77c\ub514 \uc2a4\uc6e8\uc778 \uc774\ub7f0 \ucc54\ud504\ub4e4\ucc98\ub7fc \ub364\ubd88\uc870\ub07c\ub098 \uac00\uc2dc\uac11\uc637\uc73c\ub85c \ubb34\ub825\ud654\ud558\uae30 \ud798\ub4e0 \ucc54\ud504\ub098 \uc6b0\ub9ac\ud300\uc774 \uce58\uac10\uc744 \uc5ec\ub7ec\uba85 \ubb3b\ud788\uae30 \ud798\ub4e0 \ud310\uc774\ub098 \uc6b0\ub9ac\ud300\uc774 \uc808\ub300 \uce58\uac10\ud15c\uc744 \uac00\uc8fc\uc9c0 \uc54a\ub294\ub2e4 \uc774\ub7ec\uba74 \uc6d0\ub51c\uc774\ub77c\ub3c4 \uac00\uc57c\ud558\uaca0\uc8e0. \ud2b9\ud788, \uc560\uc26c \uc2dc\ube44\ub974\ub294 \ucc98\ud615\uc778\uc758\ub300\uac80(\ud654\uacf5\ud391\ud06c\uc0ac\uc2ac\uac80) \ud6a8\uc728\uc774 \uc88b\uc740 \ud3b8\uc774\uace0\uc694. \n \n(\ucc38\uace0\ub85c \ucd5c\uadfc \uc560\uc26c\uc640 \uc2dc\ube44\ub974\ub294 \uc6d0\ub51c\uc774\ub77c\uae30\ubcf4\ub2e4 \ud3ec\ud0b9\ucc54\ud504\uc5d0 \ub354 \uac00\uae5d\uc2b5\ub2c8\ub2e4. \ud2b9\ud788 \uce7c\ubc14\ub78c\uc5d0\uc120 \uc774 \ub458\uc740 \uc785\ud788\ub294\ud53c\ud574\ub7c9 \uac10\uc18c\uac00 \ub9ce\uc774 \ubd99\uc5b4 \uc788\uc5b4\uc11c \ub354 \uadf8\ub807\uc2b5\ub2c8\ub2e4.)\n \n\uacb0\ub860 \ub0b4\ubcf4\uc790\uba74 \uc6b0\uc120\uc740 \ud300\uc6d0\uc774 \uac00\uc8fc\uba74 \ubca0\uc2a4\ud2b8, \ud558\uc9c0\ub9cc \uc6b0\ub9ac\ud300\uc774 \uc815\ub9d0 \uc548\uac04\ub2e4\uba74 \uc6d0\ub51c\uc774\ub77c\ub3c4 \uac00\uae34 \uac00\uc57c \ud568.\n\uac19\uc740 \ud300\uc6d0\uc774 \uce58\uac10\ud15c \uc62c\ub824\uc92c\ub294\ub370\ub3c4 \uc6d0\ub51c\uc774 \uce58\uac10\ud15c\uc744 \uc11e\ub294\ub2e4\uba74 \uadf8\uac74 \uc6d0\ub51c \uc798\ubabb\uc785\ub2c8\ub2e4.\n \n \n\u203b \uccb4\ub825\ucc28 \uadf9\ubcf5\n\uc6d0\ub51c\uc758 \uac00\uc7a5 \ud070 \ud575\uc2ec\uc740 \ud0f1\ucee4\ub97c \uc7a1\uc544\uc8fc\ub294 \ub51c\uc785\ub2c8\ub2e4. \ub611\uac19\uc740 \ub51c 5000\uc744 \ub123\ub294\ub2e4 \ucce4\uc744 \ub54c, \ub51c\ub7ec\uc5d0\uac8c 5000\ub370\ubbf8\uc9c0 \ub123\ub294\uac70\ub791 \ud0f1\ucee4\uc5d0\uac8c 5000\ub370\ubbf8\uc9c0 \ub123\uc5b4\uc8fc\ub294\uac70\ub791 \ub2e4\ub985\ub2c8\ub2e4. \uc608\ub97c \ub4e4\uc5b4 \uc218\ub2a5\uc5d0\uc11c \uc601\uc5b4100\uc810\uc774\ub791 \uc0ac\ud68c\ud0d0\uad6c 100\uc810\uc774\ub791 \uadf8 \uac00\uce58\uac00 \uc880 \ub2e4\ub978\uac83 \ucc98\ub7fc \uc6d0\ub51c\uc5d0\uac8c\ub294 \ud0f1\ucee4\uc5d0\uac8c \ubc15\ub294 \ub51c\uc774 \uc911\uc694\ud569\ub2c8\ub2e4. \uc6d0\ub51c\uc740 \ud0f1\ucee4\ub97c \uc7a1\uc73c\ub824\uace0 \ubf51\ub294 \ud53d\uc774\uc5d0\uc694. \uc801\ud300\uc5d0 \ud558\ub4dc\ud0f1\ucee4\uac00 \uc5c6\ub2e4? \uadf8\ub7ec\uba74 \uc870\ud569\uc5d0 \uad73\uc774 \uc6d0\ub51c\uc774 \uc5c6\uc5b4\ub3c4 \ub418\uc8e0. \uadf8\ub807\uae30 \ub54c\ubb38\uc5d0 \uac00\ub054 \uc801 \uc870\ud569\uc5d0 \ub530\ub77c \uccb4\ub825\ucc28\uadf9\ubcf5 \uc218\uce58\uac00 \uc798 \uc548\ub098\uc624\ub294 \ud310\ub3c4 \uc788\uace0 \ud558\uaca0\uc9c0\ub9cc \uc800\ub294 \uc0ac\ubbf8\ub77c\ucc98\ub7fc \ucd5c\ud6c4\uc758\uc800\ud56d \ucc0d\uac70\ub098, \ud544\uc218\uc801\uc73c\ub85c \ubd88\uba78\uc758\ucca0\uac11\uad81\uc744 \uac00\ub294 \ucc54\ud504\uac00 \uc544\ub2c8\uace0\uc120 \uccb4\ub825\ucc28\uadf9\ubcf5\uc744 \ucc0d\uc744 \uac83\uc744 \ucd94\ucc9c\ud569\ub2c8\ub2e4.\n \n\uac00\ub054 \uc218\ud638\uc790\uc758\ub9dd\uce58 \uc0ac\uba74 \uccb4\ub825\ucc28\uadf9\ubcf5 \ud6a8\uc728\uc774 \uc548\ub098\uc11c \uc548\uc88b\uc740\uac70 \uc544\ub2c8\uc5d0\uc694? \ubb3b\uac70\ub098 \uccb4\ub825\ucc28\uadf9\ubcf5 \ud6a8\uc728\uc774 \uc548\ub098\uc624\ub2c8 \uc218\ud638\uc790\uc758\ub9dd\uce58 \uc548\uc0ac\uace0 \uc2f6\uc740\ub370\uc694? \ubb3b\ub294\ub370 \uc218\ud638\uc790\uc758\ub9dd\uce58 \ud558\ub098\uae4c\uc9c0\ub294 \uad1c\ucc2e\uc2b5\ub2c8\ub2e4. \uc801\uc774 only \ub51c\ub7ec\ub9cc \uc788\ub2e4\uba74 \ud6a8\uc728 \uc548\ub098\uc624\ub294 \ud310\ub3c4 \uc788\uc744 \uc218\uc57c \uc788\uc9c0\ub9cc \uc5b4\ucc28\ud53c \ucd5c\ud6c4\uc758\uc77c\uaca9\uc774\ub098 \uc800\ud56d \ucc0d\ub294\ub2e4 \ud558\ub354\ub77c\ub3c4 \ubd84\ub2f9 50 \uc774\uc0c1 \ud6a8\uc728 \ub098\uc624\uae30 \ud798\ub4e0 \ub8ec\uc785\ub2c8\ub2e4. \uadf8 \ud558\uc704\ub8ec \ud558\ub098\uc758 \ud6a8\uc728\ubcf4\ub2e4\ub3c4 \ub9dd\uce58\uc758 \ud6a8\uc728\uc774 \uc88b\uc73c\ub2c8\uae50 \ub9dd\uce58\ub97c \uc0ac\ub294 \uac70\uace0, \ud0f1\ucee4 \uc788\ub294 \ud310\uc5d0\uc11c \ud6a8\uc728\uc774 \ub118\uc0ac\ubcbd\uc73c\ub85c \uc88b\uc73c\ub2c8\uae50 \uccb4\ub825\ucc28\uadf9\ubcf5\uc744 \ub4dc\ub294 \uac81\ub2c8\ub2e4. \ubd88\uba78\uc758\ucca0\uac11\uad81 \uac00\ub294 \ucc54\ud504\ub9cc \uccb4\ub825\ucc28\uadf9\ubcf5 \uc548\ub4e4\uba74 \ub418\uc694.\n \n \n\u203b \ub3c4\ubbf8\ub2c9\uacbd\uc758\uc778\uc0ac (=\ub3c4\ubbf8\ub2c9)\n\ub3c4\ubbf8\ub2c9\uc740 \ud0f1\ucee4 \uc7a1\uae30 \uc815\ub9d0 \uc88b\uae30 \ub54c\ubb38\uc5d0 \uc0c1\ub300 \ud0f1\ucee4\uac00 \uc548 \ub6ab\ub9b0\ub2e4 \uc2f6\uc73c\uba74 3\ucf54\uc5b4\uc5d0 \ubb34\ud55c\uc758\ub300\uac80\ubcf4\ub2e4\ub3c4 \uba3c\uc800 \ubf51\uc544\uc918\uc57c \ud558\ub294 \uc544\uc774\ud15c\uc785\ub2c8\ub2e4. \ucc54\ud504\ub098 \uc0c1\ud669\uc5d0 \ub530\ub77c 2\ucf54\uc5b4\uc5d0 \uc11e\uc5b4\uc904 \uc218 \ub3c4 \uc788\uc2b5\ub2c8\ub2e4. \ud0f1\ucee4\uac00 \uc548\uc7a1\ud78c\ub2e4\uba74 \ub3c4\ubbf8\ub2c9 \uaf2d \uc62c\ub9ac\uc138\uc694. \ud2b9\ud788, \ud611\uace1\uc5d0\uc120 \ud0f1\ucee4\uac00 \uc790\uc8fc \ub098\uc624\uc9c0\ub3c4 \uc54a\uace0 \ud0f1\uc11c\ud3ff\uc774 \ub098\uc628\ub2e4 \ud558\ub354\ub77c\ub3c4 \uc11c\ud3ec\ud130 \uace8\ub4dc \uc218\uae09\uc758 \ud55c\uacc4 \ub54c\ubb38\uc5d0 \ub3c4\ubbf8\ub2c9 \uac00\ub294 \uc0c1\ud669\uc774 \uce7c\ubc14\ub78c\uc5d0 \ube44\ud574 \uc801\uc9c0\ub9cc, \uce7c\ubc14\ub78c\uc740 \uc6d0\ub51c\uc5d0\uac8c \uc131\uc7a5\uc744 \ubab0\uc544\uc8fc\ub294 \uac83\ub3c4 \ub4dc\ubb3c\uace0 \ud0f1\ucee4\ub098 \ud0f1\uc11c\ud3ff\ub4e4\ub3c4 \ub3d9\ub4f1\ud558\uac8c \uc131\uc7a5\ud558\uae30 \ub54c\ubb38\uc5d0 \ud0f1\ucee4\ub4e4\uc744 \uc7a1\uae30 \uc704\ud574\uc120 \ub3c4\ubbf8\ub2c9\uc744 \ud611\uace1\ubcf4\ub2e4 \ub354 \uc801\uadf9\uc801\uc73c\ub85c \uad6c\ub9e4\ud574\uc918\uc57c \ud569\ub2c8\ub2e4.\n \n\ucc38\uace0\ub85c \ub3c4\ubbf8\ub2c9\uc5d0 \ub300\ud574\uc11c \ud37c\uc13c\ud2b8 \ubc29\uc5b4\ub825 \uad00\ud1b5\ub9cc \uc788\ub294\uc9c0 \uc54c\uace0 \uacc4\uc2e0 \ubd84\ub4e4\uc774 \ub9ce\uc740\ub370, \ub3c4\ubbf8\ub2c9\uc740 \uccb4\ub825\ucc28\uc774 \ub9ce\uc774 \ub098\ub294 \uc801\uc5d0\uac8c \ucd94\uac00\ud53c\ud574\ub7c9\uc774 \ub298\uc5b4\ub098\ub294 \ud6a8\uacfc\ub3c4 \uc788\ub2e4\ub294 \uac83\uc744 \uaf2d \uc54c\uc544\ub450\uc138\uc694.\n \n \n\u203b \uc5ec\uc2e0\uc758\ub208\ubb3c (=\uc5ec\ub208)\n\uc2dc\uc98c11 \ub4e4\uc5b4\uc640\uc11c \uc5ec\uc2e0\uc758\ub208\ubb3c \uac00\uaca9\uc774 400\uace8\ub4dc\ub85c \uc800\ub834\ud574\uc84c\uace0, \ub418\ud30c\ub294 \uac00\uaca9\ub3c4 \uc62c\ub77c\uc11c \ud604\uc7ac \uac00\uc131\ube44\uac00 \uad49\uc7a5\ud788 \uc88b\uc2b5\ub2c8\ub2e4. \uadf8\ub798\uc11c \uc2dc\uc791 \uc544\uc774\ud15c\uc73c\ub85c \uc4f0\ub2e4\uac00 \ub808\ubca8\uc5c5\uc744 \ud1b5\ud574 \ub9c8\ub098\ud1b5 \ub298\uc5b4\ub0ac\uc744 \ub54c \ud314\uc544\uc8fc\ub294 \ubc29\ubc95 \uc88b\uc2b5\ub2c8\ub2e4. \uaf34\ub791 120\uace8\ub4dc \uc190\ud574\ub77c 1\ud0ac\uac12(180\uace8\ub4dc)\ubcf4\ub2e4\ub3c4 \ub354 \uc801\uac70\ub4e0\uc694. \uc774\ub294 \uad73\uc774 \uc6d0\ub51c \ubfd0 \uc544\ub2c8\ub77c \ubfd4\ud53c\ub9ac\ub098 \uac80 \uc2dc\uc791\ud558\ub294 \ucc54\ud504\ub4e4\ub3c4 \ub9c8\ucc2c\uac00\uc9c0\uc785\ub2c8\ub2e4. \ubaa8\ub4e0 \uc6d0\ub51c\uc5d0\uac8c \uc801\uc6a9\ub418\ub294\uac74 \uc544\ub2c8\uc9c0\ub9cc \uba87\uba87 \ucc54\ud504\uc640 \uc870\ud569\uc5d0 \ub530\ub77c\uc11c \ucda9\ubd84\ud788 \uc120\ud0dd\ud560\ub9cc\ud558\uace0 \ucde8\ud5a5\uc5d0 \ub530\ub77c\uc11c \uac08\ub9b4 \uc218 \uc788\uc2b5\ub2c8\ub2e4.\n \n\uc6d0\ub51c\uc758 \uacbd\uc6b0 \uc218\ud638\uc790\uc758\ub9dd\uce58 + \uc5ec\uc2e0\uc758\ub208\ubb3c + \ubb3c\uc57d\uc73c\ub85c \uc2dc\uc791\ud55c \ub4a4 \uac16\uace0 \uc788\ub2e4\uac00 \uc790\ub9ac \uc5c6\uc73c\uba74 \ub098\uc911\uc5d0 \ud314\uc544\uc90d\ub2c8\ub2e4. \uc774\ub7ec\uba74 \uce68\ucc29 \ub300\uc2e0 \uacfc\ub2e4\uce58\uc720\ub97c \ucc0d\uc744 \uc218\ub3c4 \uc788\uc2b5\ub2c8\ub2e4. \uc81c\uac00 \ucd94\ucc9c\ud558\ub294 \uba87\uba87 \ucc54\ud504\ub4e4\uc740 \uc544\ub798\uc5d0 \uc0c1\uc138\ud788 \uc801\uc5b4\ub4dc\ub9ac\uaca0\uc2b5\ub2c8\ub2e4.\n \n\uc27d\uac8c \ub9d0\ud574\uc11c \uce7c\ubc14\ub78c \uc6d0\ub51c\uc740 (\uac70\uc758)\ud54f\ube5b\uae38 + \uad76\uc8fc\ub9b0\uc0ac\ub0e5\uafbc(+\ud53c\uc758\ub9db) + \uc218\ud638\uc790\uc758\ub9dd\uce58 + \ud68c\ubcf5X + (\uac00\uae09\uc801)\uce58\uc720\uac10\uc18cX \uc774\ub807\uac8c 5\uac1c\uac00 \ud575\uc2ec\uc785\ub2c8\ub2e4.\n \n\ud611\uace1 \uc6d0\ub51c\ucc98\ub7fc \ud68c\ubcf5 + \ubcf4\uc870\ub8ec \uc601\uac10 + \uc808\uc815\uc758\ud654\uc0b4 \uc2dc\uc791\ud55c \uc6d0\ub51c\uc774 \uc788\ub2e4\uba74 \uae30\ubcf8\uc801\uc73c\ub85c \ud611\uace1\uc740 \uc798 \uc54c\uc9c0\ub9cc \uce7c\ubc14\ub78c\uc740 \ucd08\ubcf4\ub77c\uace0 \ubcf4\uc2dc\uba74 \ub429\ub2c8\ub2e4.\n \n* \uc774\uc81c\ubd80\ud130 \uc544\ub798\ub294 \ubf08\ub300 \ud15c\ud2b8\ub9ac \uc18c\uac1c\uc785\ub2c8\ub2e4. \uc800\ub3c4 \uc0c1\ud669, \uc870\ud569, \ubcc0\uc218 \ub4f1 \uc5ec\ub7ec\uac00\uc9c0\uc5d0 \ub530\ub77c\uc11c \uc911\uac04\uc5d0 \uc544\uc774\ud15c\uc744 \uc11e\uc5b4\uc8fc\uae30\ub3c4 \ud558\uace0 \uc0dd\ub7b5\ud558\uae30\ub3c4 \ud558\uace0 \ub2e4\ub978 \uac83\uc73c\ub85c \uac00\uae30\ub3c4 \ud558\uace0 \ub2e4\uc591\ud558\uac8c \uac11\ub2c8\ub2e4. \ub2e4\ub9cc, \uc704\uc5d0 \uc801\uc5b4\ub454 \uce7c\ubc14\ub78c \uc6d0\ub51c \uac1c\ub150\uc740 \ubc18\ub4dc\uc2dc \uc78a\uc9c0 \uc54a\uae38 \ubc14\ub78d\ub2c8\ub2e4.\n \n \n\ucc54\ud53c\uc5b8 \ubcc4 \ucd94\ucc9c \ub8ec \ud15c\ud2b8\ub9ac\n \n\uadf8\ub808\uc774\ube0c\uc988\n\ud611\uace1\uc5d0\uc11c\uc758 \ucd94\uc138\n\ud611\uace1 \uadf8\ub808\uc774\ube0c\uc988\uc758 \uc544\uc774\ud15c\ud2b8\ub9ac\ub294 \ub3cc\ud48d \ud15c\ud2b8\ub9ac(\ub3cc\ud48d-\uc9d5\uc218\uc758\ucd1d-\ubb34\ud55c\uc758\ub300\uac80)\uc640 \uc694\uc6b0\ubb34 \ubc84\ud504\ub85c \uc778\ud574\uc11c \uc694\uc6b0\ubb34 \uc62c\ub824\uc8fc\uace0 \ub3cc\ud48d\uc774\ub098 \uc6d4\uc2dd \uac00\ub294 \ud15c\ud2b8\ub77c\uac00 \uc788\uc2b5\ub2c8\ub2e4. \n \n\uce7c\ubc14\ub78c\uc5d0\uc11c\ub294 \ub3cc\ud48d\uc740 \ube44\ucd94\ucc9c\ud558\uace0, \ucca0\uac11\uad81 \ud15c\ud2b8\ub9ac(\ucca0\uac11\uad81-\ud53c\ubc14\ub77c\uae30/\uc9d5\uc218\uc758\ucd1d-\ubb34\ud55c\uc758\ub300\uac80)\ub97c \uc81c\uc77c \ucd94\ucc9c\ud558\uace0, \uc6d4\uc2dd \ud15c\ud2b8\ub9ac\ub3c4 \uc0c1\ud669\uc5d0 \ub530\ub77c\uc11c \uc4f0\uae30 \uc88b\ub2e4\uace0 \ubcf4\uace0 \uc788\uc2b5\ub2c8\ub2e4.\n \n* \ub3cc\ud48d\uc744 \ube44\ucd94\ucc9c\ud558\ub294 \uc774\uc720\ub294 \uc21c\uac04\uc801\uc73c\ub85c \ub3cc\ud48d \ud0ac\uac01\uc744 \uc7a1\ub294\ub2e4\uace0 \ud574\uc11c \ud611\uace1\ucc98\ub7fc \ud3ec\ud0d1, \ub4dc\ub798\uace4, \uc804\ub839, \ubc14\ub860 \ub4f1 \ucd94\uac00\uc801\uc778 \uc624\ube0c\uc81d\ud2b8\uc758 \uc774\ub4dd\uc744 \ubcfc \uc218 \uc5c6\uace0, \uad50\uc804\uc744 \uacc4\uc18d\ud574\uc11c \ud558\ub294 \ub9f5\uc774\ub77c \ucfe8\ud0c0\uc784 90\ucd08\ub294 \ub9e4\uc6b0 \uae34 \ud3b8\uc5d0 \uc18d\ud558\ub294 \ud3b8. \uce7c\ubc14\ub78c\uc740 \uc720\uc9c0\ub825\uc774 \ub9e4\uc6b0 \uc911\uc694\ud558\ubbc0\ub85c \ub3cc\ud48d\ubcf4\ub2e4\ub294 \uc6d4\uc2dd\uc774\ub098 \ucca0\uac11\uad81\ucc98\ub7fc \uc720\uc9c0\ub825\uc774 \uc88b\uac8c \uac00\ub294 \uac83\uc744 \ub354 \ucd94\ucc9c. \uc21c\uac04\uc801\uc73c\ub85c \uac70\ub9ac\ub97c \uc881\ud788\ub294 \uac83\uc740 \uce7c\ubc14\ub78c\uc5d0\ub294 \ub208\ub369\uc774\uac00 \uc788\uae30 \ub54c\ubb38\uc5d0 \ubc18\ub4dc\uc2dc \ub3cc\ud48d\uc744 \uac00\uc9c0 \uc54a\uc544\ub3c4 \uc811\uadfc\uc774 \uc6a9\uc774\ud558\ub2e4 \ub4f1 \uc5ec\ub7ec\uac00\uc9c0 \uc694\uc18c\ub85c \uc778\ud574\uc11c \ub3cc\ud48d\ubcf4\ub2e8 \ucca0\uac11\uad81\uc774\ub098 \uc6d4\uc2dd \ud2b8\ub9ac\ub97c \ub354 \ucd94\ucc9c\ud569\ub2c8\ub2e4.\n \n\ucc54\ud53c\uc5b8 \ubc38\ub7f0\uc2a4 \ud6a8\uacfc\n\uc5c6\uc74c\n \n\uce7c\ubc14\ub78c \ucd94\ucc9c \ub8ec\n\uc5b4\ub460\uc758\uc218\ud655 \ud53c\uc758\ub9db \uc0ac\ub0e5\uc758\uc99d\ud45c \uad76\uc8fc\ub9b0\uc0ac\ub0e5\uafbc + \uce68\ucc29 \ud54f\ube5b\uae38\n \n\uce7c\ubc14\ub78c \ucd94\ucc9c \uc544\uc774\ud15c\n\uc218\ud638\uc790\uc758\ub9dd\uce58+\uc5ec\uc2e0\uc758\ub208\ubb3c \ubd88\uba78\uc758\ucca0\uac11\uad81 \uc9d5\uc218\uc758\ucd1d/\ud53c\ubc14\ub77c\uae30 \ubb34\ud55c\uc758\ub300\uac80\n\uc218\ud638\uc790\uc758\ub9dd\uce58+\uc5ec\uc2e0\uc758\ub208\ubb3c \uc6d4\uc2dd \ub3c5\uc0ac\uc758\uc1a1\uacf3\ub2c8 \uc9d5\uc218\uc758\ucd1d/\uce60\ud751\uc758\uc591\ub0a0\ub3c4\ub07c/\uc138\ub9b4\ub2e4\uc758\uc6d0\ud55c/\ud53c\ubc14\ub77c\uae30 \ub4f1\n \n11.17v\uc5d0\uc11c Q\uc2a4\ud0ac \ub9c8\ub098\uc18c\ubaa8\ub7c9\uc774 60 \u2192 80\uc73c\ub85c \ub298\uc5b4\ub098\uba74\uc11c \uccb4\uac10\uc774 \uaf64 \ub418\uc5b4\uc11c \uc5ec\ub208 \uc2dc\uc791\ud558\ub294\uac8c \uc88b\ub2e4\uace0 \ub290\uaef4\uc9d1\ub2c8\ub2e4.\n \n\uc6d0\ub798 \uae30\ubbfc\ud55c\ubc1c\ub180\ub9bc\ub3c4 \uc55e\ub77c\uc778 \uc5c6\uc744 \ub54c\ub294 \uc88b\uc544\ud588\ub294\ub370, \ubbf8\ub2c8\uc5b8\uc5d0\uac8c \ud68c\ubcf5\ub960\uc774 \ub5a8\uc5b4\uc9c0\uace0 \ucc54\ud53c\uc5b8\uc5d0\uac8c \ud68c\ubcf5\ub960\uc774 \uc62c\ub77c\uac00\uac8c \ubc14\ub00c\uba74\uc11c \uc0ac\uac70\ub9ac\uac00 \uc9e7\uc740 \uadf8\ub808\uc774\ube0c\uc988\ub294 \uc548\ub4dc\ub294\uac8c \uc88b\ub2e4\uace0 \ubd10\uc694. \uc774\uc81c \uc5b4\ub460\uc758\uc218\ud655\uc744 \uace0\uc815\uc73c\ub85c \ub4e4\uc5b4\uc8fc\uc138\uc694. \uc804 \uc0ac\uc6a9\ud558\uc9c0 \uc54a\uc9c0\ub9cc \ucde8\ud5a5\uc5d0 \ub530\ub77c\uc11c \uc815\ubcf5\uc790, \uce7c\ub0a0\ube44\ub97c \ub4dc\ub294 \ubd84\ub3c4 \uc788\uc2b5\ub2c8\ub2e4\n \n\uce7c\ubc14\ub78c\uc5d0\uc11c \uc218\ud638\uc790\uc758\ub9dd\uce58 \ud6a8\uc728\uc774 \uadf8\ube0c\ubcf4\ub2e4 \ub354 \uc798 \ub098\uc624\ub294 \ucc54\ud504\ub294 \ub2e8\uc5f0\ucf54 \uc5c6\uc73c\ub2c8 \uaf2d \uc218\ud638\uc790\uc758\ub9dd\uce58 \uc2dc\uc791\ud574\uc8fc\uc138\uc694.\n \n\uadf8\ube0c\ub294 \ud06c\uac8c \ucca0\uac11\uad81 \uac00\ub294 \ube4c\ub4dc\uc640 \ubc29\uad00 \ube4c\ub4dc\uac00 \uc788\ub294\ub370, \ucca0\uac11\uad81 \ube4c\ub4dc\uac00 \ub354 \uc548\uc815\uc801\uc774\uae30 \ub54c\ubb38\uc5d0 \ucca0\uac11\uad81 \ube4c\ub4dc\ub97c \ub354 \uc120\ud638\ud569\ub2c8\ub2e4. \ucca0\uac11\uad81 \ube4c\ub4dc\ub294 \uc801\ub2f9\ud55c \ucf54\uc5b4\uac00 \ub098\uc628 \uc774\ud6c4\uc5d0 \uc6b0\ub9ac\ud300 \uc55e\ub77c\uc778\uc774 \ubd80\uc871\ud558\uac70\ub098 \uc0c1\ub300 AD, AP\ub51c \ube44\uc911\uc5d0 \ub530\ub77c\uc11c \uc815\ub839\uc758\ud615\uc0c1\uc774\ub098 \uc8fd\uc74c\uc758\ubb34\ub3c4 \uc11e\uc5b4\uc918\ub3c4 \uad1c\ucc2e\uc2b5\ub2c8\ub2e4. + \uce58\uba85\ud0c0 \ube4c\ub4dc\uc5ec\ub3c4 3-4\ucf54\uc5b4\ucbe4\uc5d0 \ub3c5\uc0ac \ud544\uc694\ud558\uba74 \ub3c5\uc0ac \uac00\uc154\ub3c4 \ub429\ub2c8\ub2e4.\n \n\uc11c\ud3ec\ud305\uc774 \uc88b\uc73c\uba74\uc11c \uc0c1\ub300\uc5d0 \ubb3c\ubab8\uc774 \ub9ce\uc740 \uacbd\uc6b0\uc5d0\ub294 \uc6d4\uc2dd \ube14\ud074(\uc591\ub0a0\ub3c4\ub07c) \ub3c5\uc0ac\ucc98\ub7fc \ubc29\uad00 \ube4c\ub4dc\ub3c4 \uc88b\uc544\ud569\ub2c8\ub2e4.\n\uc81c\uac00 \uc8fc\ub85c \ubc29\uad00 \uadf8\ube0c\ud560 \ub54c\ub294 \uce74\ub974\ub9c8 \uc544\uc774\ubc88\ucc98\ub7fc \uc0c1\ub300 \uc274\ub4dc\uac00 \ub9ce\uc740\ub370 \uc6b0\ub9ac\ud300\uc5d0 \ub3c5\uc0ac \uac08 \uc0ac\ub78c\uc774 \uc5c6\ub294 \uacbd\uc6b0\uc5d0 \uac00\uc8fc\uace0 \uc788\uc2b5\ub2c8\ub2e4.\n \n \n\ub4dc\ub808\uc774\ube10\n\ud611\uace1\uc5d0\uc11c\uc758 \ucd94\uc138\n\uc0ac\uc2e4 \ub4dc\ub808\uc774\ube10\uc740 \uc7a5\uc778\ub4e4\ub9c8\ub2e4 \ud15c\ud2b8\ub9ac\ub3c4 \ub2e4 \ub2ec\ub77c\uc11c \ud50c\ub808\uc774\uac00 \ub354 \uc911\uc694\ud55c \ucc54\ud504\uc785\ub2c8\ub2e4. \uadf8\ub0e5 \ubb58 \uc62c\ub824\ub3c4 \uc219\ub828\ub3c4\ub9cc \uc788\ub2e4\uba74 \uc388 \ud3b8\uc774\uc5d0\uc694.\n \n\ud611\uace1\uc5d0\uc11c\ub294 \uc694\uc0c8 \ub3cc\ud48d/\ucca0\uac11\uad81 \uc9d5\uc218\uc758\ucd1d \ubb34\ud55c\uc758\ub300\uac80 \ud15c\ud2b8\ub9ac\uac00 \ub9ce\uc2b5\ub2c8\ub2e4.\n \n\uce7c\ubc14\ub78c\uc5d0\uc11c\ub294 \uc219\ub828\ub3c4\uac00 \uc88b\uc740 \ubd84\ub4e4\uc740 \ub3c4\ub07c \uc798 \ubc1b\uc73c\ub2c8\uae4c \uc0c1\uad00\uc5c6\ub294\ub370, \uc219\ub828\ub3c4\uac00 \ub0ae\uc740 \ubd84\ub4e4\uc740 \ucfe8\uac10\uc73c\ub85c \uac00\ub294\uac8c \ub3c4\ub07c \ubc1b\uae30 \ud3b8\ud560\uac70\ub77c \uc0dd\uac01\ub429\ub2c8\ub2e4.\n \n\ucc54\ud53c\uc5b8 \ubc38\ub7f0\uc2a4 \ud6a8\uacfc\n\uc5c6\uc74c\n \n\uce7c\ubc14\ub78c \ucd94\ucc9c \ub8ec\n\uce7c\ub0a0\ube44 \ud53c\uc758\ub9db \uc0ac\ub0e5\uc758\uc99d\ud45c \uad76\uc8fc\ub9b0\uc0ac\ub0e5\uafbc + \uacfc\ub2e4\uce58\uc720 \ud54f\ube5b\uae38\n \n\uce7c\ubc14\ub78c \ucd94\ucc9c \uc544\uc774\ud15c\n\uc218\ud638\uc790\uc758\ub9dd\uce58+\uc5ec\uc2e0\uc758\ub208\ubb3c \uc815\uc218\uc57d\ud0c8\uc790 \ub4dc\ub77d\uc0ac\ub974 \ub3c4\ubbf8\ub2c9\uacbd\uc758\uc778\uc0ac/\ud53c\ubc14\ub77c\uae30 \ubb34\ud55c\uc758\ub300\uac80\n\uc218\ud638\uc790\uc758\ub9dd\uce58+\uc5ec\uc2e0\uc758\ub208\ubb3c \ubd88\uba78\uc758\ucca0\uac11\uad81 \uc815\uc218\uc57d\ud0c8\uc790 \ubb34\ud55c\uc758\ub300\uac80/\ub3c4\ubbf8\ub2c9\uacbd\uc758\uc778\uc0ac\n \n\uc77c\ub2e8 \ub4dc\ub808\uc774\ube10\uc740 \ub8ec\uacfc \ud15c\ud2b8\ub9ac\ub3c4 \uc7a5\uc778\ub4e4\ub3c4 \ubcf8\uc778\uc5d0 \ucde8\ud5a5\uc774\ub098 \uc0c1\ud669\uc5d0 \ub530\ub77c\uc11c \uad49\uc7a5\ud788 \ub2e4\ub974\uac8c \uac11\ub2c8\ub2e4.\n\ub4dc\ube10\uc758 \uc2e4\ub825\uc774 \ub354 \uc911\uc694\ud55c \ucc54\ud504\uc785\ub2c8\ub2e4.\n \n\ub4dc\ub808\uc774\ube10\ub3c4 \uc218\ud638\uc790\uc758\ub9dd\uce58 + \uc5ec\uc2e0\uc758\ub208\ubb3c \uaf2d \uc0ac\uc8fc\uc138\uc694. \n\uadf8\ube0c \ub4dc\ube10\ucc98\ub7fc \uacf5\uc18d\uc774 \ub290\ub9ac\uace0 \ud55c\ubc29\ud55c\ubc29\uc774 \uc388 \ucc54\ud504\ub294 \uc218\ud638\uc790\uc758\ub9dd\uce58 \uc2dc\uc791\uc774 \uc815\ub9d0 \uc88b\uc544\uc694.\n \n\uc815\uc218\uc57d\ud0c8\uc790 \ub4dc\ub77d\uc0ac\ub974 \ud15c\ud2b8\ub9ac\ub294 \uc774\ub7ec\uba74 \ucfe8\uac10\uc774 \uc88b\uc544\uc11c \ub3c4\ub07c \ub3cc\ub9ac\uae30\uac00 \ud569\ub2c8\ub2e4. \n\uc5ec\uc2e0\uc758\ub208\ubb3c\uc740 \uc815\uc218\uc57d\ud0c8\uc790 \ub098\uc628 \uc774\ud6c4\ub85c \uace8\ub4dc \ud544\uc694\ud560 \ub54c \ud30c\uc2dc\uba74 \ub429\ub2c8\ub2e4.\n \n\ucca0\uac11\uad81 \ud15c\ud2b8\ub9ac\ub294 \ub2e8\ub2e8\ud558\uace0 \uc720\uc9c0\ub825\uc774 \uc88b\uc2b5\ub2c8\ub2e4. \uadf8\ub9ac\uace0 \uc870\uae08 \ub354 \ubb34\ub09c\ud55c \uc544\uc774\ud15c \ube4c\ub4dc\uc5d0\uc694.\n\uadf8\ub798\uc11c \uc6b0\ub9ac\ud300\uc5d0 \uc55e\ub77c\uc778\uc774 \uc880 \ubd80\uc871\ud558\ub2e4\ub4e0\uc9c0 \uc0c1\ub300 \ud3ec\ud0b9\uc774 \uac15\ud574\uc11c \uc720\uc9c0\ub825\uc774 \ud544\uc694\ud55c \uacbd\uc6b0\uc5d0 \uac00\uc8fc\uba74 \uc88b\uc2b5\ub2c8\ub2e4.\n\uadf8 \uc678\uc5d0 \ucca0\uac11\uad81 \uc815\uc218\uc57d\ud0c8\uc790 + \ub8ec\ud30c\ud3b8 \ucfe8\uac10 + \uc544\uc774\uc624\ub2c8\uc544 \uc2e0\ubc1c \ucc0d\uc5b4\uc11c \ubb34\ud55c\ub3c4\ub07c \ub3cc\ub9ac\ub294 \ubd84\ub4e4\ub3c4 \uc788\uc2b5\ub2c8\ub2e4.\n \n \n\ub8e8\uc2dc\uc548\n\ud611\uace1\uc5d0\uc11c\uc758 \ucd94\uc138\n\ud611\uace1 \ub8e8\uc2dc\uc548\uc740 \ub9ac\uc6cc\ud06c\uac00 \ub418\uc11c \ubc14\ud140 \uc6d0\ub51c\ub85c \ub354 \ud65c\uc6a9\ud560 \uc218 \uc788\ub3c4\ub85d \ud328\uce58\ud574\uc92c\uc2b5\ub2c8\ub2e4. \uce7c\ubc14\ub78c\uc5d0\uc11c\ub294 \uc6b0\ub9ac\ud300\uc774 \uac19\uc774 W\ud45c\uc2dd\uc744 \ud130\ud2b8\ub824\uc8fc\uae30 \uc88b\uace0 \uc11c\ud3ec\ud305\ub3c4 \uc788\uc73c\ub2c8 \ub354 \uad1c\ucc2e\uc544\uc84c\ub2e4\uace0 \ubcfc \uc218 \uc788\uc2b5\ub2c8\ub2e4.\n \n\uce7c\ubc14\ub78c \ub8e8\uc2dc\uc548\uc740 \ub3cc\ud48d \ube44\uc911\uc774 \uc904\uc5b4\ub4e4\uace0 \ud06c\ub77c\ucf04\uc774\ub098 \ucca0\uac11\uad81\uc744 \ud65c\uc6a9\ud558\ub294\uac8c \uc88b\uace0, \ub8ec\ub3c4 \ubcf4\uc870\ub8ec\uc5d0\ub294 \uc9c0\ubc30\ucabd\uc744 \ub4e4\uc5b4\uc11c \uc720\uc9c0\ub825\uc744 \ub192\uc5ec\uc8fc\ub294 \uac83\ub3c4 \ucc28\uc774\uc810\uc774\ub77c\uace0 \ud560 \uc218 \uc788\uc2b5\ub2c8\ub2e4. \ub610\ud55c, \uc885\uc885 \uc544\uc608 \ubc29\uad00 \ub8e8\uc2dc\uc548\uc744 \ud558\ub294 \uacbd\uc6b0\ub3c4 \uc788\uc2b5\ub2c8\ub2e4.\n \n\ucc54\ud53c\uc5b8 \ubc38\ub7f0\uc2a4 \ud6a8\uacfc\n\uc785\ud788\ub294 \ud53c\ud574\ub7c9 \uc99d\uac00 3%\n \n\uce7c\ubc14\ub78c \ucd94\ucc9c \ub8ec\n\uc9d1\uc911\uacf5\uaca9 \uacfc\ub2e4\uce58\uc720 \ubbfc\ucca9\ud568 \ucd5c\ud6c4\uc758\uc77c\uaca9 + \uc0ac\ub0e5\uc758\uc99d\ud45c \uad76\uc8fc\ub9b0\uc0ac\ub0e5\uafbc \n \n\uce7c\ubc14\ub78c \ucd94\ucc9c \uc544\uc774\ud15c\n\uc218\ud638\uc790\uc758\ub9dd\uce58+\uc5ec\uc2e0\uc758\ub208\ubb3c \ubd88\uba78\uc758\ucca0\uac11\uad81 \uc815\uc218\uc57d\ud0c8\uc790 \ub098\ubcf4\ub9ac\uc2e0\uc18d\uac80/\ubb34\ud55c\uc758\ub300\uac80/\ub3c4\ubbf8\ub2c9\uacbd\uc758\uc778\uc0ac\n \n\ub8e8\uc2dc\uc548\uc740 \uc218\ud638\uc790\ub9dd\uce58 \uaf2d \uc0ac\uc8fc\uc138\uc694. \n\uadf8\ub9ac\uace0 \uc5ec\uc2e0\uc758\ub208\ubb3c\ub3c4 \uc815\uc218\uc57d\ud0c8\uc790 \uc804\uae4c\uc9c0 \uad49\uc7a5\ud788 \ub3c4\uc6c0\uc774 \ub418\uc11c \uc0ac\ub294 \uac83\uc744 \ucd94\ucc9c\ud569\ub2c8\ub2e4.\n \n\ucd5c\uadfc\uc5d0\ub294 \ubbfc\ucca9\ud568 + \ucca0\uac11\uad81\uc73c\ub85c \uace0\uc815\ud558\uace0 \uc788\uc2b5\ub2c8\ub2e4.\n\ud06c\ub77c\ucf04\uc744 \uac00\ub294 \uacbd\uc6b0\ub294 \uc870\ud569\uc774 \ud0c4\ud0c4\ud558\uace0 \uc11c\ud3ec\ud305\uc774 \uc548\uc815\uc801\uc77c \ub54c\ub9cc \uac00\uace0 \uc788\uc2b5\ub2c8\ub2e4. \n \n \n- \ubbf8\uc2a4\ud3ec\uce08\n\ud611\uace1\uc5d0\uc11c\uc758 \ucd94\uc138\n11.18 \ucd5c\uadfc\uc5d0\ub294 \uc720\uc131 + \ubc29\uad00 \uc544\uc774\ud15c \ube4c\ub4dc\ub97c \ud0c0\uac70\ub098 \uc608\uc804\ucc98\ub7fc \ub2e4\uc2dc \uc9d1\uc911\uacf5\uaca9 + \ud06c\ub77c\ucf04\ud559\uc0b4\uc790 \uc9d5\uc218\uc758\ucd1d \ube4c\ub4dc\ub85c \uc624\uace0 \uc788\uc2b5\ub2c8\ub2e4.\n \n\uce7c\ubc14\ub78c\uc5d0\uc11c\ub294 \uc218\ud655 + \ubc29\uad00 \uc544\uc774\ud15c \ube4c\ub4dc\ub85c \uac00\ub294 \uac83\uc744 \ucd94\ucc9c\ud558\uace0, \uce58\uba85\ud0c0 \ube4c\ub4dc\ub85c \uac08 \ub54c\ub294 \uc9d1\uc911\uacf5\uaca9 + \ud06c\ub77c\ucf04\uae4c\uc9c0 \uace0\uc815\uc73c\ub85c \uac00\uace0 2\ucf54\uc5b4\ubd80\ud130 \ud53c\ubc14\ub77c\uae30/\ub3c4\ubbf8\ub2c9/\uc720\ub839\ubb34\ud76c/\uc9d5\uc218\uc758\ucd1d \ub4f1 \uc720\ub3d9\uc801\uc73c\ub85c \uc120\ud0dd\ud558\ub294\uac8c \uc88b\ub2e4\uace0 \ubd05\ub2c8\ub2e4.\n \n\ucc54\ud53c\uc5b8 \ubc38\ub7f0\uc2a4 \ud6a8\uacfc\n\uc785\ud788\ub294 \ud53c\ud574\ub7c9 \uac10\uc18c 8%\n\ubc1b\ub294 \ud53c\ud574\ub7c9 \uc99d\uac00 8%\n \n\ubc29\uad00 \ubbf8\uc2a4\ud3ec\uce08\n\uce7c\ubc14\ub78c \ucd94\ucc9c \ub8ec\n\uc5b4\ub460\uc758\uc218\ud655 \ube44\uc5f4\ud55c\ud55c\ubc29 \uc0ac\ub0e5\uc758\uc99d\ud45c \uad76\uc8fc\ub9b0\uc0ac\ub0e5\uafbc + \uce68\ucc29 \uccb4\ub825\ucc28\uadf9\ubcf5/\ud54f\ube5b\uae38\n \n- \uce7c\ubc14\ub78c \ucd94\ucc9c \uc544\uc774\ud15c\n\uc218\ud638\uc790\uc758\uac80+\uc5ec\uc2e0\uc758\ub208\ubb3c \uc6d4\uc2dd \ub9c8\ub098\ubb34\ub124 \uc138\ub9b4\ub2e4\uc758\uc6d0\ud55c/\ub3c5\uc0ac\uc758\uc1a1\uacf3\ub2c8 \uc774\ud6c4 \uc0c1\ud669\uc5d0 \ub9de\ub294 \ubc29\uad00\ub958 \uc544\uc774\ud15c\n \n- \ubc29\uad00 \ubbf8\ud3ec \ub8ec\uc740 \uc8fc\ub8ec(\uba54\uc778\ub8ec)\uc740 \uace0\uc815\uc774\uace0, \ubcf4\uc870\ub8ec\uc5d0 \uccb4\ub825\ucc28\uadf9\ubcf5\uc744 \ub123\uc5b4\uc8fc\uc154\ub3c4 \ub429\ub2c8\ub2e4. \uc65c\ub0d0\ud558\uba74 \ubc29\uad00 \ubbf8\ud3ec\uc758 \ub2e8\uc810\uc774 \uce58\uba85\ud0c0\uc5d0 \ube44\ud574\uc11c \uc9c0\uc18d\ub51c\ub85c \ud0f1\ucee4 \ub6ab\uae30\uac00 \ud798\ub4e4\ub2e4\ub294\uac74\ub370 \uc6d4\uc2dd\uc744 \uac00\uac8c \ub418\uba74\uc11c \ud53c\ud761\uc774 \uc0dd\uae30\ub2c8\uae50 \ud54f\ube5b\uae38 \ud558\ub098 \ube7c\uace0 \ud0f1\ucee4 \uc7a1\uae30 \ub354 \uc88b\uc740 \uccb4\ub825\ucc28\uadf9\ubcf5 \ub123\uc5b4\uc918\ub3c4 \uad1c\ucc2e\uac70\ub4e0\uc694.\n \n1-2\ucf54\uc5b4\uae4c\uc9c0\ub294 \uc6ec\ub9cc\ud558\uba74 \uace0\uc815\uc73c\ub85c \ud574\uc8fc\uc2dc\uace0, 3\ucf54\uc5b4\ub294 \ubc29\uad00\uc544\uc774\ud15c \ub9de\ucdb0\uac00\uc8fc\uc2dc\uba74 \ub429\ub2c8\ub2e4.\n\ud558\uc9c0\ub9cc, \uc81c\uc77c \ub9ce\uc774 \uc62c\ub9ac\uac8c \ub418\ub294\uac8c \uc694\uc0c8 \uc274\ub4dc\ub54c\ubb38\uc5d0 \uc9dc\uc99d\ub098\uc11c \ub3c5\uc0ac\ub97c \ub9ce\uc774 \uac00\uac70\ub098 \uc138\ub9b4\ub2e4\ub97c \uc62c\ub9ac\uac8c \ub429\ub2c8\ub2e4.\n \n\uc0c1\ub300 \ud2f0\ubaa8\uac00 \uc788\ub2e4 \uc774\ub7ec\uba74 \uadf8\ub9bc\uc790\uac80 \uc0ac\uc918\ub3c4 \ub418\uace0 \uc694\uc6b0\ubb34\ub3c4 \ubc84\ud504\ub418\uc11c \uc4f8\ub9cc\ud574\uc84c\uc2b5\ub2c8\ub2e4. \n\ubc29\uad00\ub958 \uc544\uc774\ud15c\ub4e4\uc740 \ub9ce\uc73c\ub2c8\uae50 \uc0c1\ud669\uc5d0 \ub9de\uac8c \uc62c\ub824\uc8fc\uc2dc\uba74 \ub429\ub2c8\ub2e4.\n \n\uce58\uba85\ud0c0 \ubbf8\uc2a4\ud3ec\uce08\n\uce7c\ubc14\ub78c \ucd94\ucc9c \ub8ec\n\uc9d1\uc911\uacf5\uaca9 \uacfc\ub2e4\uce58\uc720 \ud54f\ube5b\uae38 \uccb4\ub825\ucc28\uadf9\ubcf5 + \uc0ac\ub0e5\uc758\uc99d\ud45c \uad76\uc8fc\ub9b0\uc0ac\ub0e5\uafbc\n \n\uce7c\ubc14\ub78c \ucd94\ucc9c \uc544\uc774\ud15c\n\uc218\ud638\uc790\uc758\ub9dd\uce58+\uc5ec\uc2e0\uc758\ub208\ubb3c \ud06c\ub77c\ucf04\ud559\uc0b4\uc790 \uc720\ub839\ubb34\ud76c/\ud53c\ubc14\ub77c\uae30 \ubb34\ud55c\uc758\ub300\uac80/\ub3c4\ubbf8\ub2c9\uacbd\uc758\uc778\uc0ac\n \n\uc218\ud638\uc790\uc758\ub9dd\uce58 \uc5ec\uc2e0\uc758\ub208\ubb3c \ubb3c\uc57d \uc2dc\uc791\ud574\uc11c \uc790\ub9ac \uc5c6\uac70\ub098 \ud314\uc544\uc11c \ucf54\uc5b4\ud15c \ub098\uc62c \ub54c \ud314\uc544\uc8fc\uc2dc\uba74 \ub429\ub2c8\ub2e4. \ubbf8\ud3ec\ub294 E\ub97c \uacac\uc81c\uae30\ub85c \ub0a8\ubc1c\ud558\uc9c0 \uc54a\ub294\ub2e4\uba74 \ub9c8\ub098\uac00 \ub538\ub9b0 \uc6d0\ub51c\uc740 \uc544\ub2c8\ub098, \ucd08\ubc18\uc5d0 E\uacac\uc81c\ub97c \ub9ce\uc774 \ud558\uac8c \ub418\uba74 \ub9c8\ub098\uac00 \ub538\ub9ac\ub294 \ucc54\ud504\uc785\ub2c8\ub2e4. \uadf8\ub798\uc11c \uc2dc\uc791\ud15c\uc73c\ub85c \uc5ec\uc2e0\uc758\ub208\ubb3c\uc744 \uc0ac\ub294\uac8c \ucd08\ubc18\uc5d0 E\uacac\uc81c\ud558\uae30\uc5d0\ub3c4 \uc88b\uace0, \ub808\ubca8\uc5c5\uc744 \ud574\uc11c \ub9c8\ub098\ud1b5\uc774 \ub298\uc5b4\ub098\uba74 \uadf8 \ub54c \ud30c\ub294\uac8c \uc88b\uc544\uc694. \uc774\ub807\uac8c \ud558\uba74 \uce68\ucc29 \ub300\uc2e0 \uacfc\ub2e4\uce58\uc720\ub97c \ucc0d\uc744 \uc218 \uc788\uace0, \uacfc\ub2e4\uce58\uc720 \uc218\uce58\ub3c4 \uc798 \ub098\uc635\ub2c8\ub2e4.\n \n1\ucf54\uc5b4 \ub300\ubd80\ubd84 \ud06c\ub77c\ucf04\ud559\uc0b4\uc790 \uac00\uc8fc\uc2dc\uba74 \ub429\ub2c8\ub2e4. \ud2b9\ud788, \ubbf8\ud3ec\uac19\uc740 \uacbd\uc6b0\uc5d0\ub294 \uacf5\uc18d\ud15c\uc744 \uc11e\uc9c0 \uc54a\uc544\ub3c4 W\ub85c \uc778\ud574\uc11c \uacf5\uc18d\uc774 \uc798 \ub098\uc624\ub294 \ud3b8\uc774\uae30 \ub54c\ubb38\uc5d0 \ucca0\uac11\uad81\uc740 \ucd94\ucc9c\ud558\uc9c0 \uc54a\ub294 \ud3b8\uc785\ub2c8\ub2e4. \ud53c\ud761\uc774 \ud544\uc694\ud55c \uacbd\uc6b0\uc5d0\ub294 2\ucf54\uc5b4\uc5d0 \ud53c\ubc14\ub77c\uae30 \uac00\uba74 \ub418\ub2c8\uae4c\uc694.\n \n2\ucf54\uc5b4 \ubbf8\ud3ec\ub294 \uc790\uc720\ub85c\uc6b4 \ud3b8\uc785\ub2c8\ub2e4. \ud53c\ubc14\ub77c\uae30 \uc720\ub839\ubb34\ud76c \ub3c4\ubbf8\ub2c9\uc815\ub3c4 \uc120\uc5d0\uc11c \uace8\ub77c\uac00\uba74 \ub429\ub2c8\ub2e4. \ud53c\ubc14\ub77c\uae30\uac00 \ubb34\ub09c\ud55c \uc120\ud0dd\uc785\ub2c8\ub2e4. \uc77c\ub2e8 \ud53c\ubc14\ub77c\uae30 \uc62c\ub9ac\uba74 \uc6d0\ub51c\ub4e4 \ub9de\ub2e4\uc774\uc5d0 \uac15\ud558\uace0 \ube0c\ub8e8\uc800\uc5d0 \ub300\ucc98\ud558\uae30\ub3c4 \uc88b\uc2b5\ub2c8\ub2e4. \uc720\ub839\ubb34\ud76c\ub294 \uce74\uc774\ud305\ud558\uae30\ub3c4 \uc88b\uace0 DPS\ub298\ub9ac\uae30 \uc88b\uae30 \ub54c\ubb38\uc5d0 \ub51c \ub123\uae30 \ud3b8\ud55c \uad6c\ub3c4\uc5d0\uc11c \uc62c\ub824\uc8fc\uba74 \uc88b\uc2b5\ub2c8\ub2e4. \uc801 \ud0f1\ucee4\uce74 \uc544\uc608 \uadf9\ud55c\uc758 \uc548\ub6ab\ub9ac\ub294 \uc870\ud569\uc774\ub77c\uba74 2\ucf54\uc5b4\uc5d0 \ub3c4\ubbf8\ub2c9 \uc62c\ub9ac\uace0 3\ucf54\uc5b4\uc5d0 \ubb34\ud55c\uc758\ub300\uac80 \uc62c\ub824\ubc84\ub9ac\ub294 \uacbd\uc6b0\ub3c4 \uc788\uc9c0\ub9cc \ub4dc\ubb38 \uacbd\uc6b0\uc785\ub2c8\ub2e4. \uadf8 \uc678\uc5d0 \ub974\ube14\ub791 \ud53c\uc988\ucc98\ub7fc AP\ub51c\uc774 \ub108\ubb34 \uc544\ud504\uace0 \ub300\ucc98\ud558\uae30 \ud798\ub4e0 \uacbd\uc6b0\uc5d0\ub294 \uaf2d 2\ucf54\uc5b4 \uc544\ub2c8\ub354\ub77c\ub3c4 \uc911\uac04\uc5d0 \uc8fc\ubb38\ud3ec\uc2dd\uc790(\uc8fc\ud3ec)\ud558\ub098 \uc815\ub3c4 \uc11e\uc5b4\uc918\ub3c4 \uc88b\uc2b5\ub2c8\ub2e4.\n \n3\ucf54\uc5b4\ub294 \ubb34\ub09c\ud55c \uc0c1\ud669\uc5d0\uc11c\ub294 \ubb34\ud55c\uc758\ub300\uac80(\uc778\ud53c)\ub97c \uad6c\ub9e4\ud574\uc8fc\uc2dc\uace0, \uc0c1\ub300 \ud0f1\ucee4\uac00 \ub9ce\uac70\ub098 \ubc29\uc5b4\ub825\uc774 \ub192\ub2e4\uc2f6\uc73c\uba74 \ub3c4\ubbf8\ub2c9\uacbd\uc758\uc778\uc0ac \uac00\uc8fc\uc2dc\uba74 \ub429\ub2c8\ub2e4.\n \n4\ucf54\uc5b4\ubd80\ud130\ub294 \uc790\uc720\ub86d\uac8c \ud544\uc694\ud55c \uc544\uc774\ud15c \uc0ac\uc8fc\uc2dc\uba74 \ub429\ub2c8\ub2e4.\n \n \n\ubc14\ub4dc\n\ud611\uace1\uc5d0\uc11c\uc758 \ucd94\uc138\n\ud611\uace1 \ubc14\ub4dc\ub294 \uc544\ubb34\ub798\ub3c4 \ub85c\ubc0d\ud615 \uc11c\ud3ec\ud130\uc5d0\ub2e4\uac00 \ubcc0\uc218\ub97c \uac16\ub294 \uc11c\ud3ec\ud130\uc774\ub2e4 \ubcf4\ub2c8 \uc288\ub810 \uad6c\uc6d0\uc774\ub098 \uc288\ub810 \ub9dd\uac11 \ub4f1\uc744 \ud65c\uc6a9\ud55c \ubcc0\uce59\uc801\uc778 \ud50c\ub808\uc774\uac00 \uc8fc\ub825\uc785\ub2c8\ub2e4.\n \n\uce7c\ubc14\ub78c\uc5d0\uc120 \uc11c\ud3ec\ud130,AP,\ud0f1,\uc6d0\ub51c\ub85c \ubaa8\ub450 \ud65c\uc6a9 \uac00\ub2a5\ud569\ub2c8\ub2e4. \uc774 \uae00\uc5d0\uc11c\ub294 \uc6d0\ub51c \ubc14\ub4dc\ub9cc \ub2e4\ub8f9\ub2c8\ub2e4.\n \n\ucc54\ud53c\uc5b8 \ubc38\ub7f0\uc2a4 \ud6a8\uacfc\n\uc785\ud788\ub294 \ud53c\ud574\ub7c9 \uc99d\uac00 15%\n\ubc1b\ub294 \ud53c\ud574\ub7c9 \uac10\uc18c 20%\n \n\uce7c\ubc14\ub78c \ucd94\ucc9c \ub8ec\n\uce7c\ub0a0\ube44(\uc5b4\ub460\uc758\uc218\ud655) \ud53c\uc758\ub9db \uc0ac\ub0e5\uc758\uc99d\ud45c \uad76\uc8fc\ub9b0\uc0ac\ub0e5\uafbc + \uce68\ucc29 \ud54f\ube5b\uae38\n\uc9d1\uc911\uacf5\uaca9 \uce68\ucc29 \ud54f\ube5b\uae38 \uccb4\ub825\ucc28\uadf9\ubcf5 + \uc0ac\ub0e5\uc758\uc99d\ud45c \uad76\uc8fc\ub9b0\uc0ac\ub0e5\uafbc\n \n\uce7c\ubc14\ub78c \ucd94\ucc9c \uc544\uc774\ud15c\n\uc218\ud638\uc790\uc758\ub9dd\uce58 \ud06c\ub77c\ucf04\ud559\uc0b4\uc790 \uace0\uc18d\uc5f0\uc0ac\ud3ec \ubb34\ud55c\uc758\ub300\uac80/\ub3c4\ubbf8\ub2c9\uacbd\uc758\uc778\uc0ac\n \n\uce7c\ubc14\ub78c \ubc14\ub4dc\ub294 \uc785\ud788\ub294 \ud53c\ud574\ub7c9 15% \uc99d\uac00 + \ubc1b\ub294 \ud53c\ud574\ub7c9 20% \uac10\uc18c\ub85c \ubaa8\ub4e0 \ucc54\ud53c\uc5b8 \uc911 \uac00\uc7a5 \ub9ce\uc740 \uce7c\ubc14\ub78c \uc804\uc6a9 \ubc84\ud504\ub97c \ubc1b\uc740 \ucc54\ud53c\uc5b8\uc785\ub2c8\ub2e4. \uadf8\ub807\uae30 \ub54c\ubb38\uc5d0 \uce7c\ubc14\ub78c\uc5d0\uc11c\ub294 \uae30\uc874\uc5d0 \uc0ac\uc6a9\ud558\ub294 AP\ubc14\ub4dc,\uc11c\ud3ff\ubc14\ub4dc \uc678\uc5d0\ub3c4 \ud0f1\ubc14\ub4dc\ub098 \uc6d0\ub51c\ubc14\ub4dc\ub85c\ub3c4 \ud65c\uc6a9\ud560 \uc218 \uc788\uc2b5\ub2c8\ub2e4. \uc774 \uae00\uc740 \uc6d0\ub51c\uc5d0 \ub300\ud574 \ub2e4\ub8e8\ub294 \uae00\uc774\ub2c8 \uc6d0\ub51c \ubc14\ub4dc\uc5d0 \uad00\ud574\uc11c \ub2e4\ub8f9\ub2c8\ub2e4.\n \n\ub2e8\uc21c \uc6d0\ub51c\ub85c\ub9cc \ubcf4\uba74 \uce7c\ub0a0\ube44\uac00 \ub354 \uc88b\uc2b5\ub2c8\ub2e4. \ub2e4\ub9cc, \ub0b4\uac00 \uc6d0\ub51c \ubc14\ub4dc\ub97c \ud560\uc9c0 \uc880 \uc560\ub9e4\ud55c\uac70 \uac19\uc740\ub370 \uc2f6\uc744 \ub54c\ub294 \uc5b4\ub460\uc758\uc218\ud655 \ub4e4\uace0 \uc2dc\uc791\ud558\uc154\uc11c \uc0c1\ub300 \ubcf4\uace0 \uc544\uc774\ud15c \ube4c\ub4dc\ub97c \uc120\ud0dd\ud558\uc154\ub3c4 \ub429\ub2c8\ub2e4. \uadf8\ub9ac\uace0 \uc6d0\ub51c \ubc14\ub4dc\ub294 \uae30\ubcf8\uc801\uc73c\ub85c \uc5b4\ub290\uc815\ub3c4 \uc6d0\ub51c \uc2e4\ub825\uc774 \uc788\ub294 \ubd84\uc5d0 \ud55c\ud574\uc11c \uad8c\ud569\ub2c8\ub2e4.\n \n1\ucf54\uc5b4 \ud06c\ub77c\ucf04\uc744 \uac70\uc758 \ucd94\ucc9c\ud574\ub4dc\ub9ac\uace0\uc694. \n \n2\ucf54\uc5b4 \ubb34\ub09c\ud55c \uac83\uc740 \uace0\uc18d\uc5f0\uc0ac\ud3ec, \uadf8\ub9ac\uace0 \uc0c1\ub300 \ud0f1\ucee4\ub958\uac00 \ub9ce\uc73c\uba74 \ub3c4\ubbf8\ub2c9\uacbd\uc758\uc778\uc0ac \uc0ac\uc8fc\uc2dc\uba74 \ub429\ub2c8\ub2e4. \n \n3\ucf54\uc5b4 \uc0c1\ub300 \ubc29\uc5b4\ub825\uc774 \ub192\uc740 \ud0f1\ucee4\ub958\uac00 \ub9ce\uc73c\uba74 \ub3c4\ubbf8\ub2c9\uacbd\uc758\uc778\uc0ac, \uc77c\ubc18\uc801\uc778 \uc0c1\ud669\uc5d0\uc120 \ubb34\ud55c\uc758\ub300\uac80 \uc0ac\uc8fc\uc138\uc694.\n \n\uad6c\uc778\uc218\ub294 \uac00\uc9c0\ub9c8\uc138\uc694. \uc6d0\ub51c\uc774 \uc544\ub2cc \ucc54\ud504\ub97c \uc6d0\ub51c\ub85c \ub04c\uc5b4\ub2e4 \uc4f8 \ub54c \uad6c\uc778\uc218 \uc4f0\ub294\uac70 \uc544\ub2d9\ub2c8\ub2e4. \uc608\uc804\uc5d0\ub098 \ubab0\uad6c\ub8e8(\ubab0\ub77d \uad6c\uc778\uc218 \ub8e8\ub09c) \uc774\ub7f0\uc2dd\uc73c\ub85c \ud15c\ud2b8\ub9ac \uc37c\ub358\uac70\uc9c0 \uc544\uc774\ud15c \uac1c\ud3b8 \uc774\ud6c4\uc5d0\ub294 \uadf8\ub7f0\uac70 \uac00\ub294\uac70 \uc544\ub2d9\ub2c8\ub2e4.\n \n \n\ubc14\ub8e8\uc2a4\n\ud611\uace1\uc5d0\uc11c\uc758 \ucd94\uc138\n\ud604\uc7ac \ud611\uace1\uc5d0\uc11c\ub294 \uce7c\ub0a0\ube44 + \uc601\uac10\ub8ec\uc744 \ub4e4\uace0 \ubc29\uad00\ud15c\uc744 \uc62c\ub9ac\ub294 \uce7c\ub0a0\ube44 \ubc29\uad00 \ubc14\ub8e8\uc2a4\uac00 \ub728\uace0 \uc788\uc2b5\ub2c8\ub2e4. \uadf8\ub9ac\uace0 \uc608\uc804\uc5d0\ub294 \ub4dc\ub77d\uc0ac\ub974\ub97c \ub9ce\uc774 \uac14\ub294\ub370 \uc694\uc0c8\ub294 \uc790\uac1d\uc758\ubc1c\ud1b1\uc744 \uc62c\ub9ac\ub294 \uc790\ubc1c \ubc14\ub8e8\uc2a4\ub3c4 \ub098\uc624\uace0 \uc788\uc2b5\ub2c8\ub2e4.\n \n\uce7c\ubc14\ub78c\uc5d0\uc120 \uad73\uc774 \uacf5\uc18d\uc744 \uc62c\ub824\uc8fc\ub294 \uba54\uc778\ub8ec\uacfc \ubc29\uad00\uc544\uc774\ud15c\uc744 \uc11e\uc5b4\uc8fc\uba74\uc11c \uac08 \ud544\uc694\uc5c6\uace0 \uadf8\ub0e5 \uce7c\ub0a0\ube44or\uce58\uc18d \ud3c9\ud0c0\uac00\uac70\ub098 \uc720\uc131or\uc218\ud655\ub4e4\uace0 \ud3ec\ud0b9\uac00\uc2dc\uba74 \ub429\ub2c8\ub2e4. \ub2e4\ub9cc, \ubc14\ub8e8\uc2a4\ub85c \ubc29\uad00\uac08\uc9c0 \ud3c9\ud0c0\uac08\uc9c0 \uacb0\uc815\uc774 \uc560\ub9e4\ud55c \uacbd\uc6b0\uc5d0 \uce7c\ub0a0\ube44 \ub4e4\uc5b4\uc8fc\uba74 \uc88b\uc2b5\ub2c8\ub2e4. \uc774\ub7ec\uba74 \ub85c\ub529\ucc3d\uc5d0\uc11c \uc0c1\ub300 \uc870\ud569\ubcf4\uace0 \uc544\uc774\ud15c\uc744 \uc120\ud0dd\ud560 \uc218 \uc788\ub294 \uc7a5\uc810\uc774 \uc788\uc2b5\ub2c8\ub2e4.\n \n\ucc54\ud53c\uc5b8 \ubc38\ub7f0\uc2a4 \ud6a8\uacfc\n\uc785\ud788\ub294 \ud53c\ud574\ub7c9 \uac10\uc18c 5%\n\ubc1b\ub294 \ud53c\ud574\ub7c9 \uc99d\uac00 5%\n \n\ud3ec\ud0b9 \ubc14\ub8e8\uc2a4\n\uce7c\ubc14\ub78c \ucd94\ucc9c \ub8ec\n\uce7c\ub0a0\ube44 \ud53c\uc758\ub9db \uc0ac\ub0e5\uc758\uc99d\ud45c \uad76\uc8fc\ub9b0\uc0ac\ub0e5\uafbc + \uce68\ucc29 \ubbfc\ucca9\ud568/\uccb4\ub825\ucc28\uadf9\ubcf5\n\uc2e0\ube44\ub85c\uc6b4\uc720\uc131 \ub9c8\ub098\uc21c\ud658\ud314\ucc0c \uae68\ub2ec\uc74c \uc8fc\ubb38\uc791\uc5f4 + \ube44\uc5f4\ud55c\ud55c\ubc29 \uad76\uc8fc\ub9b0\uc0ac\ub0e5\uafbc\n\uc5b4\ub460\uc758\uc218\ud655 \ud53c\uc758\ub9db \uc0ac\ub0e5\uc758\uc99d\ud45c \uad76\uc8fc\ub9b0\uc0ac\ub0e5\uafbc + \uce68\ucc29 \uccb4\ub825\ucc28\uadf9\ubcf5\n \n\uce7c\ubc14\ub78c \ucd94\ucc9c \uc544\uc774\ud15c\n\uc218\ud638\uc790\uc758\uac80+\uc5ec\uc2e0\uc758\ub208\ubb3c \ub4dc\ub77d\uc0ac\ub974 \ub9c8\ub098\ubb34\ub124 \uc138\ub9b4\ub2e4\uc758\uc6d0\ud55c/\ub3c5\uc0ac\uc758\uc1a1\uacf3\ub2c8 \ub4f1 \ubc29\uad00\ud15c\n \n\ub2e8\uc21c \ud3ec\ud0b9\ubc14\ub8e8\uc2a4\ub85c\ub9cc \ubcf4\uba74 \uc720\uc131\ub8ec\uc774 \ucd08\ubc18\uc5d0 \uc555\ubc15\ud558\uae30\ub3c4 \uc88b\uace0 \ud6e8\uc52c \uc88b\uc2b5\ub2c8\ub2e4. \uadf8\ub798\uc11c \ud53d\ucc3d \ub2e8\uacc4\uc5d0\uc11c \ud0f1\ucee4\ub97c \uc7a1\uc544 \uc904 \uc218 \uc788\ub294 \ub2e4\ub978 \uc6d0\ub51c\uc774\ub098 \ube0c\ub8e8\uc800 \ucc54\ud504\ub4e4\uc774 \ucda9\ubd84\ud558\ub2e4\uace0 \ud310\ub2e8\ub418\uba74 \uc720\uc131\ub8ec\uc744 \ub4e4\uc5b4\uc8fc\uc2dc\uace0, \uc544 \uc774\uac70 \ub0b4\uac00 \ud3c9\ud0c0\ub85c \uac08\uc9c0 \ud3ec\ud0b9\uc73c\ub85c \uac08\uc9c0 \uc560\ub9e4\ud574\uc11c \uc0c1\ub300 \ud53d \ubb54\uc9c0 \ubcf4\uace0 \ub9de\ucdb0\uac00\uc57c\uaca0\ub294\ub370 \uc2f6\ub2e4\uba74 \uce7c\ub0a0\ube44\ub97c \ub4e4\uc5b4\uc8fc\uc138\uc694.\n \n\ub4dc\ub77d\uc0ac\ub974 \ub9c8\ub098\ubb34\ub124 \uc774\ud6c4\uc5d0 \uc138\ub9b4\ub2e4\uc758\uc6d0\ud55c \ub3c5\uc0ac\uc758\uc1a1\uacf3\ub2c8 \ubc24\uc758\ub05d\uc790\ub77d \uad76\uc8fc\ub9b0\ud788\ub4dc\ub77c \ub4f1 \uc720\ub3d9\uc801\uc73c\ub85c \uace8\ub77c\uc11c \uac00\uc8fc\uc2dc\uba74 \ub429\ub2c8\ub2e4. \ub4dc\ub77d\uc0ac\ub974\uc640 \ub9c8\ub098\ubb34\ub124 \uc21c\uc11c\ub294 \uc720\ub3d9\uc801\uc73c\ub85c \uc120\ud0dd\ud574\uc8fc\uc2dc\uba74 \ub429\ub2c8\ub2e4. \uc5ec\ub208 \uc2a4\ud0dd \ub2e4 \ucc28\uc788\uac70\ub098 \uc77c\uc2dc\ubd88\ub85c \ub9c8\ub098\ubb34\ub124 \uc0b4 \uc218 \uc788\ub294 \uacbd\uc6b0\uc5d0\ub294 \ub9c8\ub098\ubb34\ub124 \uc0ac\uc8fc\uc2dc\uba74 \ub418\uace0, \uc5ec\uc2e0\uc758\ub208\ubb3c \uc2a4\ud0dd\uc774 \ub9ce\uc774 \uc548 \uc313\uc600\uac70\ub098 \uc560\ub9e4\ud55c \uace8\ub4dc\uc77c \ub54c\uc5d0\ub294 \ub4dc\ub77d\uc0ac\ub974 \uc0ac\uc8fc\uc2dc\uba74 \ub429\ub2c8\ub2e4.\n \n\ud611\uace1\uc5d0\uc11c\ub294 \uc790\uac1d\uc758\ubc1c\ud1b1(\uc790\ubc1c) \ubc14\ub8e8\uc2a4\ub3c4 \uc885\uc885 \ub098\uc624\uace4 \uc788\uc9c0\ub9cc, 90\ucd08\ub85c \ub108\ud504\ub3c4 \ub418\uc5c8\uace0 \uce7c\ubc14\ub78c\uc5d0\uc120 \uad73\uc774 \uc548\uac00\ub3c4 \ub420\uac70 \uac19\uc544\uc694.\n \n\ud3c9\ud0c0 \ubc14\ub8e8\uc2a4\n\uce7c\ubc14\ub78c \ucd94\ucc9c \ub8ec\n\uce7c\ub0a0\ube44 \ud53c\uc758\ub9db \uc0ac\ub0e5\uc758\uc99d\ud45c \uad76\uc8fc\ub9b0\uc0ac\ub0e5\uafbc + \uce68\ucc29 \ubbfc\ucca9\ud568/\uccb4\ub825\ucc28\uadf9\ubcf5\n\uce58\uba85\uc801\uc18d\ub3c4 \uce68\ucc29 \ubbfc\ucca9\ud568 \ucd5c\ud6c4\uc758\uc77c\uaca9 + \uc0ac\ub0e5\uc758\uc99d\ud45c(\ud53c\uc758\ub9db) \uad76\uc8fc\ub9b0\uc0ac\ub0e5\uafbc\n \n\uce7c\ubc14\ub78c \ucd94\ucc9c \uc544\uc774\ud15c\n\uc218\ud638\uc790\uc758\ub9dd\uce58 \ubd88\uba78\uc758\ucca0\uac11\uad81 \ub8e8\ub09c\uc758\ud5c8\ub9ac\ucf00\uc778(\uc720\ub839\ubb34\ud76c)/\ub9c8\ubc95\uc0ac\uc758\ucd5c\ud6c4 \uad6c\uc778\uc218\uc758\uaca9\ub178\uac80/\ubab0\ub77d\ud55c\uc655\uc758\uac80/\ub3c4\ubbf8\ub2c9\uacbd\uc758\uc778\uc0ac\n \n\uc694\uc0c8 \ubcf4\uba74 \ud3ec\ud0b9\uac08\uc9c0 \ud3c9\ud0c0\uac08\uc9c0 \uc560\ub9e4\ud55c \uacbd\uc6b0\ub3c4 \ub9ce\uace0 \uc0c1\ub300\ub97c \ubcf4\uace0 \ub9de\ucdb0\uac00\uba74 \uc88b\uae30 \ub54c\ubb38\uc5d0 \uce7c\ub0a0\ube44\ub97c \ud6e8\uc52c \ub354 \uc790\uc8fc \uac00\ub294 \ud3b8\uc785\ub2c8\ub2e4. \uce7c\ub0a0\ube44\ub294 \ud3ec\ud0b9 \ud15c\ud2b8\ub9ac\uc5d0\ub3c4 \uc5b4\uc6b8\ub9ac\uae30 \ub54c\ubb38\uc785\ub2c8\ub2e4.\n \n\ucd5c\uadfc \ud3c9\ud0c0 \ubc14\ub8e8\uc2a4\ud560 \ub54c\ub294 \ucca0\uac11\uad81\uc744 \ub354 \uc790\uc8fc \uac00\uace0 \uc788\uc2b5\ub2c8\ub2e4.\n \n\ucca0\uac11\uad81 \uc62c\ub9ac\uace0 2\ucf54\uc5b4\ubd80\ud130\ub294 \uc720\ub3d9\uc801\uc73c\ub85c \ub8e8\ub09c\uc758\ud5c8\ub9ac\ucf00\uc778\uc774\ub098 \ub9c8\ubc95\uc0ac\uc758\ucd5c\ud6c4 \ub4f1 \uac00\uc8fc\uc2dc\ub2e4\uac00 \uad6c\uc778\uc218\ub098 \ubab0\ub77d\ud55c\uc655\uc758\uac80 \uc62c\ub824\uc8fc\uc2dc\uba74 \ub429\ub2c8\ub2e4.\n \n \n\ubca0\uc778\n\ud611\uace1\uc5d0\uc11c\uc758 \ucd94\uc138\n\ud611\uace1 \ubca0\uc778\uc740 \ud06c\ub77c\ucf04\uc744 \ub9ce\uae30 \uac00\uace0, \ub3cc\ud48d\uc774\ub098 \ucca0\uac11\uad81\ub3c4 \ucde8\ud5a5\uc5d0 \ub530\ub77c \uc62c\ub9b4 \uc218 \uc788\uc2b5\ub2c8\ub2e4. \n\ud15c\ud2b8\ub9ac\ub294 \ud06c\ub77c\ucf04 \uc720\ub839\ubb34\ud76c \ubb34\ud55c\uc758\ub300\uac80 \ubc14\ub85c \uc62c\ub9ac\ub294\uac8c \ub300\uc138\uc785\ub2c8\ub2e4.\n \n\uce7c\ubc14\ub78c \ubca0\uc778\uc740 \ud611\uace1\uc5d0 \ube44\ud574\uc11c \ub3cc\ud48d\uc758 \ube48\ub3c4\uac00 \uc801\uc5b4\uc9c0\uace0 \ucca0\uac11\uad81\uc758 \ube48\ub3c4\uac00 \uc870\uae08 \ub354 \ub192\uc544\uc9c0\ub294 \uc815\ub3c4\uc785\ub2c8\ub2e4.\n \n\ucc54\ud53c\uc5b8 \ubc38\ub7f0\uc2a4 \ud6a8\uacfc\n\uc5c6\uc74c\n \n\uce7c\ubc14\ub78c \ucd94\ucc9c \ub8ec \n\uc9d1\uc911\uacf5\uaca9 \uacfc\ub2e4\uce58\uc720 \ubbfc\ucca9\ud568 \ucd5c\ud6c4\uc758\uc77c\uaca9 + \uc0ac\ub0e5\uc758\uc99d\ud45c(\ud53c\uc758\ub9db) \uad76\uc8fc\ub9b0\uc0ac\ub0e5\uafbc\n \n\uce7c\ubc14\ub78c \ucd94\ucc9c \uc544\uc774\ud15c\n\uc218\ud638\uc790\uc758\ub9dd\uce58 \ubd88\uba78\uc758\ucca0\uac11\uad81 \uc720\ub839\ubb34\ud76c(\ub9c8\ubc95\uc0ac\uc758\ucd5c\ud6c4) \uad6c\uc778\uc218\uc758\uaca9\ub178\uac80 \ubab0\ub77d\ud55c\uc655\uc758\uac80\n \n(\ub418\uac8c \uc624\ub798 \uc0b4\uc544\ub0a8\ub294 \uacbd\uc6b0\ub97c \uc81c\uc678\ud558\uace0) \ub300\uccb4\ub85c \uce68\ucc29\uc774 \uc5c6\ub354\ub77c\ub3c4 \ub9c8\ub098\uad00\ub9ac\uac00 \ub418\ub294 \ucc54\ud504\ub77c \uacfc\ub2e4\uce58\uc720 \ucc0d\ub294 \uac83\uc744 \ucd94\ucc9c.\n \n\uce7c\ubc14\ub78c \ubca0\uc778\uc740 \uc6b0\ub9ac\ud300\uc774 \uc11c\ud3ec\ud305\uc774 \uc88b\uac70\ub098 \uc55e\ub77c\uc778\uc774 \ub108\ubb34 \uc88b\uc544\uc11c \ubca0\uc778\uc774 \ud504\ub9ac\ub51c \uac01\uc744 \ub123\uc744 \uc218 \uc788\ub294 \uacbd\uc6b0\ub97c \uc81c\uc678\ud558\uace0\ub294 \ucca0\uac11\uad81 \ud2b8\ub9ac\uac00 \ub354 \uc548\uc815\uc801\uc774\ub77c\uace0 \ubd05\ub2c8\ub2e4.\n \n1\ucf54\uc5b4 \ucca0\uac11\uad81 \uac00\uc8fc\uc2dc\uace0, 2\ucf54\uc5b4\ub294 \uc0c1\ub300\uac00 \ub9cc\uc57d AP\ub51c\uc774 \ub9ce\uc73c\uba74 \ub9c8\ubc95\uc0ac\uc758\ucd5c\ud6c4 \uadf8\uac8c \uc544\ub2c8\uba74 \uc720\ub839\ubb34\ud76c \uac00\uc8fc\uc2dc\uba74 \ub418\uace0\uc694.\n \n3\ucf54\uc5b4 \uad6c\uc778\uc218 \uc62c\ub824\uc8fc\uc2dc\uace0 4\ucf54\uc5b4\ub294 \ubab0\ub77d\ud55c\uc655\uc758\uac80 \ub780\ub450\uc778\uc758\uc608\uc5b8 \uc815\ub839\uc758\ud615\uc0c1 \ub4f1 \uc0ac\uc8fc\uc2dc\uba74 \ub429\ub2c8\ub2e4.\n \n \n\uc0ac\ubbf8\ub77c\n\ud611\uace1\uc5d0\uc11c\uc758 \ucd94\uc138\n\ud611\uace1 \uc0ac\ubbf8\ub77c\uc640 \uce7c\ubc14\ub78c \uc0ac\ubbf8\ub77c\ub294 \ud06c\uac8c \ub2e4\ub974\uc9c4 \uc54a\uc2b5\ub2c8\ub2e4. \ub8ec\uacfc \ud15c\ud2b8\ub9ac \ubaa8\ub450 \ube44\uc2b7\ud574\uc694.\n\ud604\uc7ac \uac70\uc758 \ucca0\uac11\uad81 \uc9d5\uc218\uc758\ucd1d \ubb34\ud55c\uc758\ub300\uac80\uc774\uace0 \uce7c\ubc14\ub78c\uc740 \ucca0\uac11\uad81 \uadf8\ub300\ub85c \uac00\uba74 \ub420\uac70 \uac19\uc2b5\ub2c8\ub2e4.\n \n\ucc54\ud53c\uc5b8 \ubc38\ub7f0\uc2a4 \ud6a8\uacfc\n\uc5c6\uc74c\n \n\uce7c\ubc14\ub78c \ucd94\ucc9c \ub8ec \n\uc815\ubcf5\uc790 \uce68\ucc29 \ud54f\ube5b\uae38 \ucd5c\ud6c4\uc758\uc800\ud56d + \uc0ac\ub0e5\uc758\uc99d\ud45c(\ud53c\uc758\ub9db) \uad76\uc8fc\ub9b0\uc0ac\ub0e5\uafbc\n \n\uce7c\ubc14\ub78c \ucd94\ucc9c \uc544\uc774\ud15c\n\uc218\ud638\uc790\uc758\ub9dd\uce58 \ubd88\uba78\uc758\ucca0\uac11\uad81 \uc9d5\uc218\uc758\ucd1d/\ud53c\ubc14\ub77c\uae30 \ubb34\ud55c\uc758\ub300\uac80/\ub3c4\ubbf8\ub2c9\uacbd\uc758\uc778\uc0ac\n \n\uc815\ubcf5\uc790 \ud54f\ube5b\uae38 \ucd5c\ud6c4\uc758\uc800\ud56d + \uad76\uc8fc\ub9b0\uc0ac\ub0e5\uafbc\uc740 \ud544\uc218\uc774\uace0, \uce68\ucc29\uacfc \uacfc\ub2e4\uce58\uc720\ub9cc \uc5ec\uc2e0\uc758\ub208\ubb3c \uc0c0\ub2e4 \ud314\uac74\uc9c0\uc5d0 \ub530\ub77c \uace8\ub77c\uc8fc\uc2dc\uba74 \ub418\uace0, \ubcf4\uc870\ub8ec \ud55c\uc790\ub9ac\ub294 \ud53c\uc758\ub9db \uc0ac\ub0e5\uc758\uc99d\ud45c \ub3cc\ubc1c\uc77c\uaca9 \uc911 \ucde8\ud5a5\uc73c\ub85c \ucc0d\uc5b4\uc8fc\uc2dc\uba74 \ub429\ub2c8\ub2e4.\n \n\uc2dc\uc791 \uc544\uc774\ud15c\uc740 \ud06c\uac8c 2\uac00\uc9c0\uc5d0\uc694. \uacfc\ub2e4\uce58\uc720 \ucc0d\uace0 \uc218\ud638\uc790\uc758\ub9dd\uce58 \uc5ec\uc2e0\uc758\ub208\ubb3c \ubb3c\uc57d \ud558\ub098 \uc0ac\uc11c \uc2dc\uc791\ud588\ub2e4\uac00 \ub098\uc911\uc5d0 \uc5ec\uc2e0\uc758\ub208\ubb3c \ud314\ub4e0\uc9c0, \uce68\ucc29 \ucc0d\uace0 \uc218\ud638\uc790\uc758\ub9dd\uce58 \uc2e0\ubc1c/\ub2e8\uac80 \ucda9\uc804\ud615\ubb3c\uc57d \uc2dc\uc791\ud558\ub4e0\uc9c0\uc694.\n \n1\ucf54\uc5b4 \ubd88\uba78\uc758\ucca0\uac11\uad81 \uace0\uc815\uc785\ub2c8\ub2e4\n \n2\ucf54\uc5b4 \uc9d5\uc218\uc758\ucd1d \ubcf4\ud1b5 \uc62c\ub824\uc8fc\ub294\ub370 \uc0c1\ub300 \ud3ec\ud0b9\uc774 \uc88b\uac70\ub098 \ube61\uc138\uba74 \ud53c\ubc14\ub77c\uae30 \uac00\uc8fc\uc138\uc694.\n \n3\ucf54\uc5b4 \uc0c1\ub300 \ud0f1\ucee4 \ubc29\uc5b4\ub825\uc774 \ub192\uc544\uc11c \ub6ab\uc5b4\uc57c \ud558\uba74 \ub3c4\ubbf8\ub2c9, \uadf8\uac8c \uc544\ub2c8\uba74 \ubb34\ud55c\uc758\ub300\uac80(\uc778\ud53c) \uc0ac\uc8fc\uc138\uc694.\n \n\uc0ac\ubbf8\ub77c \uc2e0\ubc1c\uc740 \ub2e4\ub978 \uc6d0\ub51c\uc5d0 \ube44\ud574\uc11c \uacf5\uc18d\uc774 \uc911\uc694\ud55c \ucc54\ud504\ub3c4 \uc544\ub2c8\ub77c\uc11c \uad11\uc804\uc0ac\uac00 \uac15\uc81c\ub418\uc9c0 \uc54a\uae30 \ub54c\ubb38\uc5d0 \ud310\uae08\uc7a5\ud654\ub098 \ud5e4\ub974\uba54\uc2a4 \uc0ac\uc8fc\uc2dc\uba74 \ub429\ub2c8\ub2e4.\n \n \n\uc2dc\ube44\ub974\n\ud611\uace1\uc5d0\uc11c\uc758 \ucd94\uc138\n\ud611\uace1 \ucd5c\uadfc \uc2dc\ube44\ub974\ub294 \ubb34\uc870\uac74 \ubc29\uad00\uc785\ub2c8\ub2e4. \uce58\uba85\ud0c0 \uac08\uac70\uba74 \ub2e4\ub978 \uc6d0\ub51c \ubf51\uace0 \ub9d0\uc8e0.\n \n\uce7c\ubc14\ub78c\uc5d0\uc11c \uc2dc\ube44\ub974\ub294 \uc6d0\ub51c\uc774\ub77c\uae30\ubcf4\ub2e4 \uc560\uc26c\ucc98\ub7fc \ud3ec\ud0b9\ucc54\ud504\ub77c\ub3c4 \ubd10\ub3c4 \ubb34\ubc29\ud569\ub2c8\ub2e4. \n\uadf8\ub9cc\ud07c \uc785\ud788\ub294\ud53c\ud574\ub7c9 \ub108\ud504\ub97c \ub9ce\uc774 \ub2f9\ud588\uc2b5\ub2c8\ub2e4.\n \n\ucc54\ud53c\uc5b8 \ubc38\ub7f0\uc2a4 \ud6a8\uacfc\n\uc785\ud788\ub294 \ud53c\ud574\ub7c9 \uac10\uc18c 15%\n\ubc1b\ub294 \ud53c\ud574\ub7c9 \uc99d\uac00 5%\n \n\ubc29\uad00 \ud3ec\ud0b9 \uc2dc\ube44\ub974\n\uce7c\ubc14\ub78c \ucd94\ucc9c \ub8ec\n\uc5b4\ub460\uc758\uc218\ud655 \ud53c\uc758\ub9db \uc0ac\ub0e5\uc758\uc99d\ud45c \uad76\uc8fc\ub9b0\uc0ac\ub0e5\uafbc + \uce68\ucc29 \uccb4\ub825\ucc28\uadf9\ubcf5\n \n\uce7c\ubc14\ub78c \ucd94\ucc9c \uc544\uc774\ud15c\n\uc218\ud638\uc790\uc758\uac80+\uc5ec\uc2e0\uc758\ub208\ubb3c \ub9c8\ub098\ubb34\ub124 \ub4dc\ub77d\uc0ac\ub974 \uc138\ub9b4\ub2e4\uc758\uc6d0\ud55c/\ub3c5\uc0ac\uc758\uc1a1\uacf3\ub2c8 \ub4f1\n \n\ub4dc\ub77d\uc0ac\ub974 \ub9c8\ub098\ubb34\ub124 \uc21c\uc11c\ub294 \uc720\ub3d9\uc801\uc73c\ub85c \ubc14\uafd4\uc8fc\uc2dc\uba74 \ub429\ub2c8\ub2e4. \uc8fd\uc5c8\ub294\ub370 \ub4dc\ub77d\uc0ac\ub974 \ub098\uc624\uba74 \ub4dc\ub77d\uc0ac\ub974 \uc0ac\uc8fc\uc2dc\uba74 \ub418\uace0, \uc5ec\ub208 \uc2a4\ud0dd \ub2e4 \uc313\uc600\uc73c\uba74 \ubc14\ub85c \ub9c8\ub098\ubb34\ub124\ubd80\ud130 \uac00\uc8fc\uc154\ub3c4 \ub429\ub2c8\ub2e4. \n \n\uc77c\ubc18\uc801\uc73c\ub85c \ub9c8\ub098\ubb34\ub124 \ub4dc\ub77d\uc0ac\ub974 \uace0\uc815\uc73c\ub85c \uac00\uc8fc\uc2dc\uace0 3\ucf54\uc5b4\ubd80\ud130\ub294 \ub3c5\uc0ac\uc758\uc1a1\uacf3\ub2c8\ub098 \uc138\ub9b4\ub2e4\uc758\uc6d0\ud55c\uc744 \uc8fc\ub85c \uc0ac\uc8fc\uace0 \ub9de\ucdb0\uac70 \uc0ac\uc8fc\uc2dc\uba74 \ub429\ub2c8\ub2e4.\n \n\uce58\uba85\ud0c0 \uc2dc\ube44\ub974\n\uce7c\ubc14\ub78c \ucd94\ucc9c \ub8ec \n\uce58\uba85\uc801\uc18d\ub3c4 \uce68\ucc29 \ud54f\ube5b\uae38 \uccb4\ub825\ucc28\uadf9\ubcf5 + \uc0ac\ub0e5\uc758\uc99d\ud45c \uad76\uc8fc\ub9b0\uc0ac\ub0e5\uafbc\n \n\uce7c\ubc14\ub78c \ucd94\ucc9c \uc544\uc774\ud15c\n\uc218\ud638\uc790\uc758\ub9dd\uce58+\uc5ec\uc2e0\uc758\ub208\ubb3c \ubd88\uba78\uc758\ucca0\uac11\uad81/\ud06c\ub77c\ucf04\ud559\uc0b4\uc790 \uc815\uc218\uc57d\ud0c8\uc790 \ubb34\ud55c\uc758\ub300\uac80/\ub3c4\ubbf8\ub2c9\uacbd\uc758\uc778\uc0ac\n \n\uc218\ud638\uc790\uc758\ub9dd\uce58 \uc5ec\uc2e0\uc758\ub208\ubb3c \ubb3c\uc57d \uc2dc\uc791\ud574\uc11c \uc5ec\uc2e0\uc758\ub208\ubb3c\uc740 \uc815\uc218\uc57d\ud0c8\uc790 \ub098\uc624\ub294 \ud0c0\uc774\ubc0d\uc5d0 \ud314\uc544\uc8fc\uc138\uc694.\n\uc5ec\uc2e0\uc758\ub208\ubb3c \uc2dc\uc791\ud558\uba74 \ub9c8\ub098\uad00\ub9ac\uac00 \uc26c\uc6cc\uc11c Q\uc9e4\uc774\ub098 W\ub85c \ub77c\uc778\ud074\ub9ac\uc5b4\ud558\uae30\ub3c4 \uc88b\uc544\uc11c \ucd08\ubc18\uc5d0 \uad49\uc7a5\ud788 \ud3b8\ud569\ub2c8\ub2e4.\n \n\uc6d0\ub798 \uce7c\ubc14\ub78c \ub51c \ub108\ud504 15% \uba39\uc740 \uc774\ud6c4\ub85c \ud06c\ub77c\ucf04 \ubb34\uc870\uac74 \uace0\uc815\ud588\ub294\ub370, \uc694\uc0c8\ub294 \uadf8\ub0e5 \uc0dd\uc874\uc774 \ucd5c\uace0\ub77c\uace0 \uc0dd\uac01\ud574\uc11c \ubd88\uba78\uc758\ucca0\uac11\uad81\ub97c \ub2e4\uc2dc \ub9ce\uc774 \uac00\uace0 \uc788\uc2b5\ub2c8\ub2e4. \ub51c \ud3b8\ud560 \ub54c\ub9cc \ud06c\ub77c\ucf04 \uc0ac\uc90d\ub2c8\ub2e4. \n \n\uce58\uba85\ud0c0 \uc2dc\ube44\ub974\ub294 \ubb34\ud55c\uc758\ub300\uac80\uc744 \ubf51\uc544\uc8fc\ub294\uac8c \ud575\uc2ec\uc785\ub2c8\ub2e4. \uadf8\uc804\uae4c\uc9c0\ub294 \ub51c\uc774 \ub108\ubb34 \uc57d\ud574\uc694.\n \n \n\uc138\ub098\n\ud611\uace1\uc5d0\uc11c\uc758 \ucd94\uc138\n\ud611\uace1 \uc138\ub098\ub294 \ud06c\ub77c\ucf04 \uad6c\uc778\uc218 \ud15c\ud2b8\ub9ac\uac00 \ub300\uc138\uc600\ub2e4\uac00 \uc2e0\ud30c\uc790 \ubc84\ud504 \uc774\ud6c4\uc5d0\ub294 \uc2e0\ud30c\uc790 \uc138\ub098\ub3c4 \ub9ce\uc774 \ub098\uc635\ub2c8\ub2e4.\n \n\uce7c\ubc14\ub78c \uc138\ub098\ub294 \ud06c\ub77c\ucf04 \uad6c\uc778\uc218 \ud15c\ud2b8\ub9ac\ub97c \ucd94\ucc9c\ud569\ub2c8\ub2e4. \uc2e0\ud30c\uc790 \uc138\ub098\ub294 \uc6d0\uac70\ub9ac \uc2e0\ud30c\uc790 \ub108\ud504 \uc774\ud6c4\uc5d0 \ub51c\uc774 \ub108\ubb34 \uc548\ub098\uc628\ub2e4\uace0 \uc0dd\uac01\ud569\ub2c8\ub2e4.\n \n\ucc54\ud53c\uc5b8 \ubc38\ub7f0\uc2a4 \ud6a8\uacfc\n\uc785\ud788\ub294 \ud53c\ud574\ub7c9 \uac10\uc18c 3%\n \n\uce7c\ubc14\ub78c \ucd94\ucc9c \ub8ec\n\ube59\uacb0\uac15\ud654 \ub9c8\ubc95\uc758\uc2e0\ubc1c \ube44\uc2a4\ud0b7\ubc30\ub2ec \ucf8c\uc18d\uc811\uadfc + \ud54f\ube5b\uae38(\ubbfc\ucca9\ud568) \uccb4\ub825\ucc28\uadf9\ubcf5/\uacfc\ub2e4\uce58\uc720\n \n\uce7c\ubc14\ub78c \ucd94\ucc9c \uc544\uc774\ud15c\n\uc218\ud638\uc790\uc758\ub9dd\uce58+\uc5ec\uc2e0\uc758\ub208\ubb3c \ud06c\ub77c\ucf04\ud559\uc0b4\uc790 \uace0\uc18d\uc5f0\uc0ac\ud3ec (\ub9c8\ub098\ubb34\ub124) \uad6c\uc778\uc218\uc758\uaca9\ub178\uac80 \ub3c4\ubbf8\ub2c9\uacbd\uc758\uc778\uc0ac/\ud53c\ubc14\ub77c\uae30\n \n\uc138\ub098\uc758 \ub8ec\uc740 \ube59\uacb0 \ucc29\ucde8 \uc218\ud655 \ub4f1 \ucde8\ud5a5\uc5d0 \ub530\ub77c \uac08\ub9b4 \uc218 \uc788\uc9c0\ub9cc \uac00\uc7a5 \ubb34\ub09c\ud55c\uac74 \ube59\uacb0\uc785\ub2c8\ub2e4.\n \n\ubcf4\uc870\ub8ec\uc740 \uce68\ucc29 \uacfc\ub2e4\uce58\uc720 \ud54f\ube5b\uae38 \ubbfc\ucca9\ud568 \uccb4\ub825\ucc28\uadf9\ubcf5 \uc911 \ub9c8\uc74c\uc5d0 \ub4dc\ub294\uac8c 2\uac1c \ub123\uc73c\uba74 \ub429\ub2c8\ub2e4.\n \n`\n\ud611\uace1\uc5d0\uc11c\uc758 \ucd94\uc138\n\ud611\uace1 \uc544\ud06c\uc0e8\uc740 \uc6d0\ub51c \ud3ec\uc9c0\uc158\ubcf4\ub2e4\ub294 \ubbf8\ub4dc \ud3ec\uc9c0\uc158\uc73c\ub85c \ud65c\uc6a9\ub418\uba70, \uc9d1\uacf5 + \ubcf4\uc870 \ub8ec\uc5d0 \ubcf4\ud638\ub9c9\uac15\ud0c0\uc640 \ubf08\ubc29\ud328\ub97c \ub123\uc73c\uba74\uc11c \uc544\ud06c\uc0e8\uc5d0 \uc274\ub4dc\ub97c \uc774\uc6a9\ud574\uc11c \ud328\ub294 \uc2a4\ud0c0\uc77c\ub85c \ud65c\uc6a9\ud569\ub2c8\ub2e4. \uc544\uc774\ud15c \uc21c\uc11c\ub294 \ud1b5\uacc4\uc0c1\uc73c\ub85c\ub294 \ub9c8\ucd5c\uc640 \ud06c\ub77c\ucf04\uc774 \ub9ce\uc9c0\ub9cc, \ucd5c\uc0c1\uc704\uad8c\uc73c\ub85c \uac00\uba74 \ucca0\uac11\uad81 \ube44\uc911\uc774 \ub192\uc544\uc9d1\ub2c8\ub2e4. E \uc120\ub9c8\ud569\ub2c8\ub2e4.\n \n\uce7c\ubc14\ub78c \uc544\ud06c\uc0e8\uc740 \ubcf4\uc870 \ub8ec\uc5d0 \uacb0\uc758\uac00 \uc544\ub2cc \uc9c0\ubc30(\uad76\uc8fc\ub9b0\uc0ac\ub0e5\uafbc + \ud53c\uc758\ub9dbor\uc0ac\ub0e5\uc758\uc99d\ud45c)\ub97c \ub123\uc5b4\uc8fc\uba74 \ub429\ub2c8\ub2e4. \n \n\ucc54\ud53c\uc5b8 \ubc38\ub7f0\uc2a4 \ud6a8\uacfc\n\uc5c6\uc74c\n \n\uce7c\ubc14\ub78c \ucd94\ucc9c \ub8ec\n\uc9d1\uc911\uacf5\uaca9 \uce68\ucc29 \ud54f\ube5b\uae38 \ucd5c\ud6c4\uc758\uc77c\uaca9 + \uc0ac\ub0e5\uc758\uc99d\ud45c \uad76\uc8fc\ub9b0\uc0ac\ub0e5\uafbc\n \n\uce7c\ubc14\ub78c \ucd94\ucc9c \uc544\uc774\ud15c\n\uc218\ud638\uc790\uc758\ub9dd\uce58 \ubd88\uba78\uc758\ucca0\uac11\uad81 \uc9d5\uc218\uc758\ucd1d \ubb34\ud55c\uc758\ub300\uac80/\ub3c4\ubbf8\ub2c9\uacbd\uc758\uc778\uc0ac\n\uc218\ud638\uc790\uc758\ub9dd\uce58 \ubd88\uba78\uc758\ucca0\uac11\uad81 \ub3c4\ubbf8\ub2c9\uacbd\uc758\uc778\uc0ac \ubb34\ud55c\uc758\ub300\uac80\n\uc218\ud638\uc790\uc758\ub9dd\uce58 \ub9c8\ubc95\uc0ac\uc758\ucd5c\ud6c4 \ubd88\uba78\uc758\ucca0\uac11\uad81 \uc9d5\uc218\uc758\ucd1d \ubb34\ud55c\uc758\ub300\uac80\n \n\uac04\ub2e8\ud558\uac8c \uc0dd\uac01\ud558\uba74 \ucca0\uac11\uad81 \uc9d5\uc218\uc758\ucd1d \ubb34\ud55c\uc758\ub300\uac80 \ud15c\ud2b8\ub9ac\uc5d0\uc11c \uc0c1\ub300 AP\uac00 \uc544\ud504\uba74 \ub9c8\ucd5c \uc11e\uc5b4\uc8fc\uace0, \uc0c1\ub300 \uc55e\ub77c\uc778\uc774 \ub2e8\ub2e8\ud558\uba74 \uc9d5\uc218\uc758\ucd1d \ub300\uc2e0 \ub3c4\ubbf8\ub2c9\uacbd\uc758\uc778\uc0ac \uba3c\uc800 \ubf51\uc544\uc900\ub2e4\uace0 \uc0dd\uac01\ud558\uc2dc\uba74 \ub429\ub2c8\ub2e4. \ub9c8\ucd5c\ub97c \ubf51\uc744 \ub54c\uc5d0\ub294 \ubb34\ud55c\uc758\ub300\uac80 \ub300\uc2e0 \uad6c\uc778\uc218 \uac00\uba74 \uc88b\uc2b5\ub2c8\ub2e4.\n \n1\ucf54\uc5b4\ub294 \uc0c1\ub300 AP\ube44\uc911\uc774 \ub192\uc73c\uba74 \ub9c8\ucd5c\ub97c \uac00\uace0, \uc77c\ubc18\uc801\uc778 \uc0c1\ud669\uc774\uba74 \ucca0\uac11\uad81 \uac00\uc8fc\uc2dc\uba74 \ub429\ub2c8\ub2e4. \ud06c\ub77c\ucf04\uc740 \ub51c \ub123\uae30 \uad49\uc7a5\ud788 \ud3b8\ud55c \uad6c\ub3c4\uc5d0\ub9cc \uc62c\ub824\uc900\ub2e4\uace0 \ubcf4\uc2dc\uba74 \ub418\uc694. \ud611\uace1\uc5d0\uc120 \ub3cc\ud48d \uac00\ub294 \uc0ac\ub78c\ub3c4 \uc788\uc9c0\ub9cc \uce7c\ubc14\ub78c\uc5d0\uc120 \uadf8\ub2e5 \ucd94\ucc9c\ud558\uc9c4 \uc54a\uc2b5\ub2c8\ub2e4.\n \n2\ucf54\uc5b4\ub294 \ub9c8\ucd5c \uc62c\ub838\uc73c\uba74 \ucca0\uac11\uad81, \ucca0\uac11\uad81 \uc62c\ub838\ub294\ub370 \ub3c4\ubbf8\ub2c9 \ube68\ub9ac \ubf51\uc744 \ud544\uc694\uc5c6\uc73c\uba74 \uc9d5\uc218\uc758\ucd1d, \uc0c1\ub300 \uc55e\ub77c\uc778\uc774 \ubc29\uc5b4\ub825 \ub54c\ubb38\uc5d0 \uc798 \uc548 \ub6ab\ub9ac\uba74 \ub3c4\ubbf8\ub2c9\uacbd\uc758\uc778\uc0ac \ubf51\uc73c\uc2dc\uba74 \ub429\ub2c8\ub2e4.\n \n\uc544\ud3a0\ub9ac\uc624\uc2a4\n\ud611\uace1\uc5d0\uc11c\uc758 \ucd94\uc138\n\ud611\uace1 \uc544\ud3a0\ub9ac\uc624\uc2a4\ub294 \uc694\uc0c8\ub294 2\ucf54\uc5b4\ub85c \uc9d5\uc218\uc758\ucd1d\uc744 \uc11e\uc5b4\uc8fc\uba74\uc11c \uac11\ub2c8\ub2e4.\n \n\uce7c\ubc14\ub78c \uc544\ud3a0\ub9ac\uc624\uc2a4\ub3c4 \ucca0\uac11\uad81 \uc9d5\uc218\uc758\ucd1d \uac00\ub294 \ube4c\ub4dc\ub3c4 \uc88b\uace0, \uc5ec\uc804\ud788 \ucca0\uac11\uad81 \ub8e8\ub09c \uac00\ub294 \ube4c\ub4dc\ub3c4 \uc88b\uc2b5\ub2c8\ub2e4.\n\ud2b9\ud788, \uc544\ud3a0\ub9ac\uc624\uc2a4\ub294 \uc6d0\ub51c\uc784\uc5d0\ub3c4 \ucc54\ud53c\uc5b8 \ubc38\ub7f0\uc2a4 \ud6a8\uacfc\uc5d0\uc11c \ub108\ud504 \uba39\ub294 \ubd80\ubd84\uc774 \uc5c6\uc5b4\uc11c \ucca0\uac11\uad81\uc744 \uac00\ub3c4 \ub51c\uc774 \uc798 \ub098\uc640\uc11c \uc88b\uc2b5\ub2c8\ub2e4.\n \n\ucc54\ud53c\uc5b8 \ubc38\ub7f0\uc2a4 \ud6a8\uacfc\n\uc5c6\uc74c\n \n\uce7c\ubc14\ub78c \ucd94\ucc9c \ub8ec \n\uce58\uba85\uc801\uc18d\ub3c4 \uacfc\ub2e4\uce58\uc720 \ubbfc\ucca9\ud568 \ucd5c\ud6c4\uc758\uc77c\uaca9 + \uc0ac\ub0e5\uc758\uc99d\ud45c(\ud53c\uc758\ub9db) \uad76\uc8fc\ub9b0\uc0ac\ub0e5\uafbc\n \n\uce7c\ubc14\ub78c \ucd94\ucc9c \uc544\uc774\ud15c\n\uc218\ud638\uc790\uc758\ub9dd\uce58+\uc5ec\uc2e0\uc758\ub208\ubb3c \ubd88\uba78\uc758\ucca0\uac11\uad81 \uc9d5\uc218\uc758\ucd1d(\ub8e8\ub09c\uc758\ud5c8\ub9ac\ucf00\uc778) \ubb34\ud55c\uc758\ub300\uac80/\ub3c4\ubbf8\ub2c9\uacbd\uc758\uc778\uc0ac\n \n\uc815\ubcf5\uc790 \uc678\uc5d0 \uce58\uba85\uc801\uc18d\ub3c4\ub098 \uc9d1\uc911\uacf5\uaca9\ub8ec\ub3c4 \ubcf8\uc778 \ucde8\ud5a5\uc5d0 \ub530\ub77c\uc11c \ub4e4\uc5b4\uc918\ub3c4 \ub429\ub2c8\ub2e4.\n \n\uc2dc\uc791 \uc544\uc774\ud15c\uc740 2\uac00\uc9c0\uc785\ub2c8\ub2e4. \uce68\ucc29 \ucc0d\uace0 \uc218\ud638\uc790\uc758\ub9dd\uce58 \uc2e0\ubc1c/\ub2e8\uac80 \ucda9\uc804\ud615\ubb3c\uc57d \uc2dc\uc791\ud558\uac70\ub098 \uacfc\ub2e4\uce58\uc720 \ucc0d\uace0 \uc218\ud638\uc790\uc758\ub9dd\uce58 \uc5ec\uc2e0\uc758\ub208\ubb3c \ubb3c\uc57d \uc2dc\uc791\ud558\uac70\ub098 \uc785\ub2c8\ub2e4. \ucde8\ud5a5\ucc28\uc774\ub2c8\uae50 \ubcf8\uc778\uc774 \uc120\ud0dd\ud558\uc2dc\uba74 \ub429\ub2c8\ub2e4.\n \n1\ucf54\uc5b4 \uc2e0\ud654\ud15c\uc740 \ubd88\uba78\uc758\ucca0\uac11\uad81\uc744 \uac00\uc90d\ub2c8\ub2e4. \ucd5c\uadfc\uc5d0\ub294 \ucca0\uac11\uad81 \uac00\ub294\uac8c \ub354 \ub300\uc138\uc785\ub2c8\ub2e4.\n \n2\ucf54\uc5b4 \uc801 \uadfc\uc811 \ucc54\ud504 \ub9ce\uac70\ub098 \ub77c\uc778\ud074\ub9ac\uc5b4\uac00 \ud544\uc694\ud558\uba74 \ub8e8\ub09c\uc758\ud5c8\ub9ac\ucf00\uc778, \uadf8\uac8c \uc544\ub2c8\uba74 \uc77c\ubc18\uc801\uc73c\ub85c \uc9d5\uc218\uc758\ucd1d \uc0ac\uc8fc\uc2dc\uba74 \ub429\ub2c8\ub2e4. \n \n3\ucf54\uc5b4\ub294 \uae30\ubcf8\uc801\uc73c\ub85c \ubb34\ud55c\uc758\ub300\uac80\uc744 \uac00\uc8fc\uc2dc\uba74 \ub418\uace0\uc694. \uc801 \ud0f1\ucee4\uac00 \ub9ce\uc544\uc11c \ubc29\uc5b4\ub825\uc774 \ub192\uc544\uc11c \uc798 \uc548\uc7a1\ud788\ub294 \uacbd\uc6b0\uc5d0\ub294 \ubb34\ud55c\uc758\ub300\uac80\ubcf4\ub2e4 \ub3c4\ubbf8\ub2c9\uc744 \uba3c\uc800 \ubf51\uc544\uc8fc\uc154\ub3c4 \ub429\ub2c8\ub2e4.\n \n4\ucf54\uc5b4\ubd80\ud130\ub294 \uc0c1\ud669\uc5d0 \ub9de\uac8c \uace8\ub77c\uc11c \uac00\uc8fc\uc2dc\uba74 \ub429\ub2c8\ub2e4.\n \n \n\uc560\uc26c\n\ud611\uace1\uc5d0\uc11c\uc758 \ucd94\uc138\n\ud611\uace1 \uc560\uc26c\ub294 \ubd88\uba78\uc758\ucca0\uac11\uad81 \ub9c8\ubc95\uc0ac\uc758\ucd5c\ud6c4 \uad6c\uc778\uc218\uc758\uaca9\ub178\uac80 \ube4c\ub4dc\uac00 \ub728\uace0 \ud604\uc7ac \ub300\ud68c\uc5d0\uc11c\ub3c4 \ub098\uc624\ub294 1\ud2f0\uc5b4\uc785\ub2c8\ub2e4.\n \n\uce7c\ubc14\ub78c \uc560\uc26c\ub294 \uc785\ud788\ub294 \ud53c\ud574\ub7c9 10% \uac10\uc18c\uac00 \ubd99\uc5b4 \uc788\uae30 \ub54c\ubb38\uc5d0 \ucca0\uac11\uad81\uc740 \uac00\uc120 \uc548\ub418\ub294 \uc544\uc774\ud15c\uc774\ub77c \ud06c\ub77c\ucf04\uc744 \uac00\uc57c \ud569\ub2c8\ub2e4.\n \n\ucc54\ud53c\uc5b8 \ubc38\ub7f0\uc2a4 \ud6a8\uacfc\n\uc785\ud788\ub294 \ud53c\ud574\ub7c9 \uac10\uc18c 10%\n\ubc1b\ub294 \ud53c\ud574\ub7c9 \uc99d\uac00 5%\n \n\ud3ec\ud0b9 \uc560\uc26c\n\uce7c\ubc14\ub78c \ucd94\ucc9c \ub8ec\n\uc2e0\ube44\ub85c\uc6b4\uc720\uc131 \ub9c8\ub098\uc21c\ud658\ud314\ucc0c \uae68\ub2ec\uc74c \uc8fc\ubb38\uc791\uc5f4(\ud3ed\ud48d\uc758\uacb0\uc9d1) + \ud53c\uc758\ub9db \uad81\uadf9\uc758\uc0ac\ub0e5\uafbc\n\uc5b4\ub460\uc758\uc218\ud655 \ud53c\uc758\ub9db \uc0ac\ub0e5\uc758\uc99d\ud45c \uad81\uadf9\uc758\uc0ac\ub0e5\uafbc + \uce68\ucc29 \uccb4\ub825\ucc28\uadf9\ubcf5\n \n\uce7c\ubc14\ub78c \ucd94\ucc9c \uc544\uc774\ud15c\n\uc218\ud638\uc790\uc758\uac80+\uc5ec\uc2e0\uc758\ub208\ubb3c \uc81c\uad6d\uc758\uba85\ub839 \ub9c8\ub098\ubb34\ub124 \uc138\ub9b4\ub2e4\uc758\uc6d0\ud55c \uad76\uc8fc\ub9b0\ud788\ub4dc\ub77c/\ud654\uacf5\ud391\ud06c\uc0ac\uc2ac\uac80/\uce60\ud751\uc758\uc591\ub0a0\ub3c4\ub07c\n\uc218\ud638\uc790\uc758\uac80+\uc5ec\uc2e0\uc758\ub208\ubb3c \ub9c8\ub098\ubb34\ub124 \ub4dc\ub77d\uc0ac\ub974 \uc138\ub9b4\ub2e4\uc758\uc6d0\ud55c \uad76\uc8fc\ub9b0\ud788\ub4dc\ub77c/\ud654\uacf5\ud391\ud06c\uc0ac\uc2ac\uac80/\uce60\ud751\uc758\uc591\ub0a0\ub3c4\ub07c\n \n\uad81\uadf9\uc758\uc0ac\ub0e5\uafbc\uacfc \uad76\uc8fc\ub9b0\uc0ac\ub0e5\uafbc\uc740 \ucde8\ud5a5\uc785\ub2c8\ub2e4. \uad81\uc0ac\ub97c \ucc0d\uc73c\uba74 \uad81\ucfe8\uc774 \ube68\ub77c\uc11c \uc88b\uace0, \uad76\uc0ac\ub97c \ucc0d\uc73c\uba74 \uc720\uc9c0\ub825\uc774 \uc88b\uc544\uc9d1\ub2c8\ub2e4.\n \n\uc218\ud638\uc790\uc758\uac80 \uc2a4\ud0c0\ud2b8 \uc544\uc8fc \uc88b\uc2b5\ub2c8\ub2e4. \ucd08\ubc18\uc5d0 \ud6a8\uc728 \uc88b\uc740 \ub2a5\ub825\uce58\uac00 \ub2e4 \ubd99\uc5b4 \uc788\uc2b5\ub2c8\ub2e4. \n \n\uc8fc\uc758\ud560 \uc810\uc740 \uc81c\uad6d\uc758\uba85\ub839 \uc0b4 \ub54c \ud558\uc704\ud15c\uc73c\ub85c \ubc34\ub4e4\uc720\ub9ac\uac70\uc6b8\ubd80\ud130 \uc0ac\uc9c0\ub9c8\uc2dc\uace0 \uc810\ud654\uc11d\ubd80\ud130 \uc0ac\uc2dc\uae38 \ubc14\ub78d\ub2c8\ub2e4.\n\ubc34\ub4e4\uc720\ub9ac\uac70\uc6b8 \ubd99\uc5b4 \uc788\ub294 \uc8fc\ubb38\ub825\uc740 \uc560\uc26c\uc5d0\uac8c \uc544\ubb34 \uc4f0\uc798\ub370\uae30 \uc5c6\uc2b5\ub2c8\ub2e4. \uc560\uc26c\ub294 \uad81\uadf9\uae30\uc5d0\ub9cc AP\uacc4\uc218 1\uc774 \ubd99\uc5b4 \uc788\uae30 \ub54c\ubb38\uc5d0 \uc8fc\ubb38\ub825\uc740 \ud544\uc694\uc5c6\uace0, \uad81\uadf9\uae30\ub9c8\uc800\ub3c4 6\ub819\uc804\uae4c\uc9c0 \uc788\uc9c0\ub3c4 \uc54a\uace0 \ud55c\ubc88 \uc4f4\ub2e4\ud574\ub3c4 \ucd08\ubc18\uc5d0\ub294 \uad81\ucfe8\uc774 \uae38\uae30 \ub54c\ubb38\uc5d0 \ubb34\uc4f8\ubaa8\uc785\ub2c8\ub2e4. \uc810\ud654\uc11d\uc0ac\uc11c \uccb4\ub825200\uc774\ub77c\ub3c4 \ucc59\uae30\uc138\uc694.\n \n\uadf8\ub9ac\uace0 \uc81c\uad6d \uac19\uc774 \ud130\ud2b8\ub824\uc904 \ucc54\ud504\uac00 \uc5c6\ub294 \uacbd\uc6b0\uc5d0\ub294 \uc218\ud638\uc790\uc758\uac80 \uc2dc\uc791\ud574\uc11c \ub9c8\ub098\ubb34\ub124 \uba3c\uc800 \uc62c\ub9ac\uace0 \uadf8 \ub4a4\uc5d0 \uc81c\uad6d \uc548\uac00\uace0 \ub4dc\ub77d\uc0ac\ub974 \uc62c\ub9ac\ub294 \uac83\uc774 \ub354 \uc88b\ub2e4\uace0 \uc0dd\uac01\ud569\ub2c8\ub2e4.\n \n\uce58\uba85\ud0c0 \uc560\uc26c\n\uce7c\ubc14\ub78c \ucd94\ucc9c \ub8ec\n\uce58\uba85\uc801\uc18d\ub3c4 \uce68\ucc29 \ubbfc\ucca9\ud568(\ud54f\ube5b\uae38) \ucd5c\ud6c4\uc758\uc77c\uaca9 + \ube44\uc5f4\ud55c\ud55c\ubc29 \uad76\uc8fc\ub9b0\uc0ac\ub0e5\uafbc\n\uce58\uba85\uc801\uc18d\ub3c4 \uce68\ucc29 \ud54f\ube5b\uae38 \ucd5c\ud6c4\uc758\uc77c\uaca9 + \ub9c8\ubc95\uc758\uc2e0\ubc1c \ucf8c\uc18d\uc811\uadfc\n\uce7c\ub0a0\ube44 \ube44\uc5f4\ud55c\ud55c\ubc29 \uc0ac\ub0e5\uc758\uc99d\ud45c \uad76\uc8fc\ub9b0\uc0ac\ub0e5\uafbc + \uce68\ucc29 \ubbfc\ucca9\ud568\n \n\uce7c\ubc14\ub78c \ucd94\ucc9c \uc544\uc774\ud15c\n\uc218\ud638\uc790\uc758\ub9dd\uce58 \ubd88\uba78\uc758\ucca0\uac11\uad81 \uad6c\uc778\uc218\uc758\uaca9\ub178\uac80 \ubab0\ub77d\ud55c\uc655\uc758\uac80 \ub3c4\ubbf8\ub2c9\uacbd\uc758\uc778\uc0ac\n \n\ubcf4\uc870\ub8ec \uad76\uc8fc\ub9b0\uc0ac\ub0e5\uafbc \ucc0d\uc744\uac70\uba74 \ubbfc\ucca9\ud568\uc744 \ucc0d\uc5b4\uc8fc\uc2dc\uace0, \ucf8c\uc18d\uc811\uadfc \ucc0d\uc73c\uc2e4\uac70\ub77c\uba74 \uaf2d \ud54f\ube5b\uae38\uc744 \ucc0d\uc5b4\uc8fc\uc138\uc694.\n \n\uac1c\uc778\uc801\uc73c\ub85c \uc560\uc26c\uc774\ub4e0 \uc2dc\ube44\ub974\uc774\ub4e0 \uc694\uc0c8 \ud3ec\ud0b9\uc6d0\ub51c\ub85c \ubc14\ub010 \ucc54\ud504\ub4e4\ub3c4 \ud3c9\ud0c0\ud615\uc73c\ub85c \uc4f0\ub294 \uac83\uc744 \uc88b\uc544\ud558\ub294 \ud3b8\uc774\ub77c \uad49\uc7a5\ud788 \uc5f0\uad6c\ud558\ub294 \ud3b8\uc778\ub370\uc694. (\ubb3c\ub860, \uc131\ub2a5\uc740 \ud3ec\ud0b9\uc774 \ud6e8\uc52c \uc88b\ub2e4\uace0 \uc0dd\uac01\ud569\ub2c8\ub2e4.)\n \n\uadf8\ub3d9\uc548 \uc560\uc26c\ub294 \uc785\ud788\ub294\ud53c\ud574\ub7c9 \ub108\ud504\uac00 \ub108\ubb34 \uc2ec\ud558\uae30 \ub54c\ubb38\uc5d0 \ubb34\uc870\uac74 \ud06c\ub77c\ucf04\ud559\uc0b4\uc790 \uac04\ub2e4! \uadf8\ub798\uc11c \ub51c\uc744 \ucd5c\ub300\ud55c \ub9e4\uafbc\ub2e4 \uc774\ub807\uac8c \uc0dd\uac01\ud558\uace0 \uc788\uc5c8\ub294\ub370 \uc694\uc0c8 \ub2e4\uc774\uc560\ub098 \ube44\uc5d0\uace0 \uce74\ubc00 \uc774\ub7f0 \uc560\ub4e4 \ud2b9\ud788 \ub2e4\uc774\uc560\ub098\ub97c \ub108\ubb34 \uc0c1\ub300\ud558\uae30\uac00 \ud798\ub4e4\uc5b4\uc11c \ubbfc\ucca9\ud568 + \ucca0\uac11\uad81 \uc62c\ub9b0 \ub4a4\uc5d0 \uad6c\uc778\uc218 \ubab0\ub77d\uc758\uac80 \ud2b8\ub9ac\ub97c \uc120\ud638\ud558\uace0 \uc788\uc2b5\ub2c8\ub2e4. \uc774\ub7f0 \uacbd\uc6b0 \uc0c1\ub300 AP\uac00 \uc388 \uacbd\uc6b0 \ub9c8\ucd5c\ub97c \uc911\uac04\uc5d0 \uaef4\ub123\uae30\ub3c4 \uc88b\uc544\uc11c \ucca0\uac11\uad81 \uc274\ub4dc + \ub9c8\ucd5c \ub9c8\uc800\ud558\uba74 \uc0dd\uc874\ub825\ub3c4 \uc62c\ub77c\uac00\uc11c \uad1c\ucc2e\ub354\ub77c\uace0\uc694. \uc624\ud788\ub824 \uc0dd\uc874\ub825\uc774 \uc62c\ub77c\uac00\uc11c \ub51c \ub123\uae30 \ud3b8\ud574\uc9c0\uace0 \uc911\ud6c4\ubc18\uc5d0\ub294 \uc57d\ud574\ube60\uc9c4 \uc560\uc26c \ub51c\uc744 \uad6c\uc778\uc218 \ubab0\ub77d\uc73c\ub85c \uac09\uc544\uba39\ub294\ub2e4\ub294 \ub290\ub08c\uc73c\ub85c \ud558\uace0 \uc788\uc2b5\ub2c8\ub2e4.\n \n1\ucf54\uc5b4 \ubd88\uba78\uc758\ucca0\uac11\uad81 \uac11\ub2c8\ub2e4.\n \ncf. \ub9c8\ubc95\uc0ac\uc758\ucd5c\ud6c4\ub294 2\ucf54\uc5b4\ubd80\ud130 \ud544\uc694\ud55c \uacbd\uc6b0\uc5d0 \uc720\ub3d9\uc801\uc73c\ub85c \uad6c\ub9e4\ud574\uc8fc\uc2dc\uba74 \ub429\ub2c8\ub2e4.\n \n2\ucf54\uc5b4 \uad6c\uc778\uc218\uc758 \uaca9\ub178\uac80 \uc0ac\uc90d\ub2c8\ub2e4.\n \n3\ucf54\uc5b4 \ubab0\ub77d\ud55c\uc655\uc758\uac80, \ub9c8\ubc95\uc0ac\uc758\ucd5c\ud6c4, \ub3c4\ubbf8\ub2c9\uacbd\uc758\uc778\uc0ac, \ud53c\ubc14\ub77c\uae30, \ub8e8\ub09c\uc758\ud5c8\ub9ac\ucf00\uc778 \ub4f1 \uc790\uc720\ub86d\uac8c \uad6c\ub9e4\ud574\uc8fc\uc138\uc694.\n \n \n\uc774\uc988\ub9ac\uc5bc\n\ud611\uace1\uc5d0\uc11c\uc758 \ucd94\uc138\n\ud611\uace1 \uc774\uc988\ub9ac\uc5bc\uc740 \uc2e0\uc131\ud55c\ud30c\uad34\uc790\ub97c \uc790\uc8fc \uac00\ub2e4\uac00 \ub108\ud504 \ub41c \uc774\ud6c4\ub85c\ub294 \uc2e0\ud30c\uc790/\uc815\uc218/\uc0bc\uc704\uc77c\uccb4 \ubaa8\ub450 \ub9ce\uc774 \uc0ac\uc6a9\ub429\ub2c8\ub2e4. \uc5ec\uc804\ud788 \uc2e0\ud30c\uc790\uac00 \uac00\uc7a5 \ub9ce\uc774 \ud65c\uc6a9\ub418\uae34 \ud558\uc9c0\ub9cc\uc694.\n \n* \uce7c\ubc14\ub78c \uc774\uc988\ub9ac\uc5bc \uac1c\ub150\n\ud611\uace1\ub9cc \ud558\ub358 \uc720\uc800\ub4e4\uc774 \ub300\ubd80\ubd84 \uce7c\ubc14\ub78c\uc5d0\uc11c \uc774\uc988\ub97c \uc7a1\uac8c \ub418\uba74 \ud68c\ubcf5 + \ubcf4\uc870\ub8ec \ub300\ubd80\ubd84 \ub9c8\ubc95(or\uc601\uac10) + \uad11\ud718\uc758\uac80 \uc5ec\uc2e0\uc758\ub208\ubb3c \uc2dc\uc791\uc744 \uc790\uc5f0\uc2a4\ub808 \ud558\uac8c \ub429\ub2c8\ub2e4. \uc65c\ub0d0\ud558\uba74 \ud611\uace1\uc5d0\uc120 \uc6d0\ub51c \ud68c\ubcf5\uc774 \uae30\ubcf8\uc774\uace0 \ubcf4\uc870\ub8ec\uc5d0 \ub9c8\ubc95(\uae68\ub2ec\uc74c+\ub9c8\ub098\uc21c\ud658\ud314\ucc0c)\uc774\ub098 \uc601\uac10(\uc6b0\uc8fc\uc801\ud1b5\ucc30\ub825+\ube44\uc2a4\ud0b7)\uc744 \uc790\uc8fc \uc0ac\uc6a9\ud558\ub2c8\uae4c\uc694. \uadf8\ub9ac\uace0 \uace8\ub4dc\uc57c 1400\uc73c\ub85c \uc2dc\uc791\ud558\ub2c8 \uc5b4? \uc774\uac70 \uc774\uc988\uc758 \ud575\uc2ec\ud15c\uc778 \uad11\ud718\uc758\uac80\uacfc \uc5ec\uc2e0\uc758\ub208\ubb3c\uc744 \ubc14\ub85c \uc0b4 \uc218 \uc788\ub124? \ub77c\uace0 \uc0dd\uac01\uc774 \ub4dc\ub2c8\uae4c\uc694.\n \n\ud558\uc9c0\ub9cc \uce7c\ubc14\ub78c\uc5d0\uc11c \uc6d0\ub51c\uc740 \ud611\uace1\ubcf4\ub2e4 \ub9ce\uc740 \uc591\uc758 \ud53c\ud761\uacfc \uc720\uc9c0\ub825\uc744 \ud544\uc694\ub85c \ud569\ub2c8\ub2e4. \uc65c\ub0d0\ud558\uba74 \uc9d1\uc744 \ubabb\uac00\ub2c8\uae4c\uc694. \uadf8\ub807\uae30 \ub54c\ubb38\uc5d0 \uad76\uc8fc\ub9b0\uc0ac\ub0e5\uafbc\uc744 \ucc0d\uc5b4\uc904 \uc218 \uc788\ub294 \ubcf4\uc870\ub8ec \uc9c0\ubc30(\uc0ac\ub0e5\uc758\uc99d\ud45c \uad76\uc8fc\ub9b0\uc0ac\ub0e5\uafbc)\uc744 \ucc0d\uc5b4\uc8fc\ub294\uac8c \uc88b\uc2b5\ub2c8\ub2e4. \ub610\ud55c, 5:5\uc778 \ub9f5\uc5d0\uc11c \ud55c\ubc29\uc5d0 \ud130\uc9c0\ub294\uac78 \ubc29\uc9c0\ud558\uae30 \uc704\ud574\uc11c \uc815\ud654\ub97c \ub4dc\ub294\uac8c \uae30\ubcf8\uc785\ub2c8\ub2e4. \ud68c\ubcf5\uc740 \ubc14\ud140\uc5d0\uc11c 2:2\ud560 \ub54c\ub098 \uc4f0\ub294 \uac83\uc785\ub2c8\ub2e4. \uadf8\ub9ac\uace0 \uc2dc\uc791 \uc544\uc774\ud15c\ub3c4 \uad11\ud718\uc758\uac80 + \uc5ec\uc2e0\uc758\ub208\ubb3c\uc774 \uc544\ub2cc \uc218\ud638\uc790\uc758\ub9dd\uce58 + \uc5ec\uc2e0\uc758\ub208\ubb3c\uc744 \uc0ac\uac8c \ub418\uba74 \ub208\uc5d0 \ubcf4\uc77c\uc815\ub3c4\ub85c \uc720\uc9c0\ub825\uc774 \uc88b\uc544\uc9d1\ub2c8\ub2e4. \ubbf8\ub2c8\uc5b8\uc5d0 Q\ud55c\ubc29\uc529 \ub9de\ucd9c \ub54c\ub9c8\ub2e4 \uccb4\ub825 \ucc28\ub294\uac8c \ubcf4\uc774\uc8e0. \n \n\uadf8\ub798\uc11c \ud611\uace1 \uc774\uc988 \uc2a4\ud0c0\uc77c(\ud68c\ubcf5 + \ubcf4\uc870\ub8ec \ub9c8\ubc95 + \uad11\ud718\uc758\uac80 \uc5ec\uc2e0\uc758\ub208\ubb3c)\uacfc \uce7c\ubc14\ub78c \uc774\uc988 \uc2a4\ud0c0\uc77c(\uc815\ud654 + \ubcf4\uc870\ub8ec \uc9c0\ubc30 + \uc218\ud638\uc790\uc758\ub9dd\uce58 \uc5ec\uc2e0\uc758\ub208\ubb3c)\uc744 \ub9cc\ub098\uac8c \ub418\uba74 \uce7c\ubc14\ub78c \uc774\ud574\ub3c4 \uc790\uccb4\uac00 \ub2e4\ub974\uae30 \ub54c\ubb38\uc5d0 \uc2e4\ub825\ucc28\uc774\uac00 \uad49\uc7a5\ud788 \ub0a9\ub2c8\ub2e4.\n \n\ucc54\ud53c\uc5b8 \ubc38\ub7f0\uc2a4 \ud6a8\uacfc\n\uc785\ud788\ub294 \ud53c\ud574\ub7c9 \uac10\uc18c 5%\n\ubc1b\ub294 \ud53c\ud574\ub7c9 \uc99d\uac00 3%\n \n\uce7c\ubc14\ub78c \ucd94\ucc9c \ub8ec\n\uc815\ubcf5\uc790 \uacfc\ub2e4\uce58\uc720 \ud54f\ube5b\uae38 \ucd5c\ud6c4\uc758\uc77c\uaca9  + \uc0ac\ub0e5\uc758\uc99d\ud45c \uad76\uc8fc\ub9b0\uc0ac\ub0e5\uafbc\n \n\uce7c\ubc14\ub78c \ucd94\ucc9c \uc544\uc774\ud15c\n\uc218\ud638\uc790\uc758\ub9dd\uce58+\uc5ec\uc2e0\uc758\ub208\ubb3c \uc0bc\uc704\uc77c\uccb4/\uc2e0\uc131\ud55c\ud30c\uad34\uc790 \ub9c8\ub098\ubb34\ub124 \uc138\ub9b4\ub2e4\uc758\uc6d0\ud55c/\uc5bc\uc5b4\ubd99\uc740\uc2ec\uc7a5/\ub9ec\ubaa8\uc154\uc2a4\uc758\uc544\uadc0/\uad76\uc8fc\ub9b0\ud788\ub4dc\ub77c\n\uc218\ud638\uc790\uc758\ub9dd\uce58+\uc5ec\uc2e0\uc758\ub208\ubb3c \uc815\uc218\uc57d\ud0c8\uc790 (\ub4dc\ub77d\uc0ac\ub974) \ub9c8\ub098\ubb34\ub124 \uc138\ub9b4\ub2e4\uc758\uc6d0\ud55c/\uc5bc\uc5b4\ubd99\uc740\uc2ec\uc7a5/\ub9ec\ubaa8\uc154\uc2a4\uc758\uc544\uadc0/\uad76\uc8fc\ub9b0\ud788\ub4dc\ub77c\n \n11.13\ud328\uce58 \ub54c \uc6d0\uac70\ub9ac \uc2e0\ud30c\uc790 \ub51c\uc774 12%\uc5d0\uc11c 9%\ub85c \ub108\ud504\ub418\uc5c8\uc2b5\ub2c8\ub2e4.\n \n\uacfc\ub2e4\uce58\uc720\uac00 \uc218\uce58\ub3c4 \uc798 \ub098\uc624\uace0 \ud574\uc11c \uc88b\ub2e4\uace0 \ubd05\ub2c8\ub2e4. \ub2e4\ub9cc,  \uacfc\ub2e4\uce58\uc720 \ucc0d\uc5c8\uc744 \ub54c \ub300\uce58\uad6c\ub3c4\uac00 \uc5c4\uccad \uc624\ub798\ub418\uba74 \ub9c8\ub098\uac00 \uc870\uae08 \ubd80\uc871\ud560 \uc21c \uc788\uc2b5\ub2c8\ub2e4. (cf. \uc815\uc218 \ud15c\ud2b8\ub9ac\ub294 \ub9c8\ub098\uac00 \ubd80\uc871\ud558\uc9c0 \uc54a\uc544\uc11c \uacfc\ub2e4\uce58\uc720 \ud544\uc218\uc785\ub2c8\ub2e4.)\n \n\uc815\ubcf5\uc790\uac00 \uc218\uce58\uc0c1\uc73c\ub85c \ud45c\uc2dc\ub418\ub294 \uac83\uc740 \uc801\uc9c0\ub9cc, \ubaa8\ub4e0 \ud45c\ubcf8\uc744 \ub2e4 \ub530\uc838\ubd10\ub3c4 \uc2b9\ub960\uc774 \uc88b\uc2b5\ub2c8\ub2e4.\n\uc800\ub294 \uc120\ud638\ud558\uc9c4 \uc54a\uc9c0\ub9cc \ucde8\ud5a5\uc5d0 \ub530\ub77c\uc11c \uce58\uba85\uc801\uc18d\ub3c4 \uc5b4\ub460\uc758\uc218\ud655 \uc9d1\uc911\uacf5\uaca9\ub3c4 \uc0ac\uc6a9\ud560 \uc218 \uc788\ub2e4\uace0 \uc0dd\uac01\ud569\ub2c8\ub2e4.\n\uac8c\uc784\uc5d0 \ud070 \uc9c0\uc7a5\uc744 \uc904 \uc815\ub3c4\ub85c \ucc28\uc774\ub09c\ub2e4\uace0\ub294 \uc0dd\uac01\uc9c0 \uc54a\uc2b5\ub2c8\ub2e4.\n \n\ud15c\ud2b8\ub9ac\ub294 \uc815\uc218\uc57d\ud0c8\uc790, \uc0bc\uc704\uc77c\uccb4(\ud2b8\ud3ec), \uc2e0\uc131\ud55c\ud30c\uad34\uc790 \ud15c\ud2b8\ub9ac 3\uac1c\ub85c \uac08\ub9bd\ub2c8\ub2e4.\n\uc2e0\uc131\ud55c\ud30c\uad34\uc790\ub294 \uce7c\ubc14\ub78c \uc9c0\ud45c\uba74\uc5d0\uc11c\ub294 \uc5ec\uc804\ud788 \uc88b\uc740 \ubaa8\uc2b5\uc744 \ubcf4\uc5ec\uc8fc\uae34 \ud558\uc9c0\ub9cc, \ub108\ud504\uac00 \ub9ce\uc774 \ub418\uc5b4\uc11c \ub2e8\ub2e8\ud558\uac8c \uac00\uac70\ub098 \uc0c1\ub300 \uccb4\ub825 \ub192\uc740 \ud0f1\ucee4 \ube44\uc911\uc774 \uc788\uc744 \ub54c\ub9cc \uac00\uc8fc\uba74 \ub420\uac70 \uac19\uace0\uc694.\n \n\uce7c\ubc14\ub78c \ud1b5\uacc4\uc0c1\uc73c\ub85c\ub294 \uc815\uc218\uc57d\ud0c8\uc790 \ub4dc\ub77d\uc0ac\ub974 \ube4c\ub4dc\uac00 \uc2b9\ub960\uc774 \ub5a8\uc5b4\uc9c0\uae34 \ud558\uc9c0\ub9cc, \ud611\uace1\uc5d0\uc11c \ud504\ub85c\uac8c\uc774\uba38 \ub4f1 \uc0c1\uc704\uad8c\uc5d0\uc11c\ub294 \uc815\uc218 \ub4dc\ub77d \ud15c\ud2b8\ub9ac\ub85c \ub3cc\uc544\uac00\ub294 \ubaa8\uc2b5\uc774 \ubcf4\uc774\uace0 \uc788\uc2b5\ub2c8\ub2e4. \uac00\uaca9\uc774 \uc800\ub834\ud574\uc11c \ucf54\uc5b4\ub97c \ube68\ub9b4 \uc218 \uc788\uace0 \uacfc\ub514\uce58\uc720\ub97c \ud655\uc815\uc801\uc73c\ub85c \ucc0d\uc744 \uc218 \uc788\ub294 \uc7a5\uc810\uc774 \uc788\uc2b5\ub2c8\ub2e4. \ub354\uc0e4\uc774\ub3c4 \uce7c\ubc14\ub78c \uc774\uc988\ub85c\ub294 \uc815\uc218 \ud15c\ud2b8\ub9ac\ub97c \uc120\ud638\ud558\uae30\ub3c4 \ud569\ub2c8\u314f\u3137.\n \n\uc815\uc218 \ud15c\ud2b8\ub9ac\uc5d0\uc11c \ub4dc\ub77d\uc0ac\ub974\ub294 \uc0dd\ub7b5\ud558\uc154\ub3c4 \ub429\ub2c8\ub2e4. \n \n\uc815\uc218 \ub4dc\ub77d, \uc0bc\uc704\uc77c\uccb4, \uc2e0\uc131\ud55c\ud30c\uad34\uc790 + \ub9c8\ub098\ubb34\ub124 \uc774\ud6c4\ub85c\ub294 \uc138\ub9b4\ub2e4, \uc5bc\uc5b4\ubd99\uc740\uc2ec\uc7a5, \ub9ec\ubaa8\uc154\uc2a4 3\uac1c\uac00 \uac00\uc7a5 \ubcf4\ud3b8\uc801\uc73c\ub85c \uc62c\ub9ac\ub294 \uc544\uc774\ud15c\uc785\ub2c8\ub2e4. \uad76\uc8fc\ub9b0\ud788\ub4dc\ub77c\ub3c4 \uc720\uc9c0\ub825\uc774 \ud544\uc694\ud55c \uacbd\uc6b0\uc5d0 \uc88b\uace0\uc694. \ubab0\ub77d\ud55c\uc655\uc758\uac80\uc740 \uac70\uc758 \uac08 \uc77c\uc774 \ub4dc\ubb3c\uc9c0\ub9cc, \ub0b4\uac00 \uc138\ub9b4\ub2e4\uae4c\uc9c0 \ub2e4 \uc62c\ub838\ub294\ub370\uc5d0\ub3c4 \uc801 \ud0f1\ucee4\uac00 \uccb4\ub825\uc774 \ub108\ubb34 \ub9ce\uc544\uc11c \ud0f1\ucee4\ub958\ub4e4\uc774 \uc548\uc7a1\ud790 \ub54c\ub9cc \ud55c\uc815\ud574\uc11c \uc0ac\uc8fc\uc138\uc694.\n \n \n\uc790\uc57c\n\ud611\uace1\uc5d0\uc11c\uc758 \ucd94\uc138\n\ud611\uace1 \uc790\uc57c\ub294 \ub4dc\ub77d\uc0ac\ub974 \ud15c\ud2b8\ub9ac\uac00 \uc7a0\uae50 \ub728\ub2e4\uac00 \ubc14\ub85c \uc0ac\uc7a5\ub418\uc5c8\uace0\uc694. \ub3cc\ud48d(\ud06c\ub77c\ucf04) \uc815\uc218 \ud15c\ud2b8\ub9ac\uac00 \uac00\uc7a5 \ubb34\ub09c\ud55c \uc120\ud0dd\uc785\ub2c8\ub2e4.\n \n\uce7c\ubc14\ub78c \uc790\uc57c\ub3c4 \ud611\uace1\uacfc \ube44\uc2b7\ud558\uac8c \uac00\uba74 \ub418\ub294\ub370 \uc0c1\ud669\uc5d0 \ub530\ub77c\uc11c \uc815\uc218 \ub300\uc2e0 \ub098\ubcf4\ub9ac\ub97c \uc62c\ub824\ub3c4 \uc88b\uc2b5\ub2c8\ub2e4.\n\uc800\ub294 \uac1c\uc778\uc801\uc73c\ub85c \uc694\uc0c8 \uc790\uc57c\ub85c \n \n\ucc54\ud53c\uc5b8 \ubc38\ub7f0\uc2a4 \ud6a8\uacfc\n\uc5c6\uc74c\n \n\uce7c\ubc14\ub78c \ucd94\ucc9c \ub8ec\n\uce58\uba85\uc801\uc18d\ub3c4 \uacfc\ub2e4\uce58\uc720 \ud54f\ube5b\uae38 \uccb4\ub825\ucc28\uadf9\ubcf5 + \uc0ac\ub0e5\uc758\uc99d\ud45c \uad76\uc8fc\ub9b0\uc0ac\ub0e5\uafbc(\uad81\uadf9\uc758\uc0ac\ub0e5\uafbc)\n \n\uce7c\ubc14\ub78c \ucd94\ucc9c \uc544\uc774\ud15c\n\uc218\ud638\uc790\uc758\ub9dd\uce58+\uc5ec\uc2e0\uc758\ub208\ubb3c \ud06c\ub77c\ucf04\ud559\uc0b4\uc790(\ub3cc\ud48d) \uc815\uc218\uc57d\ud0c8\uc790 \ub098\ubcf4\ub9ac\uc2e0\uc18d\uac80/\ubb34\ud55c\uc758\ub300\uac80/\ub3c4\ubbf8\ub2c9\uacbd\uc758\uc778\uc0ac\n \n11.15v\uc5d0\uc11c \uc790\uc57c \uad81\uadf9\uae30 \uc7ac\uc0ac\uc6a9\ub300\uae30\uac04\uc774 \uac10\uc18c\ub418\uc5c8\uc73c\ubbc0\ub85c \uc774\uc81c \uad81\uadf9\uc758\uc0ac\ub0e5\uafbc\uc744 \ud544\uc218\ub85c \ucc0d\uc9c0 \uc54a\uc544\ub3c4 \ub429\ub2c8\ub2e4.\n \n1\ucf54\uc5b4 \ud06c\ub77c\ucf04\uc774\ub098 \ub3cc\ud48d\uc73c\ub85c \uad6c\ub9e4\ud569\ub2c8\ub2e4. \uc800\ub294 \uce7c\ubc14\ub78c \ub3cc\ud48d\uc740 \uac00\uae09\uc801 \ucd94\ucc9c\ud558\uc9c4 \uc54a\ub294 \ud3b8\uc778\ub370 \uc790\uc57c\ub294 \uad49\uc7a5\ud788 \ubc1b\uc544\uce58\uae30\uc5d0\ub294 \uc88b\uc740 \ucc54\ud504\uc774\ub098 \uc218\ub3d9\uc801\uc778 \ucc54\ud504\ub77c \ub530\ub77c\uac00\ub294 \uad6c\ub3c4\uc5d0\uc11c\ub294 \uc548\uc88b\uae30 \ub54c\ubb38\uc5d0 \ub3cc\ud48d\uc774 \uc790\uc57c\uc5d0\uac8c\ub294 \ub098\uc058\uc9c0 \uc54a\uc2b5\ub2c8\ub2e4. \ubcf4\uc720 \uace8\ub4dc\uc5d0 \ub530\ub77c\uc11c \uc6d0\ud050\uc5d0 \uc815\uc218\uc57d\ud0c8\uc790 \ub098\uc624\uba74 \uc815\uc218\uc57d\ud0c8\uc790 1\ucf54\uc5b4\ub85c \ubf51\uc73c\uc154\ub3c4 \ub429\ub2c8\ub2e4.\n \n2\ucf54\uc5b4 \ubb34\ub09c\ud55c \uacbd\uc6b0\uc5d0\ub294 \uc815\uc218\uc57d\ud0c8\uc790 \uad6c\ub9e4\ud574\uc8fc\uc2dc\uba74 \ub429\ub2c8\ub2e4.\n\uc740\uadfc\ud788 2\ucf54\uc5b4\ub85c \uc9d5\uc218\uc758\ucd1d \uc0ac\uc2dc\ub294 \ubd84\ub4e4\uc774 \ub9ce\ub358\ub370 \uadf8\ub7ec\uc9c0 \ub9c8\uc138\uc694.\n \n3\ucf54\uc5b4 \uc0c1\ub300 \ud0f1\ucee4 \ubc29\uc5b4\ub825\uc774 \ub192\uc544\uc11c \uc548 \ub6ab\ub9ac\uaca0\ub2e4 \uc2f6\uc73c\uba74 \ub3c4\ubbf8\ub2c9\uacbd\uc758\uc778\uc0ac, \ub3c4\ubbf8\ub2c9 \uc62c\ub9b4 \ud544\uc694\uac00 \uc5c6\uace0 \uc0c1\ub300\uac00 \ub098\ub97c \ubb34\ub294\uac8c \ub9ce\uace0 \ud574\uc11c \uad81\ucfe8 \ub4f1\uc744 \uc904\uc5ec\uc57c \ud55c\ub2e4 \ub098\ubcf4\ub9ac\uc2e0\uc18d\uac80, \uc77c\ubc18\uc801\uc778 \uc0c1\ud669\uc774\ub2e4 \ubb34\ud55c\uc758\ub300\uac80 \uac00\uc8fc\uc2dc\uba74 \ub429\ub2c8\ub2e4.\n \n \n\uc9c4 (jhin, jin, zin, gin)\n\ud611\uace1\uc5d0\uc11c\uc758 \ucd94\uc138\n\ud611\uace1 \uc9c4\uc740 \ub3cc\ud48d \uc9d5\uc218\uc758\ucd1d (\uace0\uc18d\uc5f0\uc0ac\ud3ec) \ubb34\ud55c\uc758\ub300\uac80 \ud15c\ud2b8\ub9ac\uac00 \uc81c\uc77c \ub9ce\uc2b5\ub2c8\ub2e4.\n \n\ucc54\ud53c\uc5b8 \ubc38\ub7f0\uc2a4 \ud6a8\uacfc\n\uc785\ud788\ub294 \ud53c\ud574\ub7c9 \uac10\uc18c 10%\n \n\uce7c\ubc14\ub78c \ucd94\ucc9c \ub8ec\n\uae30\ubbfc\ud55c\ubc1c\ub180\ub9bc \uce68\ucc29 \ud54f\ube5b\uae38 \uccb4\ub825\ucc28\uadf9\ubcf5 + \uc0ac\ub0e5\uc758\uc99d\ud45c \uad76\uc8fc\ub9b0\uc0ac\ub0e5\uafbc\n\uc5b4\ub460\uc758\uc218\ud655 \ud53c\uc758\ub9db \uc0ac\ub0e5\uc758\uc99d\ud45c \uad76\uc8fc\ub9b0\uc0ac\ub0e5\uafbc + \uce68\ucc29 \ud54f\ube5b\uae38\n \n\uce7c\ubc14\ub78c \ucd94\ucc9c \uc544\uc774\ud15c\n\uc218\ud638\uc790\uc758\ub9dd\uce58 \ub3cc\ud48d \uc9d5\uc218\uc758\ucd1d \uace0\uc18d\uc5f0\uc0ac\ud3ec/\ubb34\ud55c\uc758\ub300\uac80/\ub3c4\ubbf8\ub2c9\uacbd\uc758\uc778\uc0ac\n \n11.17 \ud328\uce58 \ub54c \uae30\ubbfc\ud55c \ubc1c\ub180\ub9bc\uc774 \ucc54\ud53c\uc5b8\uc5d0\uac8c \ub54c\ub9b4 \ub54c\uc758 \ud68c\ubcf5\uc740 \ub298\uc5b4\ub098\uace0 \ubbf8\ub2c8\uc5b8\uc5d0\uac8c \ub54c\ub9b4 \ub54c\uc758 \ud68c\ubcf5\uc740 \uc904\uc5b4\ub4e4\uc5c8\uc2b5\ub2c8\ub2e4. \uc9c4\uc5d0\uac8c\ub294 \ubc84\ud504\ub77c\uace0 \uc0dd\uac01\ud569\ub2c8\ub2e4. \uc9c4\uc740 \ucc54\ud53c\uc5b8 \ub54c\ub9ac\uae30 \uc26c\uc6b4 \ud3b8\uc774\ub77c \uae30\ubc1c \uc218\uce58\uac00 \ub9ce\uc774 \ub298\uc5b4\ub0ac\ub354\ub77c\uace0\uc694.\n\ud0f1\uc7a1\uae30 \ubd88\ud3b8\ud55c \uc6d0\ub51c\uc5d0\uac8c \uccb4\ub825\ucc28\uadf9\ubcf5 \ucc0d\uc744 \uc218 \uc788\ub294 \ud6a8\uacfc\ub3c4 \uc88b\uace0\uc694.\n \n\uc9c4\uc740 \ub8ec \ud30c\ud3b8 \uccab\ubc88\uc9f8 \uacf5\uaca9\uc18d\ub3c4 \ucc0d\uc9c0 \ub9c8\uc2dc\uace0 \uc801\uc751\ud615\ub2a5\ub825\uce58 \ud639\uc740 \uc2a4\ud0ac\uac00\uc18d\ub97c \ucc0d\uc73c\uc138\uc694.\n \n\uacfc\ub2e4\uce58\uc720 \ucc0d\uace0 \uc2f6\uc73c\uc2e0 \ubd84\uc740 \uc218\ud638\uc790\uc758\ub9dd\uce58 \uc5ec\uc2e0\uc758\ub208\ubb3c \ubb3c\uc57d\uc73c\ub85c \uc2dc\uc791\ud558\uc168\ub2e4\uac00 \ud30c\uc154\ub3c4 \ub429\ub2c8\ub2e4.\n \n1\ucf54\uc5b4\ub294 \ub3cc\ud48d \ubb34\ub09c\ud569\ub2c8\ub2e4.\n \n2\ucf54\uc5b4 \uc9d5\uc218\uc758\ucd1d \ub3c4\ubbf8\ub2c9\uacbd\uc758\uc778\uc0ac \uace0\uc18d\uc5f0\uc0ac\ud3ec \uc911\uc5d0 \ud558\ub098 \uac00\uc8fc\uc2dc\uba74 \ub429\ub2c8\ub2e4. \n\ub300\uccb4\ub85c \uc9d5\uc218\uc758\ucd1d\uc774 \ubb34\ub09c\ud569\ub2c8\ub2e4. \ub3c4\ubbf8\ub2c9\uc740 \uc0c1\ub300 \ud0f1\ucee4\uac00 \ub9ce\uc740 \uacbd\uc6b0\uc5d0 \uc62c\ub824\uc90d\ub2c8\ub2e4. \n \n3\ucf54\uc5b4 \uace0\uc18d\uc5f0\uc0ac\ud3ec/\ubb34\ud55c\uc758\ub300\uac80/\ub3c4\ubbf8\ub2c9\uacbd\uc758\uc778\uc0ac\ub97c \uc0c1\ud669\uc5d0 \ub9de\uac8c \uc0ac\uc8fc\uc2dc\uba74 \ub429\ub2c8\ub2e4.\n \n \n\uc9d5\ud06c\uc2a4\n\ud611\uace1\uc5d0\uc11c\uc758 \ucd94\uc138\n\ud611\uace1 \uc9d5\ud06c\uc2a4\ub294 \ub3cc\ud48d \ub108\ud504 \uc774\ud6c4\uc5d0\ub294 \ud06c\ub77c\ucf04\uc744 \uc81c\uc77c \ub9ce\uc774 \uac00\uc90d\ub2c8\ub2e4. v11.3\uc5d0\uc11c \ub300\ud3ec \ud3fc(\ub85c\ucf13 \ub7f0\ucc98) \uc0ac\uac70\ub9ac\ub3c4 25 \ub298\uc5b4\uc11c \ubc84\ud504\ub418\uc5c8\uc2b5\ub2c8\ub2e4. \ub8ec\uc740 \uc815\ubcf5\uc790\uc640 \uce58\uba85\uc801\uc18d\ub3c4 \ud06c\uac8c 2\uac00\uc9c0\ub85c \uac08\ub9ac\ub294 \ud3b8\uc785\ub2c8\ub2e4. v11.3\uc5d0\uc11c \ub300\ud3ec \ud3fc(\ub85c\ucf13 \ub7f0\ucc98) \uacf5\uc18d \uac10\uc18c\ub7c9\uc774 \uc904\uc5b4\uc11c \ubc84\ud504\ub418\uc5c8\uae30 \ub54c\ubb38\uc5d0 \uce58\uba85\uc801\uc18d\ub3c4 \ub8ec\ub3c4 \uc5ec\uc804\ud788 \ub9ce\uc9c0\ub9cc, \uc815\ubcf5\uc790 \ube44\uc911\uc774 \ub354 \ub9ce\uc774 \uc62c\ub77c\uac14\uc2b5\ub2c8\ub2e4.\n \n\uce7c\ubc14\ub78c \uc9d5\ud06c\uc2a4\ub294 \uae30\ubbfc\ud55c\ubc1c\ub180\ub9bc\uc740 \ub4e4\uc5b4\uc904 \ud544\uc694 \uc5c6\uace0 \uce58\uba85\uc801\uc18d\ub3c4\ub098 \uc815\ubcf5\uc790 \ucde8\ud5a5\uc73c\ub85c \ub4e4\uba74 \ub429\ub2c8\ub2e4. \uce58\uba85\uc801\uc18d\ub3c4\uac00 \ub354 \ub9ce\uc774 \ubcf4\uc774\uace0 \ud3c9\uade0 \uc2b9\ub960\uc774 \ub192\uc740 \ud3b8\uc774\uc9c0\ub9cc, \ud611\uace1 \ud2f0\uc5b4\uac00 \ub192\uc740 \uc0ac\ub78c\uc77c\uc218\ub85d \uc815\ubcf5\uc790 \uc2b9\ub960\uc774 \uc62c\ub77c\uac11\ub2c8\ub2e4.\n \n\ucc54\ud53c\uc5b8 \ubc38\ub7f0\uc2a4 \ud6a8\uacfc\n\uc785\ud788\ub294 \ud53c\ud574\ub7c9 \uac10\uc18c 5%\n\ubc1b\ub294 \ud53c\ud574\ub7c9 \uc99d\uac00 5%\n \n\uce7c\ubc14\ub78c \ucd94\ucc9c \ub8ec\n\uce58\uba85\uc801\uc18d\ub3c4(\uc815\ubcf5\uc790) \uce68\ucc29 \ud54f\ube5b\uae38 \uccb4\ub825\ucc28\uadf9\ubcf5/\ucd5c\ud6c4\uc758\uc77c\uaca9 + \uc0ac\ub0e5\uc758\uc99d\ud45c(\ud53c\uc758\ub9db) \uad76\uc8fc\ub9b0\uc0ac\ub0e5\uafbc\n \n\uce7c\ubc14\ub78c \ucd94\ucc9c \uc544\uc774\ud15c\n\uc218\ud638\uc790\uc758\ub9dd\uce58+\uc5ec\uc2e0\uc758\ub208\ubb3c \ud06c\ub77c\ucf04\ud559\uc0b4\uc790 \uace0\uc18d\uc5f0\uc0ac\ud3ec/\ub8e8\ub09c\uc758\ud5c8\ub9ac\ucf00\uc778 \ubb34\ud55c\uc758\ub300\uac80/\ub3c4\ubbf8\ub2c8\uacbd\uc758\uc778\uc0ac\n \n11.5 \ud328\uce58\uc5d0\uc11c \uc815\ubcf5\uc790 \ub8e8\ub09c\uc5d0 \uc911\ucca9 \uc548\ub418\uac8c \ub2e4\uc2dc \uace0\uccd0\uc84c\uc2b5\ub2c8\ub2e4.\n \n\uc9d5\ud06c\uc2a4\ub294 \uc6d0\ub51c \uce58\uace0 \uccb4\ub825\uc774 \uaf64 \ub192\uc740 \uc6d0\ub51c\uc774\ub77c\uc11c \uc218\ud638\uc790\uc758\ub9dd\uce58\uae4c\uc9c0 \uc0ac\uac8c \ub418\uba74 \uccb4\ub825\ucc28\uadf9\ubcf5 \uc218\uce58\uac00 \uc5c4\uccad \uc798 \ub098\uc624\ub294 \ud3b8\uc740 \uc544\ub2c8\uc5d0\uc694. \ub9cc\uc57d, \ud53d\ucc3d\uc5d0\uc11c \uc6b0\ub9ac\ud300 \uc870\ud569\uc0c1 \uc0c1\ub300\ud300\uc5d0 \ucd08\uac00\uc2a4 \ubb38\ub3c4\ubc15\uc0ac \uc0ac\uc774\uc628\uac19\uc740 \uccb4\ub825 \ub192\uc740 \ud0f1\ucee4 \ub098\uc624\uba74 \ub2f5\uc774 \uc5c6\uaca0\ub2e4 \uc2f6\uc744 \ub54c \uccb4\ub825\ucc28\uadf9\ubcf5 \ucc0d\uace0 \ud558\uc2dc\uba74 \ub429\ub2c8\ub2e4.\n \n\uc218\ud638\uc790\uc758\ub9dd\uce58 \uc5ec\uc2e0\uc758\ub208\ubb3c \ubb3c\uc57d \uc2dc\uc791\ud574\uc11c \uc5ec\uc720\ub86d\uac8c \ub85c\ucf13\ub7f0\uccd0\ud3fc \uc4f0\ub2e4\uac00 \ub098\uc911\uc5d0 \ud314\uc544\uc8fc\uc138\uc694.\n\uc815\uc218\uc57d\ud0c8\uc790 \uc62c\ub9ac\ub294 \ucc54\ud53c\uc5b8\uc740 \uc544\ub2c8\ub77c\uc11c \uc5ec\uc2e0\uc758\ub208\ubb3c \uc2dc\uc791\ud588\uc5b4\ub3c4 \uacfc\ub2e4\uce58\uc720 \ucc0d\uc744\uac74 \uc544\ub2c8\uae30 \ub54c\ubb38\uc5d0 \uc5ec\uc2e0\uc758\ub208\ubb3c \uc0ac\uae30 \uc2eb\uc73c\uc2e0 \ubd84\uc740 \uc548\uc0ac\uc154\ub3c4 \ub429\ub2c8\ub2e4. \ubc14\ub85c \uc218\ud638\uc790\uc758\ub9dd\uce58 \uc2e0\ubc1c/\ub2e8\uac80 \ucda9\uc804\ud615\ubb3c\uc57d \uc0ac\uc8fc\uc138\uc694.\n \n1\ucf54\uc5b4 \uc2e0\ud654\ud15c\uc740 \ub300\ubd80\ubd84 \ud06c\ub77c\ucf04\ud559\uc0b4\uc790 \uc0ac\uc900\ub2e4\uace0 \ubcf4\uba74 \ub418\uace0\uc694. \ub3cc\ud48d\uc740 \uc801 \ub3cc\uc9c4\uae30 \uc788\ub294 \ucc54\ud504 \uc0c1\ub300\ub85c \ud55c\ud134 \ub4a4\ub85c \ube7c\uba74 \uc88b\uc740 \uc0c1\ud669\uc77c \ub54c \uc0ac\uc8fc\uba74 \ub429\ub2c8\ub2e4. \uc81c\uac00 \uc694\uc0c8 \uc6d0\ub51c\ub85c \ucca0\uac11\uad81\uc744 \ub9ce\uc774 \uc120\ud638\ud558\uac8c \ub418\uc5c8\ub294\ub370\ub3c4 \ubd88\uad6c\ud558\uace0 \uc9d5\ud06c\uc2a4\ub294 \ub300\ud3ec\ud3fc \uc0ac\uac70\ub9ac\uac00 \uc6cc\ub099 \uae38\uace0 \uc548\uc815\uc801\uc774\uae30 \ub54c\ubb38\uc5d0 \uac00\uae09\uc801 \ud06c\ub77c\ucf04 \uac00\ub294 \uac83\uc744 \ucd94\ucc9c\ud569\ub2c8\ub2e4.\n \n2\ucf54\uc5b4 \uc801 \ud314\uc774 \uae38\uc5b4\uc11c \ud55c\ub300 \ud1a1\ud1a1 \uce58\uace0 \uc624\uac70\ub098 \uc0ac\uac70\ub9ac\ub97c \uc8fc\uba74 \uc548\ub418\ub294 \uacbd\uc6b0\uc5d0 \uace0\uc18d\uc5f0\uc0ac\ud3ec \uc0ac\uc8fc\uc2dc\uba74 \uc88b\uace0. \uc801 \uc55e\ub77c\uc778\uc774 \ub9ce\uace0 \ub77c\uc778\ud074\ub9ac\uc5b4\uac00 \ub2f5\ub2f5\ud55c \uc870\ud569\uc774\ub77c\uba74 \ub8e8\ub09c\uc758\ud5c8\ub9ac\ucf00\uc778 \uc0ac\uc8fc\uc2dc\uba74 \ub429\ub2c8\ub2e4. \n \n3\ucf54\uc5b4\ub294 \uae30\ubcf8\uc801\uc73c\ub85c \ubb34\ud55c\uc758\ub300\uac80\uc744 \uac00\uc8fc\uc2dc\uba74 \ub418\uace0\uc694. \uc801 \ud0f1\ucee4\uac00 \ub9ce\uc544\uc11c \ubc29\uc5b4\ub825\uc774 \ub192\uc544\uc11c \uc798 \uc548\uc7a1\ud788\ub294 \uacbd\uc6b0\uc5d0\ub294 \ubb34\ud55c\uc758\ub300\uac80\ubcf4\ub2e4 \ub3c4\ubbf8\ub2c9\uc744 \uba3c\uc800 \ubf51\uc544\uc8fc\uc154\ub3c4 \ub429\ub2c8\ub2e4. \ub9cc\uc57d, \ub51c\uc740 \ucda9\ubd84\ud55c\ub370 \uc0dd\uc874\uc774 \ub108\ubb34 \ud798\ub4e0 \ud310(\ud2b9\ud788 \uc81c\ub4dc \uce74\uc9c1\uc2a4)\uc5d0\ub294 \ud53c\ubc14\ub77c\uae30 \uba3c\uc800 \uc0ac\uc8fc\uc154\ub3c4 \ub418\uace0\uc694.\n \n \n\uce74\uc774\uc0ac\n\ud611\uace1\uc5d0\uc11c\uc758 \ucd94\uc138\n\ud611\uace1 \uce74\uc774\uc0ac\ub294 \ud604\uc7ac \uc808\uc815\uc758\ud654\uc0b4\uacfc \ud1b1\ub0a0\ub2e8\uac80\uc744 \uad6c\ub9e4\ud574\uc8fc\uba74\uc11c Q\uc9c4\ud654\ub97c \ube60\ub974\uac8c \ud558\uace0 \uc2e0\ud654\ud15c(\ub3cc\ud48d/\ud06c\ub77c\ucf04) \uc62c\ub9ac\uace0 \uc9d5\uc218\uc758\ucd1d \uc644\uc131\ud558\uace0 \uc720\ub839\ubb34\ud76c \ubb34\ud55c\uc758\ub300\uac80 \uac00\ub294 \uac83\uc774 \ucd94\uc138\uc785\ub2c8\ub2e4.\n \n* \uce7c\ubc14\ub78c \uce74\uc774\uc0ac \uac1c\ub150\n\ud611\uace1\uc5d0\uc11c\uc758 \uce74\uc774\uc0ac\ub294 \ub3c4\ub780\uac80\uc774\ub098 \ub871\uc18c\ub4dc\ub85c \uc2dc\uc791\uc744\ud558\uace0, Q\uc9c4\ud654\ub97c \uc704\ud574\uc11c \ud1b1\ub0a0\ub2e8\uac80 + \uc808\uc815\uc758\ud654\uc0b4(\u2192 \ud06c\ub77c\ucf04/\ub3cc\ud48d)\uc744 \uc62c\ub9ac\uace0 \uc9d5\uc218\uc758\ucd1d\uae4c\uc9c0 \uc790\uc5f0\uc2a4\ub7fd\uac8c \uc0ac\uac8c \ub429\ub2c8\ub2e4. \ub300\ubd80\ubd84 \uce7c\ubc14\ub78c\uc758 \uad00\uc2ec\uc774 \uc5c6\ub294 \uc720\uc800\ub4e4\uc740 \uc774 \uac1c\ub150\uc744 \uadf8\ub300\ub85c \uce7c\ubc14\ub78c\uc5d0 \uac00\uc838\uc624\uac8c \ub429\ub2c8\ub2e4. \uadf8\ub798\uc11c \uc808\uc815\uc758\ud654\uc0b4\uc744 \uc2dc\uc791\ud558\uace0 1\ucf54\uc5b4 \ub9de\ucd98 \ud6c4\uc5d0 \ud1b1\ub0a0\ub2e8\uac80 \uc0ac\uc11c Q\uc9c4\ud654\ud574\uc8fc\uace0 \uc9d5\uc218\uc758\ucd1d\uae4c\uc9c0 \uc62c\ub9ac\uac8c \ub418\uac8c \ub418\uc8e0. \ubcf8\uc778 \uc785\uc7a5\uc5d0\uc120 \ud611\uace1\ub9cc \ud558\uba74 \ub2f9\uc5f0\ud55c \uc0dd\uac01\uc785\ub2c8\ub2e4.\n \n\ud558\uc9c0\ub9cc, \uce7c\ubc14\ub78c\uc5d0\uc120 \uc6d0\ub51c\uc758 \uc5ed\ud560\uc774 \ud0f1\ucee4\ub97c \uc7a1\ub294\ub370 \ub354 \uc8fc\ub825\uc774 \ub418\uc5b4 \uc788\uace0 \ud55c\ud0c0\ub9cc \ud558\uae30 \ub54c\ubb38\uc5d0 \uc9d5\uc218\uc758\ucd1d\ubcf4\ub2e4 \ub3c4\ubbf8\ub2c9\uc774\ub098 \ubb34\ud55c\uc758\ub300\uac80, \ud53c\ud761 \ub4f1\uc744 \ube68\ub9ac \ub561\uaca8\uc640\uc57c \ud569\ub2c8\ub2e4. \n \n\uc774\uac8c \uc0ac\uc2e4 \uc81c\uac00 \uc704\uc5d0 \uce7c\ubc14\ub78c \uc6d0\ub51c \uac1c\ub150\ucabd\uc5d0 \uc368\ub193\uc740 \uc218\ud638\uc790\uc758\ub9dd\uce58 \uc0ac\ub77c\ub294 \ub9d0\uc744 \uc798 \ub4e4\uc73c\uba74 \uc77c\uc5b4\ub098\uc9c0 \uc54a\ub294 \uc0ac\uace0(\uc0dd\uac01)\uc758 \ud750\ub984\uc785\ub2c8\ub2e4. \uc218\ud638\uc790\uc758\ub9dd\uce58\ub97c \uc0ac\uace0 1\ucf54\uc5b4 \uc2e0\ud654\ud15c\uc744 \uc62c\ub9ac\uac8c \ub418\uba74 Q\uc9c4\ud654\uac00 \ub429\ub2c8\ub2e4. \uadf8\ub7ec\uba74 \uc5b4? \uc774\uac70 \ud1b1\ub0a0\ub2e8\uac80 \uc548\uc0ac\uace0 Q\uc9c4\ud654\uac00 \ub418\ub124?\ub77c\ub294 \uc0dd\uac01\uc774 \uc790\uc5f0\uc2a4\ub808 \ub4e4\uac8c\ub418\uc8e0. \uadf8\ub7ec\uba74 \ubc14\ub85c 2\ucf54\uc5b4\ubd80\ud130 \ub8e8\ub09c,\uc720\ub839\ubb34\ud76c,\uace0\uc18d\uc5f0\uc0ac\ud3ec\uac19\uc740 \uacf5\uc18d\ud15c\uc744 \uc62c\ub9b4 \uc218 \uc788\uc2b5\ub2c8\ub2e4. \uadf8\ub798\uc11c \uc9d5\uc218\uc758\ucd1d \uc0c0\uc73c\uba74 \uacf5\uc18d\ud15c 3\ucf54\uc5b4\ub85c \uc62c\ub9ac\uace0 \ubb34\ud55c\uc758\ub300\uac80, \ub3c4\ubbf8\ub2c9\uc744 4\ucf54\uc5b4\uc5d0 \ub561\uaca8\uc640\uc57c \ud588\ub294\ub370 \uc0dd\ub7b5\ud558\ub2c8 \ubb34\ud55c\uc758\ub300\uac80\uc774\ub098 \ub3c4\ubbf8\ub2c9\uc744 3\ucf54\uc5b4\ub85c \ube60\ub974\uac8c \ub561\uaca8 \uc62c \uc218 \uc788\uc2b5\ub2c8\ub2e4. \uadf8\ub9ac\uace0 \ub9dd\uce58\uc758 \ud53c\ud761 \ub54c\ubb38\uc5d0 \uadf8\ub3d9\uc548\uc758 \uc720\uc9c0\ub825\ub3c4 \uc5c4\uccad\ub098\uac8c \uc88b\uc544\uc9c0\uace0\uc694. \n \n\ucc54\ud53c\uc5b8 \ubc38\ub7f0\uc2a4 \ud6a8\uacfc\n\uc785\ud788\ub294 \ud53c\ud574\ub7c9 \uc99d\uac00 5%\n \n\uce7c\ubc14\ub78c \ucd94\ucc9c \ub8ec\n\uce7c\ub0a0\ube44 \ud53c\uc758\ub9db \uc0ac\ub0e5\uc758\uc99d\ud45c \uad76\uc8fc\ub9b0\uc0ac\ub0e5\uafbc + \uce68\ucc29 \ud54f\ube5b\uae38\n\uce58\uba85\uc801\uc18d\ub3c4 \uce68\ucc29 \ud54f\ube5b\uae38 \uccb4\ub825\ucc28\uadf9\ubcf5 + \uc0ac\ub0e5\uc758\uc99d\ud45c(\ud53c\uc758\ub9db) \uad76\uc8fc\ub9b0\uc0ac\ub0e5\uafbc\n \n\uce7c\ubc14\ub78c \ucd94\ucc9c \uc544\uc774\ud15c\n\uc218\ud638\uc790\uc758\ub9dd\uce58 \ud06c\ub77c\ucf04\ud559\uc0b4\uc790 \ub8e8\ub09c\uc758\ud5c8\ub9ac\ucf00\uc778/\uc720\ub839\ubb34\ud76c \ubb34\ud55c\uc758\ub300\uac80/\ub3c4\ubbf8\ub2c9\uacbd\uc758\uc778\uc0ac\n \n\uc218\ud638\uc790\uc758\ub9dd\uce58 \uaf2d \uc0ac\uc8fc\uc138\uc694. \uc218\ud638\uc790\uc758\ub9dd\uce58 + \ud06c\ub77c\ucf04\uc774\uba74 Q\uc9c4\ud654 \ub429\ub2c8\ub2e4.\n \n\ub2e4\ub978 \uc6d0\ub51c\ub4e4\uc740 \uacfc\ub2e4\uce58\uc720 + \uc5ec\uc2e0\uc758\ub208\ubb3c \uc2dc\uc791\ub3c4 \ub9ce\uc774 \ucd94\ucc9c\ub4dc\ub9ac\ub294\ub370, \uce74\uc774\uc0ac\ub294 \uacfc\ub2e4\uce58\uc720\ub9d0\uace0 \uce68\ucc29\uc744 \ucd94\ucc9c\ud558\ub294 \uc774\uc720\ub294 \ucf54\uc5b4\ud15c \ube68\ub9ac \ubf51\uc544\uc11c \uc9c4\ud654\ud574\uc57c \ud558\uae30 \ub54c\ubb38\uc785\ub2c8\ub2e4.\n \n\ucd5c\uadfc AP\uce74\uc774\uc0ac \uc911\uc5d0 \ud06c\ub77c\ucf04\ud559\uc0b4\uc790 \ub9c8\ubc95\uc0ac\uc758\ucd5c\ud6c4 \ub0b4\uc154\uc758\uc774\ube68 \uc774\ud6c4 \uacf5\ud5c8/\ubaa8\uc790/\ubaa8\ub810\ub85c \ub4f1 \uac00\ub294 AP\uce74\uc774\uc0ac\ub3c4 \ud611\uace1 \ud15c\ud2b8\ub9ac\ub3c4 \uc4f0\uae30\ub3c4 \ud558\uc9c0\ub9cc, \uc800\ub294 \uc6b0\ub9ac\ud300\uc774 AD\uac00 \ub9ce\ub2e4\ub294 \uc870\uac74 \ud558\uc5d0\uc11c\ub294 \uc88b\ub2e4\uace0 \uc0dd\uac01\ud558\uace0 \uc870\uac74\uc5d0 \ubd80\ud569\ud558\uc9c0 \uc54a\uc73c \ucd94\ucc9c\ud558\uc9c0 \uc54a\uc2b5\ub2c8\ub2e4.\n \n \n\uce7c\ub9ac\uc2a4\ud0c0\n\ud611\uace1\uc5d0\uc11c\uc758 \ucd94\uc138\n\ud611\uace1 \uce7c\ub9ac\uc2a4\ud0c0\ub294 \ucca0\uac11\uad81 \ub8e8\ub09c \uc774\ub807\uac8c \uac00\ub294\uac8c \uc77c\ubc18\uc801\uc785\ub2c8\ub2e4.\n \n\uce7c\ubc14\ub78c\uc5d0\uc11c\ub3c4 \ub9c8\ucc2c\uac00\uc9c0\uc785\ub2c8\ub2e4.\n \n\ucc54\ud53c\uc5b8 \ubc38\ub7f0\uc2a4 \ud6a8\uacfc\n\uc785\ud788\ub294 \ud53c\ud574\ub7c9 \uc99d\uac00 5%\n \n\uce7c\ubc14\ub78c \ucd94\ucc9c \ub8ec\n\uce7c\ub0a0\ube44 \ub3cc\ubc1c\uc77c\uaca9 \uc0ac\ub0e5\uc758\uc99d\ud45c \uad76\uc8fc\ub9b0\uc0ac\ub0e5\uafbc + \uacfc\ub2e4\uce58\uc720 \ubbfc\ucca9\ud568\n\uce58\uba85\uc801\uc18d\ub3c4 \uacfc\ub2e4\uce58\uc720 \ubbfc\ucca9\ud568 \ucd5c\ud6c4\uc758\uc77c\uaca9(\uc800\ud56d) + \ub3cc\ubc1c\uc77c\uaca9 \uad76\uc8fc\ub9b0\uc0ac\ub0e5\uafbc\n \n\uce7c\ubc14\ub78c \ucd94\ucc9c \uc544\uc774\ud15c\n\uc218\ud638\uc790\uc758\ub9dd\uce58+\uc5ec\uc2e0\uc758\ub208\ubb3c \ubd88\uba78\uc758\ucca0\uac11\uad81 \ub8e8\ub09c\uc758\ud5c8\ub9ac\ucf00\uc778 \ub9c8\ubc95\uc0ac\uc758\ucd5c\ud6c4/\uad6c\uc778\uc218\uc758\uaca9\ub178\uac80 \ub3c4\ubbf8\ub2c9\uacbd\uc758\uc778\uc0ac\n \n\uce7c\ub9ac\uc2a4\ud0c0\ub294 \uc8fc\ub85c \ud53c\ud761 \ud15c\uc744 \uac00\uae30 \ub54c\ubb38\uc5d0 \uacfc\ub2e4\uce58\uc720 \ud6a8\uc728\uc774 \uc5c4\uccad\ub098\uac8c \uc88b\uc740 \ucc54\ud504\uc774\uae30 \ub54c\ubb38\uc5d0 \uc218\ud638\uc790\uc758\ub9dd\uce58 \uc5ec\uc2e0\uc758\ub208\ubb3c \ubb3c\uc57d \uc2dc\uc791\ud574\uc11c \ub098\uc911\uc5d0 \uc790\ub9ac\uc5c6\uc744 \ub54c \uc5ec\uc2e0\uc758\ub208\ubb3c \ud314\uc544\uc8fc\uc2dc\ub294\uac8c \uc88b\uc2b5\ub2c8\ub2e4.\n \n\uce7c\ub9ac\uc2a4\ud0c0\ub294 \uad11\uc804\uc0ac(\uacf5\uc18d\uc2e0) \ube68\ub9ac \uc0ac\uc8fc\uba74 \uc88b\uc2b5\ub2c8\ub2e4. \n \n3\ucf54\uc5b4 \uc774\ud6c4\ubd80\ud130\ub294 \ud53c\ubc14\ub77c\uae30 \ub3c4\ubbf8\ub2c9\uacbd\uc758\uc778\uc0ac \ub4f1 \uad6c\ub9e4\ud574\uc8fc\uc2dc\uba74 \ub429\ub2c8\ub2e4.\n \n\uadf8\ub9ac\uace0 \uce7c\ubc14\ub78c \uce7c\ub9ac\uc2a4\ud0c0\ub294 \uc801 \uadfc\uc811\uc774 \ub9ce\uc544\uc11c \ucc3d \ub9ce\uc774 \uaf42\uc544\uc11c E\ucc22\uc744 \uc218 \uc788\ub294 \uacbd\uc6b0\ub97c \uc81c\uc678\ud558\uace0\uc120 E\uc120\ub9c8\ubcf4\ub2e4 Q\uc120\ub9c8 \ud574\uc8fc\uc2dc\uba74 \uc88b\uc2b5\ub2c8\ub2e4.\n \n \n\ucf00\uc774\ud2c0\ub9b0\n\ud611\uace1\uc5d0\uc11c\uc758 \ucd94\uc138\n\ud611\uace1 \ucf00\uc774\ud2c0\ub9b0\uc740 \ub3cc\ud48d\uc774 \uace0\uc815\uc774\uc5c8\ub2e4\uac00 \ub3cc\ud48d \ub108\ud504 \ud6c4\uc5d0\ub294 \ud06c\ub77c\ucf04 \ub3cc\ud48d \ubaa8\ub450 \ub9ce\uc774 \uac11\ub2c8\ub2e4.\n \n\uce7c\ubc14\ub78c\uc5d0\uc11c\ub294 \ub300\ubd80\ubd84 \ud06c\ub77c\ucf04\uc744 \ucd94\ucc9c\ub4dc\ub9bd\ub2c8\ub2e4.\n \n\ucc54\ud53c\uc5b8 \ubc38\ub7f0\uc2a4 \ud6a8\uacfc\n\uc785\ud788\ub294 \ud53c\ud574\ub7c9 \uac10\uc18c 5%\n \n\uce7c\ubc14\ub78c \ucd94\ucc9c \ub8ec\n\uce58\uba85\uc801\uc18d\ub3c4(\uae30\ubbfc\ud55c\ubc1c\ub180\ub9bc) \uacfc\ub2e4\uce58\uc720 \ud54f\ube5b\uae38 \uccb4\ub825\ucc28\uadf9\ubcf5 + \uc0ac\ub0e5\uc758\uc99d\ud45c(\ud53c\uc758\ub9db) \uad76\uc8fc\ub9b0\uc0ac\ub0e5\uafbc\n \n\uce7c\ubc14\ub78c \ucd94\ucc9c \uc544\uc774\ud15c\n\uc218\ud638\uc790\uc758\ub9dd\uce58+\uc5ec\uc2e0\uc758\ub208\ubb3c \ud06c\ub77c\ucf04\ud559\uc0b4\uc790 \uace0\uc18d\uc5f0\uc0ac\ud3ec \ubb34\ud55c\uc758\ub300\uac80/\ub3c4\ubbf8\ub2c9\uacbd\uc758\uc778\uc0ac\n \n\uc218\ud638\uc790\uc758\ub9dd\uce58 \uc5ec\uc2e0\uc758\ub208\ubb3c \ubb3c\uc57d \uc2dc\uc791\ud574\uc11c \uc5ec\ub208\uc740 \ub098\uc911\uc5d0 \ud314\uc544\uc8fc\uc138\uc694. \ucd08\ubc18\uc5d0 \ub77c\uc778\ud074\ub9ac\uc5b4\ub098 \uacac\uc81c\ud558\uae30 \ud3b8\ud574\uc9d1\ub2c8\ub2e4. \uce68\ucc29 + no\uc5ec\ub208 vs \uacfc\ub2e4\uce58\uc720+\uc5ec\ub208 \ucd94\ud6c4 \ub418\ud314\uae30\uc758 \uc120\ud0dd\uc774\uace0 \ucde8\ud5a5\ucc28\uc774\uc774\uc9c0\ub9cc, \uc800\ub294 \uc5ec\ub208+\uacfc\ub2e4\uce58\uc720\uac00 1\ucf54\uc5b4 \uc870\uae08 \ub290\ub824\uc9c0\ub294\uac70 \ube7c\uace0\ub294 \ucd08\ubc18\uc5d0\ub3c4 \uc88b\uace0 \uc911\ud6c4\ubc18\uac08\uc218\ub85d\ub3c4 \ub354 \uc88b\ub2e4\uace0 \ubd10\uc694.\n \n1\ucf54\uc5b4\ub294 \ud06c\ub77c\ucf04\uc774 \uc81c\uc77c \ub9ce\uc774 \uc120\ud0dd\ud569\ub2c8\ub2e4. \ucca0\uac11\uad81\uc740 \uc798 \uc548 \uac00\uc9c0\ub9cc \uad6c\ub3c4\ub97c \ub531 \ubd24\uc744 \ub54c \uc801 \ud3ec\ud0b9\uc774 \ub108\ubb34 \uc384\uc11c \ucd08\ubc18\ubd80\ud130 \ubc84\ud2f0\uae30 \ud798\ub4e0 \uacbd\uc6b0, \uc6b0\ub9ac\ud300\uc774 \ube0c\ub8e8\uc800\uc640 \uc554\uc0b4\uc790\ud55c\ud14c \ucde8\uc57d\ud55c\ub370 \ud0c8\uc9c4\uc740 \uc5c6\uace0 \ub098\ub97c \uc9c0\ucf1c\uc904 \ucc54\ud504\uac00 \uc5c6\ub294 \uacbd\uc6b0\uc5d0\ub9cc \uc120\ud0dd\uc801\uc73c\ub85c \uac00\uc8fc\uc2dc\uba74 \ub429\ub2c8\ub2e4.\n \n2\ucf54\uc5b4\ub85c \uc801 \uc0ac\uac70\ub9ac\uac00 \uae38\uac70\ub098 \uac70\ub9ac\uc8fc\uba74 \uc548\ub420 \ub54c \ub0b4\uac00 \uc0ac\uac70\ub9ac\ub97c \uc774\uc6a9\ud558\uc5ec \ud55c\ub300\uc529 \ucf55! \uce58\uace0 \uc624\uba74 \uc88b\uc744 \ud310\uc77c \ub54c \uace0\uc18d\uc5f0\uc0ac\ud3ec \uc88b\uace0\uc694. \uc801 \uadfc\uc811\uc774 \ube44\ube44\ub7ec \uc628\ub2e4 \uc2f6\uc73c\uba74 \ud3ed\ud48d\uac08\ud034 \uc88b\uc2b5\ub2c8\ub2e4. \uc801 \uadfc\uc811\uc774 \ub2e4\uc218\uc774\uba74\uc11c \ub77c\uc778\ud074\ub9ac\uc5b4\uac00 \ub290\ub9ac\uace0 \ucf00\ud2c0 \ub51c \ube44\uc911\uc774 \ub192\uc740 \uc870\ud569\uc5d0\uc11c\ub294 \ub8e8\ub09c\ub3c4 \uc88b\uc2b5\ub2c8\ub2e4. \ud0f1\ucee4\uac00 \uc815\ub9d0 \uc548\uc7a1\ud788\uba74 2\ucf54\uc5b4\uc5d0 \ub3c4\ubbf8\ub2c9\uc744 \uac00\uc154\ub3c4 \ub429\ub2c8\ub2e4.\n \n3\ucf54\uc5b4\ub294 \uac00\uae09\uc801 \ubb34\ud55c\uc758\ub300\uac80 \uc62c\ub9ac\ub294\ub370, \uc801 \ud0f1\ucee4\uac00 \uc548 \ub6ab\ub9b0\ub2e4 \uc2f6\uc73c\uba74 \ub3c4\ubbf8\ub2c9\ubd80\ud130 \uba3c\uc800 \uac00\uace0 \ubb34\ud55c\uc758\ub300\uac80 \uc0ac\uc154\ub3c4 \ub418\uace0\uc694. \uc0c1\ub300\uac00 \ud314\uc774 \uae38\uace0 \ubc29\uc5b4\ub825\uc774 \ub192\uc740 \ud3b8\uc774 \uc544\ub2c8\ub77c \uace0\uc18d\uc5f0\uc0ac\ud3ec\ub791 \uc2dc\ub108\uc9c0 \ub0b4\ub824\uace0 \ud3ed\ud48d\uac08\ud034 \uac00\ub294 \uac83\ub3c4 \uad1c\ucc2e\uc2b5\ub2c8\ub2e4.\n \n4\ucf54\uc5b4\ubd80\ud130\ub294 \ud53c\ubc14\ub77c\uae30 \ub3c4\ubbf8\ub2c9 \uc2dc\ubbf8\ud130 \ub9ec\ubaa8\uc154\uc2a4 \ub4f1 \uc720\ub3d9\uc801\uc73c\ub85c \ud544\uc694\ud55c\uac70 \uad6c\ub9e4\ud574\uc8fc\uc2dc\uba74 \ub429\ub2c8\ub2e4.\n \n \n\ucf00\uc77c\n\ud611\uace1\uc5d0\uc11c\uc758 \ucd94\uc138\n\ud611\uace1 \ucf00\uc77c\uc740 \ud604\uc7ac AD\ucf00\uc77c\ub85c \uc4f0\ub294 \ub354 \ub9ce\uace0 \ud3c9\ub3c4 \uc88b\uc2b5\ub2c8\ub2e4. \ud611\uace1\uc5d0\uc11c \ucf00\uc77c\ub9cc \ud558\ub294 \uc7a5\uc778\ubd84\ub4e4\ub3c4 only \uce58\uba85\ud0c0\ub85c \uac00\ub294 \uacbd\uc6b0\uac00 \ud6e8\uc52c \ub354 \ub9ce\uace0, AP\ub97c \uac00\ub354\ub77c\ub3c4 \uade0\uc5f4 \ub0b4\uc154 \uc774\ud6c4\uc5d0 \ub9c8\ucd5c\ub9cc \uc11e\ub294 \uc815\ub3c4\uc785\ub2c8\ub2e4.\n \n\uce7c\ubc14\ub78c \ucf00\uc77c\ub3c4 AD\ucf00\uc77c\uc774 \uc88b\uc740\ub370, \uc5bc\ub9c8\uc804\uc5d0 \uade0\uc5f4\uc774 \uc57d\uac04 \ud328\uce58\ub97c \ubc1b\uc73c\uba74\uc11c AP\ucf00\uc77c\uc758 \uc2b9\ub960\uc774 \uad49\uc7a5\ud788 \ub9ce\uc774 \uc62c\ub77c\uac14\uc2b5\ub2c8\ub2e4. \uac1c\uc778\uc801\uc73c\ub85c AD\ucf00\uc77c\uc744 \ub354 \ucd94\ucc9c\ud569\ub2c8\ub2e4. AP\ucf00\uc77c\uc740 \uc2dc\uac04\uc774 \uc9c0\ub098\uce58\uac8c \ub108\ubb34 \ub9ce\uc774 \uac78\ub9ac\uace0, \ub51c\uc801\uc778 \ubd80\ubd84\uc5d0\uc11c\ub3c4 AD\ube4c\ub4dc\uac00 \ub354 \uc88b\uc2b5\ub2c8\ub2e4. \ub2e4\ub9cc \uc2a4\ud0ac \uacc4\uc218\uac00 AP\ub85c \ub2ec\ub824\uc788\ub2e4\ubcf4\ub2c8 \uc720\ud2f8\uc131\uc774\ub098 \uc2a4\ud0ac\ub51c\uc5d0\uc11c\ub294 AP\uac00 \uc88b\uc2b5\ub2c8\ub2e4.\n \n\ud639\uc790\ub294 \ucf00\uc77c\uc744 AD\ub85c \uc4f8\uac70\uba74 \ub2e4\ub978 \uc6d0\ub51c\uc744 \uc4f0\ub294\uac8c \ub0ab\uc9c0 \uc54a\ub0d0? \ud558\ub294\ub370 \ub9de\uc2b5\ub2c8\ub2e4. \uc6d0\ub51c\uacfc \ucf00\uc77c\uc774 \uc788\uc73c\uba74 \uc6d0\ub51c\uc774 \uac00\uae09\uc801 \uc6b0\uc120\uc21c\uc704\uc785\ub2c8\ub2e4. \uce7c\ubc14\ub78c \ucf00\uc77c\uc740 \uc5b4\ub514\uae4c\uc9c0\ub098 \ud0f1\uc7a1\uc774\uc6a9 \uc720\uc0ac \uc6d0\ub51c\ub85c \ud65c\uc6a9\ud558\ub824\uace0 \uc6d0\ub51c\ud53d \uc5c6\uc744 \ub54c \uaebc\ub0b4\ub294 \ud53d\uc774\uc5d0\uc694. \uc8fc\uad00\uc801\uc73c\ub85c \ub2e8\uc5b8\ucee8\ub370 AD\uac00 \ub354 \uc88b\uc2b5\ub2c8\ub2e4. AP\ub294 \uadf9\ud6c4\ubc18\uc5d0\ub9cc \uc88b\uace0 \ud300\uc758 \uace0\ud608 \ube68\uc544\uba39\ub294 \ud53d\uc774\ub77c\uace0 \uc0dd\uac01\ud574\uc694.\n \n\ucc54\ud53c\uc5b8 \ubc38\ub7f0\uc2a4 \ud6a8\uacfc\n\uc785\ud788\ub294 \ud53c\ud574\ub7c9 \uac10\uc18c 5%\n\ubc1b\ub294 \ud53c\ud574\ub7c9 \uc99d\uac00 3%\n \n\uce7c\ubc14\ub78c \ucd94\ucc9c \ub8ec\n\uce58\uba85\uc801\uc18d\ub3c4(\uc9d1\uc911\uacf5\uaca9) \uce68\ucc29 \ubbfc\ucca9\ud568 \ucd5c\ud6c4\uc758\uc77c\uaca9 + \uc0ac\ub0e5\uc758\uc99d\ud45c \uad76\uc8fc\ub9b0\uc0ac\ub0e5\uafbc\n \n\uce7c\ubc14\ub78c \ucd94\ucc9c \uc544\uc774\ud15c\n\uc218\ud638\uc790\uc758\ub9dd\uce58+\uc5ec\uc2e0\uc758\ub208\ubb3c \ubd88\uba78\uc758\ucca0\uac11\uad81 \uad6c\uc778\uc218\uc758\uaca9\ub178\uac80 \ub9c8\ub098\ubb34\ub124 \ubab0\ub77d\ud55c\uc655\uc758\uac80\n\uc218\ud638\uc790\uc758\ub9dd\uce58+\uc5ec\uc2e0\uc758\ub208\ubb3c \ubd88\uba78\uc758\ucca0\uac11\uad81 \uc720\ub839\ubb34\ud76c(\ub8e8\ub09c\uc758\ud5c8\ub9ac\ucf00\uc778) \ubb34\ud55c\uc758\ub300\uac80\n \n\uce58\uba85\uc801\uc18d\ub3c4\uc640 \uc9d1\uc911\uacf5\uaca9\uc740 \ucde8\ud5a5\ucc28\uc774\uc785\ub2c8\ub2e4. \uce58\uba85\uc801\uc18d\ub3c4\ub294 \ubb34\ub09c\ud558\uace0 \uc9d1\uc911\uacf5\uaca9\uc740 \uc544\uc774\ud15c\uc774 \uc5b4\ub290 \uc815\ub3c4 \ub098\uc640\uc11c \uacf5\uaca9\uc18d\ub3c4\uac00 \ub530\ub77c\uc8fc\uba74 \uc88b\uc2b5\ub2c8\ub2e4. \ud0f1\ucee4 \uc7a1\uae30\ub3c4 \uc88b\uace0\uc694. \ucc38\uace0\ub85c \uc9d1\uc911\uacf5\uaca9\uc740 E\uc2a4\ud0ac\uc5d0\ub3c4 \uc313\uc774\uace0 \uad6c\uc778\uc218 \uc2a4\ud0dd \ud6a8\uacfc\uc5d0\ub3c4 \uc313\uc774\uae30 \ub54c\ubb38\uc5d0 \ud3c9\ud3c9e\ub098 \uad6c\uc778\uc218\ub97c \uc62c\ub838\uc744 \ub54c\uc5d0\ub294 \ud3c9e, \ud3c9\ud3c9\uc5d0\ub3c4 \ud130\uc9d1\ub2c8\ub2e4.\n \n\uc2dc\uc791\uc544\uc774\ud15c \uc5ec\uc2e0\uc758\ub208\ubb3c\uc744 \uc0ac\uba74 \ucd08\ubc18 \ub2e8\uacc4\uc5d0\uc11c \ub9c8\ub098\uad00\ub9ac\ud558\uae30 \uad49\uc7a5\ud788 \ud3b8\ub9ac\ud569\ub2c8\ub2e4.\n \n\ucf00\uc77c\uc740 \ud15c\ud2b8\ub9ac\ub3c4 \uc880 \ub2e4\uc591\ud55c \ud3b8\uc778\ub370\uc694. \ud06c\uac8c\ub294 \uce58\uba85\ud0c0 \ube4c\ub4dc\uc640 \uad6c\uc778\uc218\ub97c \ud65c\uc6a9\ud55c \ucd94\uac00\ub370\ubbf8\uc9c0 \ube4c\ub4dc\uac00 \uc788\uc2b5\ub2c8\ub2e4.\n\uce58\uba85\ud0c0 \ube4c\ub4dc\uac00 \ub51c\uc744 \ud655\uc2e4\ud788 \uc88b\uace0 \uc798 \ucef8\uc744 \ub54c \uac00\uba74 \uc88b\uc2b5\ub2c8\ub2e4. \ucd5c\uadfc\uc5d0\ub294 \uad6c\uc778\uc218 \ube4c\ub4dc\uac00 \ud558\uc704\ud15c \ud6a8\uc728\ub3c4 \uc88b\uace0 \uc0c1\ud669\uc5d0 \ub9de\uac8c \ub9de\ucdb0\uac00\uae30 \uc88b\uc544\uc11c \ub354 \ub9ce\uc774 \uac00\ub294\uac70 \uac19\uc2b5\ub2c8\ub2e4. \uad6c\uc778\uc218 \ube4c\ub4dc\ub294 \ucca0\uac11\uad81 \uad6c\uc778\uc218 \ub9c8\ub098\ubb34\ub124 \ubab0\ub77d \uc21c\uc11c\ub85c \uc0ac\uc8fc\uc2dc\uba74 \ub418\uace0, \ub9c8\uc800\uac00 \ud544\uc694\ud55c \ud310\uc5d0 \uc911\uac04\uc5d0 \ub9c8\ucd5c\ub97c \uc11e\uc5b4\uc8fc\uc2dc\uba74 \ub429\ub2c8\ub2e4. \uc774 \uc21c\uc11c\ub85c \uac00\uac8c \ub418\uba74 \ub9c8\ub098\ubb34\ub124\uac00 \uc870\uae08 \ub290\ub9ac\uac8c \ub098\uc62c \uc218 \uc788\uae30 \ub54c\ubb38\uc5d0 \uace8\ub4dc\uc5d0 \ub9de\ucdb0\uc11c \uad6c\uc778\uc218 \ud558\uc704\ud15c\uc778 \ubd84\ub178\uc758\uce7c\ub9cc \uc0ac\uace0 \ub9c8\ub098\ubb34\ub124\ub97c \ub354 \ube68\ub9ac \uc62c\ub824\uc918\ub3c4 \uc88b\uc2b5\ub2c8\ub2e4.\n \n\uc800\ub294 \uc694\uc0c8 \uc6d0\ub51c\ub4e4\ub85c \ucca0\uac11\uad81 \uac00\ub294 \uac83\uc744 \uc120\ud638\ud574\uc11c \ucca0\uac11\uad81\uc744 \uae30\ubcf8 \ubca0\uc774\uc2a4\ub85c \ub480\ub294\ub370, \ub9cc\uc57d \ud06c\ub77c\ucf04 \uac00\ub294 \uac83\uc744 \ub354 \uc120\ud638\ud55c\ub2e4\uba74 \ud54f\ube5b\uae38 \ucc0d\uace0 \ud06c\ub77c\ucf04\uc744 \uac00\uc154\ub3c4 \ub429\ub2c8\ub2e4. \n \n \n\ucf54\uadf8\ubaa8\n\ud611\uace1\uc5d0\uc11c\uc758 \ucd94\uc138\n\ud611\uace1 \ucf54\uadf8\ubaa8\ub294 \uc694\uc0c8 \ud0f1\uadf8\ubaa8\ub77c\uace0 \ud574\uc11c \ub9c8\ucd5c \ub8e8\ub09c \ub780\ub450\uc778\uc73c\ub85c \ub2e8\ub2e8\ud558\uac8c \uac00\ub294\uac8c \ub300\uc138\uc785\ub2c8\ub2e4.\n \n\uce7c\ubc14\ub78c \ucf54\uadf8\ubaa8\ub3c4 \ub9c8\ucc2c\uac00\uc9c0\ub85c \ud0f1\uadf8\ubaa8\ucc98\ub7fc \uac00\ub294\uac8c \uc88b\ub2e4\uace0 \uc0dd\uac01\ud569\ub2c8\ub2e4.\n \n\ucc54\ud53c\uc5b8 \ubc38\ub7f0\uc2a4 \ud6a8\uacfc\n\uc785\ud788\ub294 \ud53c\ud574\ub7c9 \uac10\uc18c 10%\n\ubc1b\ub294 \ud53c\ud574\ub7c9 \uc99d\uac00 5%\n \n\uce7c\ubc14\ub78c \ucd94\ucc9c \ub8ec\n\uce58\uba85\uc801\uc18d\ub3c4 \uce68\ucc29 \ud54f\ube5b\uae38 \uccb4\ub825\ucc28\uadf9\ubcf5 + \uc0ac\ub0e5\uc758\uc99d\ud45c \uad76\uc8fc\ub9b0\uc0ac\ub0e5\uafbc\n \n\uce7c\ubc14\ub78c \ucd94\ucc9c \uc544\uc774\ud15c\n\uc218\ud638\uc790\uc758\ub9dd\uce58 \ub9c8\ubc95\uc0ac\uc758\ucd5c\ud6c4 \ub8e8\ub09c\uc758\ud5c8\ub9ac\ucf00\uc778(\uc720\ub839\ubb34\ud76c) \ubd88\uba78\uc758\ucca0\uac11\uad81/\ud06c\ub77c\ucf04\ud559\uc0b4\uc790 \uad6c\uc778\uc218\uc758\uaca9\ub178\uac80/\ub780\ub450\uc758\uc758\uc608\uc5b8/\uc815\ub839\uc758\ud615\uc0c1 \ub4f1\n \n\uc2dc\uc98c10 \ucf54\uadf8\ubaa8\uc5d0 \ube44\ud574\uc11c \uc2dc\uc98c11 \ucf54\uadf8\ubaa8\ub294 \uc774\uc81c \uc0c1\ud669\ubcc4\ub85c \ub9de\ucdb0\uac00\ub294\uac8c \uc815\ub9d0 \uc911\uc694\ud55c\uac70 \uac19\uc544\uc694. \uc9c4\uc9dc \ub51c \ub123\uae30 \ud3b8\ud55c \uad6c\ub3c4\uc5d0\uc11c\ub294 \uadf9\ub51c\ub85c \ud06c\ub77c\ucf04 \ub8e8\ub09c \ub098\ubcf4\ub9ac \uc778\ud53c(\ubb34\ub300) \uc774\ub807\uac8c \uac00\ub3c4 \ub418\uaca0\uc9c0\ub9cc, \uac19\uc740 \ud300\uc774 \uc798 \ubd10\uc8fc\uc9c0 \uc54a\uace0\uc120 \uc774\ub7f0 \uacbd\uc6b0\ub294 \ub4dc\ubb3c\uc5b4\uc694. \ucc54\ud53c\uc5b8 \ubc38\ub7f0\uc2a4 \ud6a8\uacfc \ub54c\ubb38\uc5d0 \uc8fd\ub294\uac74 \uc27d\uac8c \uc8fd\uace0, \ub51c\uc740 \uc0dd\uac01\ubcf4\ub2e4 \uc798 \uc548\ubc15\ud788\ub294 \uc6d0\ub51c.\n \n\ub9c8\ucd5c(\uc704\uce58\uc5d4\ub4dc) \ub8e8\ub09c \ucca0\uac11\uad81 \ud06c\ub77c\ucf04 \uad6c\uc778\uc218 \ub098\ubcf4\ub9ac \ub3c4\ubbf8\ub2c9 \uc778\ud53c(\ubb34\ub300) \ub780\ub450\uc778 \uc774\ub7f0 \uc544\uc774\ud15c \ubaa8\ub450 \uc62c\ub824\ubcfc\ub9cc\ud55c \uc544\uc774\ud15c\uc774\ub2c8 \ud544\uc694\ud55c \uc0c1\ud669\uc5d0 \uc21c\uc11c \ubf51\uc544\uc11c \uac00\uc8fc\uba74 \ub420\uac70 \uac19\uc2b5\ub2c8\ub2e4.\n \n\ub9c8\ubc95\uc0ac\uc758\ucd5c\ud6c4 \ub8e8\ub09c\uc758\ud5c8\ub9ac\ucf00\uc778\uae4c\uc9c0 \uc62c\ub824\uc8fc\uace0 3\ucf54\uc5b4\uc5d0\uc11c \ub51c \ub123\uae30 \ud3b8\ud558\uba74 \ud06c\ub77c\ucf04, \ud798\ub4e4\uba74 \ucca0\uac11\uad81 \uac00\uc8fc\uc2dc\uba74 \ub429\ub2c8\ub2e4.\n \n11.17 \ud328\uce58\uc5d0\uc11c \ub9c8\ubc95\uc0ac\uc758\ucd5c\ud6c4\uac00 8\ub819\uae4c\uc9c4 \uc57d\ud558\ub3c4\ub85d \ud328\uce58 \ubc1b\uc558\ub294\ub370 \uce7c\ubc14\ub78c\uc740 3\ub819\ubd80\ud130 \uc2dc\uc791\ud574\uc11c 8\ub819\uae4c\uc9c0 \uae08\ubc29 \ub3c4\ub2ec\ud558\uace0, \uc218\ud638\uc790\uc758\ub9dd\uce58\ub3c4 \uc0ac\uace0 \uc2dc\uc791\ud560\uac70\ub77c \ub300\uccb4\ub85c \uc5c4\uccad \uc798 \ud070\uac8c \uc544\ub2c8\uba74 1\ucf54\uc5b4 \ub9c8\ucd5c \ub728\ub294 \ud0c0\uc774\ubc0d\uc774\uba74 \ubb34\ub09c\ud558\uac8c 8-9\ub808\ubca8 \uc815\ub3c4\ub418\uc11c \uc774\uc804\uacfc\ub294 \ucc28\uc774\ub294 \uc5c6\ub2e4\uace0 \ubcf4\uc2dc\uba74 \ub429\ub2c8\ub2e4.\n \n \n\ucf54\ub974\ud0a4\n\ud611\uace1\uc5d0\uc11c\uc758 \ucd94\uc138\n\ud611\uace1 \ucf54\ub974\ud0a4\ub294 \uc815\uc218\uc57d\ud0c8\uc790 \ub9c8\ub098\ubb34\ub124 \ucca0\uac11\uad81 \ubb34\ud55c\uc758\ub300\uac80 \ud15c\ud2b8\ub9ac\uc640 \uc2e0\ud30c\uc790\uac00 \ubc84\ud504\ub418\uba74\uc11c \uc2e0\ud30c\uc790 \ubb34\ub77c\ub9c8\ub098 \ud15c\ud2b8\ub9ac\uac00 \uc788\uc2b5\ub2c8\ub2e4.\n \n\uce7c\ubc14\ub78c \ucf54\ub974\ud0a4\ub3c4 \ub9c8\ucc2c\uac00\uc9c0\ub85c \uac00\uc8fc\uba74 \ub418\ub294\ub370 \uc218\ud638\uc790\uc758\ub9dd\uce58 + \uc5ec\uc2e0\uc758\ub208\ubb3c\ub85c \uc2dc\uc791\ud574\uc8fc\uc2dc\uba74 \ub429\ub2c8\ub2e4.\n \n\ucc54\ud53c\uc5b8 \ubc38\ub7f0\uc2a4 \ud6a8\uacfc\n\uc5c6\uc74c\n \n\uce7c\ubc14\ub78c \ucd94\ucc9c \ub8ec\n\uae30\ubbfc\ud55c\ubc1c\ub180\ub9bc \uacfc\ub2e4\uce58\uc720 \ud54f\ube5b\uae38 \ucd5c\ud6c4\uc758\uc77c\uaca9 + \ud53c\uc758\ub9db(\uc0ac\ub0e5\uc758\uc99d\ud45c) \uad76\uc8fc\ub9b0\uc0ac\ub0e5\uafbc\n\uc5b4\ub460\uc758\uc218\ud655 \ud53c\uc758\ub9db \uc0ac\ub0e5\uc758\uc99d\ud45c \uad76\uc8fc\ub9b0\uc0ac\ub0e5\uafbc + \uacfc\ub2e4\uce58\uc720 \ud54f\ube5b\uae38\n \n\uce7c\ubc14\ub78c \ucd94\ucc9c \uc544\uc774\ud15c\n\uc218\ud638\uc790\uc758\ub9dd\uce58+\uc5ec\uc2e0\uc758\ub208\ubb3c \uc815\uc218\uc57d\ud0c8\uc790 \ub9c8\ub098\ubb34\ub124 \ubd88\uba78\uc758\ucca0\uac11\uad81 \ubb34\ud55c\uc758\ub300\uac80 \uace0\uc18d\uc5f0\uc0ac\ud3ec\n \n\uc2e0\ubc1c\uc740 \ub9c8\uad00\uc2e0 \uac00\uc90d\ub2c8\ub2e4.\n \n\ub8ec\uc740 \uae30\ubbfc\ud55c\ubc1c\ub180\ub9bc\uc744 \uc774\uc6a9\ud574\uc11c \uc720\uc9c0\ub825\uc744 \uc870\uae08 \ub298\ub824\uc8fc\uac70\ub098 \uc5b4\ub460\uc758\uc218\ud655\uc744 \ub4e4\uc5b4\uc11c \ub51c\uc744 \uc870\uae08 \ub354 \uc8fc\uac70\ub098 \ud558\uba74 \ub429\ub2c8\ub2e4.\n \n11.17 \ub54c \uae30\ubbfc\ud55c\ubc1c\ub180\ub9bc\uc774 \uc6d0\uac70\ub9ac \ucc54\ud504\ud55c\ud14c \ubc84\ud504\ub418\uba74\uc11c \uae30\ubc1c \uc88b\ub2e4\uace0 \uc0dd\uac01\ud574\uc694. \uc774\uac8c \uc720\uc9c0\ub825\uc774 \uc5c4\uccad \ub298\uc5c8\ub354\ub77c\uace0\uc694.\n \n\ucf54\ub974\ud0a4 \uc2e0\ud30c\uc790 \ub108\ud504 \ub2f9\ud55c \uc774\ud6c4\ub85c \uc2e0\ud30c\uc790\ub294 \ub51c\uc774 \ubcc4\ub85c \uc548\ub098\uc628\ub2e4\uace0 \uc0dd\uac01\ud574\uc11c \uc815\uc218 \ucd94\ucc9c\ud569\ub2c8\ub2e4.\n \n* \ucd5c\uadfc \ub354\uc0e4\uc774\ub294 \ucf54\ub974\ud0a4\ub97c \ud560 \ub54c \uc815\uc218, \uc989 \uc8fc\ubb38\uac80 \ud6a8\uacfc\ub97c \uac00\uc9c0\uc54a\uace0 \ube60\ub974\uac8c \ub9dd\uce58 \ub9c8\ub098\ubb34\ub124 \ucca0\uac11\uad81 \uace0\uc5f0\ud3ec \uc778\ud53c(\ubb34\ud55c\uc758\ub300\uac80) \uc774\ub807\uac8c \uac00\ub294\ub370 \uad1c\ucc2e\uc740\uac70 \uac19\uc544\uc694. \uc815\uc218 \ucf54\ub974\ud0a4 \ud560 \ub54c\ub9c8\ub2e4 \ub290\ub080\uac74\ub370 3\ucf54\uc5b4 \ucca0\uac11\uad81 \ub098\uc624\uae30\ub9cc \ud558\uba74 \ub51c+\ud53c\ud761 \uc644\ubcbd\ud55c\ub370 \uc655\uadc0\uac00 \uc624\ub798 \uac78\ub9b0\ub2e4\ub294 \ub290\ub08c\uc774 \uc788\uc5c8\uac70\ub4e0\uc694. \ub354\uc0e4\uc774\ucc98\ub7fc \uc815\uc218\ub97c \ube7c\ubc84\ub9ac\ub4e0\uc9c0, \ub9c8\ub098\ubb34\ub124 \ube7c\uace0 \uc815\uc218 \ucca0\uac11 \uc778\ud53c \uace0\uc5f0\ud3ec \uc774\ub807\uac8c \uc5f0\uad6c\ud574\ubcf4\uba74 \uad1c\ucc2e\uc740\uac70 \uac19\uc2b5\ub2c8\ub2e4.\n \n\ud038\n\ud611\uace1\uc5d0\uc11c\uc758 \ucd94\uc138\n\ud611\uace1 \ud038\uc740 \ud0d1 \ucc54\ud504\uc774\uc8e0. \ub3cc\ud48d \ud3ed\ud48d\uac08\ud034 \ubb34\ud55c\uc758\ub300\uac80 \ud15c\ud2b8\ub9ac\ub97c \ub9ce\uc774 \uac11\ub2c8\ub2e4.\n \n\uce7c\ubc14\ub78c \ud038\uc740 \ud06c\uac8c \ubc29\uad00 \ud038\uacfc \uce58\uba85\ud0c0 \ud038\uc73c\ub85c \ub098\ub20c \uc218 \uc788\uc2b5\ub2c8\ub2e4.\n \n\ucc54\ud53c\uc5b8 \ubc38\ub7f0\uc2a4 \ud6a8\uacfc\n\uc785\ud788\ub294 \ud53c\ud574\ub7c9 \uc99d\uac00 8%\n\ubc1b\ub294 \ud53c\ud574\ub7c9 \uac10\uc18c 8%\n \n\uce58\uba85\ud0c0 \ud038\n\uce7c\ubc14\ub78c \ucd94\ucc9c \ub8ec\n\uce58\uba85\uc801\uc18d\ub3c4 \uacfc\ub2e4\uce58\uc720 \ud54f\ube5b\uae38 \uccb4\ub825\ucc28\uadf9\ubcf5 + \uc0ac\ub0e5\uc758\uc99d\ud45c(\ud53c\uc758\ub9db) \uad76\uc8fc\ub9b0\uc0ac\ub0e5\uafbc \n \n\uce7c\ubc14\ub78c \ucd94\ucc9c \uc544\uc774\ud15c\n\uc218\ud638\uc790\uc758\ub9dd\uce58+\uc5ec\uc2e0\uc758\ub208\ubb3c \ud06c\ub77c\ucf04\ud559\uc0b4\uc790 \ud53c\ubc14\ub77c\uae30 \ubb34\ud55c\uc758\ub300\uac80/\ub3c4\ubbf8\ub2c9\uacbd\uc758\uc778\uc0ac\n \n\ud038\uc740 \uc5ec\uc2e0\uc758\ub208\ubb3c \uc0ac\ub480\ub2e4\uac00 \ud314\uc544\uc8fc\uae30 \uc544\uc8fc \uc88b\uc740 \ucc54\ud504\uc5d0\uc694. \uad81 1\ub808\ubca8 \ub54c \ub9c8\ub098100\uc774\ub77c\uc11c \uad81 \uc790\uc8fc \ubcc0\uc2e0\ud558\uae30\uc5d0 \uad49\uc7a5\ud788 \ub9c8\ub098\ubd80\ub2f4\uc774 \ub418\ub294\ub370 \uc218\ud638\uc790\uc758\ub9dd\uce58 \uc5ec\uc2e0\uc758\ub208\ubb3c \ubb3c\uc57d \uc2dc\uc791\ud574\uc11c \uc5ec\uc2e0\uc758\ub208\ubb3c \uc4f0\ub2e4\uac00 \uad81 2\ub808\ubca8 \ucc0d\ub294 \ud0c0\uc774\ubc0d\uc5d0 \ud314\uc544\uc8fc\uc2dc\uba74 \ub429\ub2c8\ub2e4. \ud038\uc758 \uad81\uc740 1\ub819 \ub9c8\ub098100, 2\ub819 \ub9c8\ub09850, 3\ub819 \ub9c8\ub0980 \ub4e4\uc5b4\uac11\ub2c8\ub2e4.\n \n\ub9cc\uc57d, \uc5ec\uc2e0\uc758\ub208\ubb3c \uc0ac\uae30 \uc2eb\uc73c\uc2dc\uba74 \uacfc\ub2e4\uce58\uc720 \ub300\uc2e0 \uce68\ucc29 \ucc0d\uc5b4\uc8fc\uc2dc\uba74 \ub429\ub2c8\ub2e4.\n \n\ud06c\ub77c\ucf04\uc744 \uae30\ubcf8\uc73c\ub85c \ub450\uace0, \uc0c1\ub300\uc5d0 \ud3ec\ud0b9\uc774 \uc9c0\ub098\uce58\uac8c \uac15\ud558\uac70\ub098 \uc81c\ub4dc \ud53c\uc988\uac19\uc740 \uc544\uad70\uc774 \ub3c4\uc640\uc8fc\uc9c0 \uc54a\uc73c\uba74 \ub300\ucc98\ud558\uae30 \uc5b4\ub824\uc6b4 \ucc54\ud504\ub4e4\uc774 \ub9ce\uc73c\uba74 \ucca0\uac11\uad81\ub3c4 \uace0\ub824\ud574\uc8fc\uc138\uc694.\n \n2\ucf54\uc5b4\ub294 \uc6d0\ub798 \ud3ed\uac08\uc774\ub098 \uace0\uc18d\uc5f0\uc0ac\ud3ec\ub97c \uc774\uc6a9\ud558\ub294\uac78 \uc88b\uc544\ud588\ub294\ub370 \ucd5c\uadfc\uc5d0\ub294 \ud53c\ubc14\ub77c\uae30\ub97c \uc560\uc6a9 \uc911\uc785\ub2c8\ub2e4. \uce58\uc18d + W\ud328\uc2dc\ube0c\ub97c \ud65c\uc6a9\ud574\uc11c \uacf5\uc18d\uc744 \ub9e4\uafb8\uace0 \uc57d\uc810\ud130\ud2b8\ub9ac\uba74 \ud53c\ud7612\ubc30\ub77c\ub294 \ud6a8\uacfc\uc640 \uacfc\ub2e4\uce58\uc720 \uc2dc\ub108\uc9c0\ub97c \ub0c5\ub2c8\ub2e4.\n \n3\ucf54\uc5b4\ub294 \ubb34\ud55c\uc758\ub300\uac80\uc774\ub098 \uc0c1\ub300 \ud0f1\ucee4 \ubc29\uc5b4\ub825\uc5d0 \ub530\ub77c\uc11c \ub3c4\ubbf8\ub2c9\uacbd\uc758\uc778\uc0ac \uc0ac\uc8fc\uc154\ub3c4 \ub429\ub2c8\ub2e4.\n \n\uadf8\ub9ac\uace0 \ud038 \uc2a4\ud0ac\uc21c\uc11c QEW\uc774\ub807\uac8c \ucc0d\ub294 \ubd84\ub4e4\uc774 \uaf64\ub098 \ub9ce\ub358\ub370 \ud038\uc740 \ucde8\uc57d \ud130\ud2b8\ub9ac\uba74\uc11c \ub098\uc624\ub294 W\uc2a4\ud0ac \ud328\uc2dc\ube0c\uc758 \uacf5\uc18d+\uc774\uc18d\uc774 \ud575\uc2ec\uc785\ub2c8\ub2e4. WQE\uac00 \uae30\ubcf8\uc774\uba70, \uc0c1\ub300\uac00 \ud314\uc774 \ub108\ubb34 \uae38\uac70\ub098 \ud3ec\ud0b9 \ub300\uce58\uad6c\ub3c4\uac00 \ub41c\ub2e4 \uc774\ub7f4 \ub54c\ub9cc QWE\ub85c \ucc0d\uc73c\uc138\uc694. \ubc29\uad00 \ud038\uc774\ub77c\uba74 \ubaa8\ub97c\uae4c \uc9c0\uc18d\ub51c\uc744 \ub123\uc5b4\uc57c \ud558\ub294 \uce58\uba85\ud0c0\ud038\uc740 QEW\ub85c \uc2a4\ud0ac \ucc0d\ub294\uac70 \uc544\ub2d9\ub2c8\ub2e4.\n \n \n\ud0a8\ub4dc\ub808\ub4dc\n\ud611\uace1\uc5d0\uc11c\uc758 \ucd94\uc138\n\ud611\uace1 \ud0a8\ub4dc\ub808\ub4dc\ub294 \uce7c\ubc14\ub78c\uacfc \ub2e4\ub974\uac8c \uc815\uae00 \ud3ec\uc9c0\uc158\uc774\uc8e0. \ud1b5\uacc4\uc0c1\uc73c\ub85c\ub294 \ud06c\ub77c\ucf04 \uc9d5\uc218\uc758\ucd1d (\uace0\uc18d\uc5f0\uc0ac\ud3ec) \ubb34\ud55c\uc758\ub300\uac80\uc744 \uac11\ub2c8\ub2e4.\n \n\ucc54\ud53c\uc5b8 \ubc38\ub7f0\uc2a4 \ud6a8\uacfc\n\uc785\ud788\ub294 \ud53c\ud574\ub7c9 \uc99d\uac00 10%\n\ubc1b\ub294 \ud53c\ud574\ub7c9 \uac10\uc18c 10%\n \n\uce7c\ubc14\ub78c \ucd94\ucc9c \ub8ec\n\uce58\uba85\uc801\uc18d\ub3c4/\uc815\ubcf5\uc790 \uce68\ucc29 \ud54f\ube5b\uae38 \uccb4\ub825\ucc28\uadf9\ubcf5(\ucd5c\ud6c4\uc758\uc800\ud56d) + \uc0ac\ub0e5\uc758\uc99d\ud45c \uad76\uc8fc\ub9b0\uc0ac\ub0e5\uafbc/\uad81\uadf9\uc758\uc0ac\ub0e5\uafbc\n \n\uce7c\ubc14\ub78c \ucd94\ucc9c \uc544\uc774\ud15c\n\uc218\ud638\uc790\uc758\ub9dd\uce58 \ud06c\ub77c\ucf04\ud559\uc0b4\uc790 \ub8e8\ub09c\uc758\ud5c8\ub9ac\ucf00\uc778(\uc815\uc218\uc57d\ud0c8\uc790) \ubb34\ud55c\uc758\ub300\uac80/\ub3c4\ubbf8\ub2c9\uacbd\uc758\uc778\uc0ac\n \n\ud0a8\ub4dc\ub808\ub4dc\ub294 \uc9d1\uc911\uacf5\uaca9, \uce58\uba85\uc801\uc18d\ub3c4, \uc815\ubcf5\uc790, \uce7c\ub0a0\ube44 \ubaa8\ub450 \uc0ac\uc6a9\ud560 \uc218 \uc788\ub294 \ucc54\ud504\ub85c \ucde8\ud5a5\uc73c\ub85c \ucc0d\uc5b4\uc8fc\uc2dc\uba74 \ub429\ub2c8\ub2e4.\n \n\ucd5c\ud6c4\uc758\uc800\ud56d vs \uccb4\ub825\ucc28\uadf9\ubcf5\n\uccb4\ub825\ucc28\uadf9\ubcf5\uc740 \uc218\ud638\uc790\uc758\ub9dd\uce58 \ube7c\uace4 \uccb4\ub825\uc544\uc774\ud15c\uc774 \uc5c6\ub2e4\ubcf4\ub2c8\uae4c \uccb4\ub825\ucc28\uadf9\ubcf5\ub3c4 \ud6a8\uc728\uc774 \uc798 \ub098\uae34 \ud558\uac70\ub4e0\uc694.\n\ub9cc\uc57d \uc0c1\ub300\uac00 \uccb4\ub825 \ub192\uc740 \ud0f1\ucee4\uac00 \uc788\uc73c\uba74 \ubd84\ub2f9 100\uc774\uc0c1 \ud655\uc2e4\ud788 \uc218\uce58\uac00 \uc5c4\uccad \uc798 \ub098\uc624\ub294\ub370 \ud0f1\ucee4\uac00 \uc5c6\ub294 \ud310\uc5d0\uc11c\ub294 \uac70\uc758 \uc4f8\ubaa8\uac00 \uc5c6\uae30\ub3c4 \ud574\uc694. \uadf8\ub798\uc11c \uccb4\ub825\ucc28\uadf9\ubcf5\uc740 \uc880 \uae30\ubcf5\uc774 \uac08\ub9ac\ub294 \ub8ec\uc778\ub370 \ub300\uccb4\ub85c \ud0f1\ucee4 \uc870\uae08\ub9cc \uc788\uc5b4\ub3c4 \ud6a8\uc728\uc740 \uc88b\uae34 \ud558\ub2e4 \uc774\uc815\ub3c4\uc774\uace0\uc694.\n \n\ucd5c\ud6c4\uc758\uc800\ud56d\uc740 \ud0a8\ub4dc\uac00 \uad81\uc4f0\uace0 \ub538\ud53c\ub418\uc5c8\uc744 \ub54c \uc918\ud328\uac70\ub098 \uc774\ub7f4 \ub54c \ud6a8\uc728\uc774 \uc798 \ub098\uc640\uc11c \ubd84\ub2f9 50~100\uc815\ub3c4\ub294 \ub098\uc624\ub354\ub77c\uace0\uc694. \uadf8\ub798\uc11c \uc694\uc0c8\ub294 \ucd5c\ud6c4\uc758\uc800\ud56d\uc744 \ub354 \ub9ce\uc774 \ub4e4\uace0 \uc788\uc2b5\ub2c8\ub2e4. \ub9cc\uc57d, \ud53d\ucc3d \ub2e8\uacc4\uc5d0\uc11c \uc6b0\ub9ac\ud300 \uc870\ud569\uc774 \ud0f1\ucee4 \uc7a1\uc744 \uc870\ud569\uc774 \uc544\ub2cc\ub370? \uc774\ub7ec\uba74 \uccb4\ub825\ucc28\uadf9\ubcf5\uc744 \ub4e4\uc5b4\uc8fc\uc138\uc694.\n \n\ud0a8\ub4dc\ub294 \uc218\ud638\uc790\uc758\ub9dd\uce58 \uac00\uae09\uc801 \uc0ac\uc8fc\uc138\uc694. \uc218\ud638\uc790\uc758\ub9dd\uce58 + \ud54f\ube5b\uae38\uacfc \ud568\uaed8 W\uc2a4\ud0ac \ud328\uc2dc\ube0c\ub54c\ubb38\uc5d0 \uc720\uc9c0\ub825\uc774 \uc88b\uc740 \ud3b8\uc774\ub77c\uc11c \ud06c\ub77c\ucf04\uc744 \uac00\uace0 \uc788\uc2b5\ub2c8\ub2e4.\n \n2\ucf54\uc5b4\ub294 \uc815\uc218\uc57d\ud0c8\uc790 \uc720\ub839\ubb34\ud76c \ub8e8\ub09c \ub4f1 \ucde8\ud5a5\uc5d0 \ub530\ub77c\uc11c \uac00\uc8fc\uc2dc\uba74 \ub429\ub2c8\ub2e4.\n \n\ud0a8\ub4dc\ub294 \uc0ac\uc2e4 \uce7c\ubc14\ub78c \ubc84\ud504 \ubc1b\uc740 \uc0c1\ud0dc\ub77c \ubb58 \uc62c\ub824\ub3c4 \ub51c \ud558\ub098\ub9cc\ud07c\uc740 \uc5c4\uccad \uc388 \uc0c1\ud0dc\uc785\ub2c8\ub2e4.\n \n \n\ud2b8\ub9ac\uc2a4\ud0c0\ub098\n\ud611\uace1\uc5d0\uc11c\uc758 \ucd94\uc138\n\ud611\uace1 \ud2b8\ub9ac\uc2a4\ud0c0\ub098\ub294 \ub3cc\ud48d \ub108\ud504 \uc774\ud6c4\uc5d0\ub294 \ud06c\ub77c\ucf04 \uc704\uc8fc\uc5d0\ub2e4\uac00 \ub3cc\ud48d\ub3c4 \uc885\uc885 \ub098\uc624\ub294 \ucd94\uc138\uc785\ub2c8\ub2e4.\n2\ucf54\uc5b4\uc5d0\ub294 \uc6d0\ub798 \ud3ed\uac08\uc774\ub098 \uc9d5\uc218\ub97c \ub9ce\uc774 \uac14\ub294\ub370 \uc720\ub839\ubb34\ud76c \ubc84\ud504 \uc774\ud6c4\ub85c \uc720\ub839\ubb34\ud76c\uac00 \ub300\uc138\uc785\ub2c8\ub2e4.\n \n\uce7c\ubc14\ub78c\uc5d0\uc11c\ub3c4 \ube44\uc2b7\ud558\uac8c \uac00\uba74 \ub418\ub294\ub370, \uc801\ud300\uc5d0 \uadfc\uc811 \ub9ce\uc73c\uba74 \ub8e8\ub09c\ub3c4 \uc885\uc885 \uc0bd\ub2c8\ub2e4.\n \n\ucc54\ud53c\uc5b8 \ubc38\ub7f0\uc2a4 \ud6a8\uacfc\n\uc5c6\uc74c\n \n\uce7c\ubc14\ub78c \ucd94\ucc9c \ub8ec\n\uce7c\ub0a0\ube44 \ud53c\uc758\ub9db \uc0ac\ub0e5\uc758\uc99d\ud45c \uad76\uc8fc\ub9b0\uc0ac\ub0e5\uafbc + \ud54f\ube5b\uae38 \uacfc\ub2e4\uce58\uc720/\uccb4\ub825\ucc28\uadf9\ubcf5 \n\uc9d1\uc911\uacf5\uaca9 \uacfc\ub2e4\uce58\uc720 \ud54f\ube5b\uae38 \uccb4\ub825\ucc28\uadf9\ubcf5 + \uc0ac\ub0e5\uc758\uc99d\ud45c(\ud53c\uc758\ub9db) \uad76\uc8fc\ub9b0\uc0ac\ub0e5\uafbc\n \n\uce7c\ubc14\ub78c \ucd94\ucc9c \uc544\uc774\ud15c\n\uc218\ud638\uc790\uc758\ub9dd\uce58 \ud06c\ub77c\ucf04\ud559\uc0b4\uc790(\ub3cc\ud48d) \uace0\uc18d\uc5f0\uc0ac\ud3ec/\uc720\ub839\ubb34\ud76c \ubb34\ud55c\uc758\ub300\uac80/\ub3c4\ubbf8\ub2c9\uacbd\uc758\uc778\uc0ac\n \n\ud2b8\ud0c0\ub294 \ud06c\uac8c \uc9d1\uc911\uacf5\uaca9\ub8ec\uacfc \uce7c\ub0a0\ube44\ub8ec 2\uac1c\ub85c \uac08\ub9ac\ub294\ub370\uc694. \uc694\uc0c8 \ud611\uace1\uc5d0\uc11c\ub3c4 \uce7c\ub0a0\ube44\ub97c \ub354 \ub9ce\uc774 \uc0ac\uc6a9\ud558\ub294 \ucd94\uc138\uc774\uace0 \ub354\uc6b1 \uce7c\ubc14\ub78c\uc5d0\uc120 \ucd08\ubc18\uc5d0 \ub418\uac8c \ub2f5\ub2f5\ud55c \ubd80\ubd84\uc774 \ub9ce\uace0 \uacac\uc81c\uae30\ub3c4 \ub531\ud788 \uc5c6\uc5b4\uc11c \uce7c\ub0a0\ube44\ub97c \ucd94\ucc9c\ub4dc\ub9bd\ub2c8\ub2e4.\n \n\uc218\ud638\uc790\uc758\ub9dd\uce58\ub294 \uaf2d \uc0ac\uc8fc\uc138\uc694. \ud2b8\ud0c0\ub294 \ub9c8\ub098\uc18c\ubaa8\uac00 \ud070 \uc6d0\ub51c\uc740 \uc544\ub2c8\uae30 \ub54c\ubb38\uc5d0 \uc5ec\uc2e0\uc758\ub208\ubb3c \uc548 \uc0ac\uace0 \uacfc\ub2e4\uce58\uc720 \ucc0d\uc5b4\ub3c4 \ub429\ub2c8\ub2e4. \uc544\uad70 \uc870\ud569\uc0c1 \ud0f1\ucee4\ub97c \uc798 \ubabb \uc7a1\ub294 \uc870\ud569\uc774\uba74 \uacfc\ub2e4\uce58\uc720 \ube7c\uace0 \uccb4\ub825\ucc28\uadf9\ubcf5 \ub123\uc5b4\uc8fc\uc138\uc694.\n \n1\ucf54\uc5b4 \ud06c\ub77c\ucf04\ud559\uc0b4\uc790 \ud639\uc740 \ub3cc\ud48d \uac00\uc8fc\uc2dc\uba74 \ub429\ub2c8\ub2e4. \ud06c\ub77c\ucf04\uc774 \uc815\uc11d\uc774\uace0 \ub3cc\ud48d\ub3c4 \ud2b8\ud0c0\uc5d0\uac8c \ucd5c\uc545\uc758 \uc120\ud0dd\uc740 \uc544\ub2c8\ub77c \ub3cc\ud48d\ub3c4 \uc120\ud0dd\uc801\uc73c\ub85c \uace0\ub97c \uc218 \uc788\uc2b5\ub2c8\ub2e4. \ucca0\uac11\uad81\uc740 \uac70\uc758 \uc548\uac11\ub2c8\ub2e4.\n \n2\ucf54\uc5b4\ub294 \uc6d0\ub798 \ud611\uace1\ucc98\ub7fc \uc720\ub839\ubb34\ud76c\uac00\ub294 \uac83\uc744 \uc120\ud638\ud588\uc5c8\ub294\ub370 \ucd5c\uadfc\uc5d0\ub294 \uc6d0\ub51c\ub85c \uc801 \ub2e4\uc774\uc560\ub098 \uce74\ubc00 \ube44\uc5d0\uace0 \ub4f1\uc774 \ub108\ubb34 \ubd80\ub2f4\ub418\uc11c \uc0ac\uac70\ub9ac\ub97c \ucd5c\ub300\ud55c \uc548\uc8fc\uac70\ub098 \uae34 \ud2b8\ud0c0 \uc0ac\uac70\ub9ac\ub97c \uc774\uc6a9\ud574\uc11c \ud55c\ub300 \uce58\uace0 \uc624\uae30 \uc704\ud574\uc11c \uace0\uc18d\uc5f0\uc0ac\ud3ec\ub97c \uc120\ud638\ud558\uace0 \uc788\uc2b5\ub2c8\ub2e4. \ub9cc\uc57d, \ub300\uce58\uad6c\ub3c4\uc5d0\uc11c \ud1a1\ud1a1 \uce58\ub294\uac70\ubcf4\ub2e4 \uc9c0\uc18d\uc801\uc778 \ub51c\uc774 \ud544\uc694\ud55c \uad6c\ub3c4\uc5d0\uc11c\ub294 \uc720\ub839\ubb34\ud76c \uc0ac\uc8fc\uc2dc\uba74 \ub429\ub2c8\ub2e4.\n \n3\ucf54\uc5b4 \ud3ed\ud48d\uac08\ud034/\ubb34\ud55c\uc758\ub300\uac80\uc774/\ub3c4\ubbf8\ub2c9\uacbd\uc758\uc778\uc0ac \uac00\uc8fc\uc2dc\uba74 \ub429\ub2c8\ub2e4.\n \n \n\ud2b8\uc704\uce58\n\ud611\uace1\uc5d0\uc11c\uc758 \ucd94\uc138\n\ud611\uace1 \ud2b8\uc704\uce58\ub294 \ud604\uc7ac \ub9c8\ubc95\uc0ac\uc758\ucd5c\ud6c4(or\ubab0\ub77d\ud55c\uc655\uc758\uac80) \ub8e8\ub09c\uc758\ud5c8\ub9ac\ucf00\uc778 \uac70\ub300\ud55c\ud788\ub4dc\ub77c \uce60\ud751\uc758\uc591\ub0a0\ub3c4\ub07c\ub85c \uc774\uc5b4\uc9c0\ub294 \uc2e0\uaddc \ud15c\ud2b8\ub9ac\uac00 \ub728\uace0 \uc788\uc2b5\ub2c8\ub2e4. \uadf8\ub9ac\uace0 \ud611\uace1 \ud2b8\uc704\uce58\ub294 \uc9d1\uc911\uacf5\uaca9\ub8ec\uc744 \ub354 \ub9ce\uc774 \uc0ac\uc6a9\ud569\ub2c8\ub2e4. \uc9d1\uc911\uacf5\uaca9\uc774 2:2\uc5d0\uc11c \ud0ac\uac01\ub0b4\uae30 \uc88b\uac70\ub4e0\uc694. \n \n\uce7c\ubc14\ub78c \ud2b8\uc704\uce58\ub294 \uad81\uc744 \ud65c\uc6a9\ud558\uc5ec \uc5ec\ub7ff\uc744 \uc7a1\uc544\uc57c \ud574\uc11c \uce7c\ub0a0\ube44\ub098 \uce58\uba85\uc801\uc18d\ub3c4\ucc98\ub7fc \uacf5\uaca9\uc18d\ub3c4\ub97c \ud65c\uc6a9\ud574 \ub2e4\uc218\ub97c \uc7a1\uc744 \uc218 \uc788\ub294 \ub8ec\uc774 \ub354 \uc88b\ub2e4\uace0 \uc0dd\uac01\ud569\ub2c8\ub2e4. \uadf8\ub9ac\uace0 \ub9c8\ucd5c \ub8e8\ub09c \uac70\ub4dc\ub77c \ube4c\ub4dc\ub294 \uc548\uc815\uc801\uc774\uae34 \ud558\uc9c0\ub9cc, \uce7c\ubc14\ub78c\uc5d0\uc120 \uce58\uba85\ud0c0 \ube4c\ub4dc \ud0c0\ub294\uac8c \ud6e8\uc52c \ub354 \uac15\ub825\ud558\ub2e4\uace0 \uc0dd\uac01\ud569\ub2c8\ub2e4.\n \n\ucc54\ud53c\uc5b8 \ubc38\ub7f0\uc2a4 \ud6a8\uacfc\n\uc5c6\uc74c\n \n\uce7c\ubc14\ub78c \ucd94\ucc9c \ub8ec\n\uce7c\ub0a0\ube44 \ub3cc\ubc1c\uc77c\uaca9 \uc0ac\ub0e5\uc758\uc99d\ud45c \uad76\uc8fc\ub9b0\uc0ac\ub0e5\uafbc(\uad81\uadf9\uc758\uc0ac\ub0e5\uafbc) + \uce68\ucc29 \ud54f\ube5b\uae38\n\uce58\uba85\uc801\uc18d\ub3c4 \uce68\ucc29 \ud54f\ube5b\uae38 \uccb4\ub825\ucc28\uadf9\ubcf5 + \uc0ac\ub0e5\uc758\uc99d\ud45c \uad76\uc8fc\ub9b0\uc0ac\ub0e5\uafbc(\uad81\uadf9\uc758\uc0ac\ub0e5\uafbc)\n \n\uce7c\ubc14\ub78c \ucd94\ucc9c \uc544\uc774\ud15c\n\uc218\ud638\uc790\uc758\ub9dd\uce58 \ud06c\ub77c\ucf04\ud559\uc0b4\uc790(\ubd88\uba78\uc758\ucca0\uac11\uad81) \ub8e8\ub09c\uc758\ud5c8\ub9ac\ucf00\uc778 \ubb34\ud55c\uc758\ub300\uac80/\ub3c4\ubbf8\ub2c9\uacbd\uc758\uc778\uc0ac\n \n\uce7c\ub0a0\ube44\uc640 \uce58\uba85\uc801\uc18d\ub3c4\ub294 \ucde8\ud5a5\ucc28\uc774\uc785\ub2c8\ub2e4. \uce7c\ub0a0\ube44\ub294 \uc9e4\uc9e4\uc774 \uce58\uae30 \uc88b\uace0, \ub51c\ub7ec\ub4e4 \uc0c1\ub300\ub85c \ub530\ub974\uac8c 3\ud0c0 \ub54c\ub824\ub123\uc744 \uc218 \uc788\uc5b4\uc11c \uc88b\uace0, \uce58\uba85\uc801\uc18d\ub3c4\ub294 \uccb4\ub825\ucc28\uadf9\ubcf5\uc744 \ucc0d\uc744 \uc218 \uc788\uace0 \uc9c0\uc18d\ub51c\uc774 \uce7c\ub0a0\ube44\ubcf4\ub2e4 \ub354 \uac15\ud558\uae30 \ub54c\ubb38\uc5d0 \ud0f1\ucee4\ub958 \ub54c\ub824 \uc7a1\uae30\uc5d0 \uc88b\uc2b5\ub2c8\ub2e4. \uc6b0\ub9ac\ud300 \uc870\ud569\uc0c1 \ud0f1\ucee4 \uc7a1\ub294\uac8c \ud544\uc694\ud558\uace0 \uc11c\ud3ec\ud305\uc774 \uc88b\uc73c\uba74 \uce58\uba85\uc801\uc18d\ub3c4 \uc88b\uc544\uc694.\n \n\uad76\uc8fc\ub9b0\uc0ac\ub0e5\uafbc \ub300\uc2e0 \uad81\uadf9\uc758\uc0ac\ub0e5\uafbc \ucc0d\uc73c\uc2dc\ub294 \ubd84\ub4e4\ub3c4 \uc788\uc2b5\ub2c8\ub2e4. \uc800\ub294 \ud06c\ub77c\ucf04\uc744 \uc8fc\ub85c \uc88b\uc544\ud574\uc11c \uad81\uc0ac\uae4c\uc9c0 \ucc0d\uc73c\uba74 \ud53c\ud761\uc774 \uc880 \ud6c4\ub2ec\ub9b0 \uacbd\uc6b0\uac00 \ub9ce\uc544\uc11c \uc798 \uc548\ucc0d\uc9c0\ub9cc, \ub9cc\uc57d \ubcf8\uc778\uc774 \ucca0\uac11\uad81\uc744 \uc120\ud638\ud558\uac70\ub098 \uc6b0\ub9ac\ud300 \uc11c\ud3ec\ud305\uc774 \uc88b\ub124? \uc774\ub7ec\uba74 \uad81\uadf9\uc758\uc0ac\ub0e5\uafbc \ucc0d\uc5b4\uc904 \uc218\ub3c4 \uc788\uaca0\uc8e0.\n \n1\ucf54\uc5b4 \uac00\uae09\uc801 \ud06c\ub77c\ucf04\ud559\uc0b4\uc790. \ub2e4\ub9cc, \uc0c1\ub300 \ud3ec\ud0b9\uc774 \uc9c0\ub098\uce58\uac8c \uc384\uc11c \ubc84\ud2f8 \uc218 \uc5c6\uc744 \uacbd\uc6b0\uc5d0\ub9cc \ubd88\uba78\uc758\ucca0\uac11\uad81 \uad6c\ub9e4\n \n2\ucf54\uc5b4 \ub8e8\ub09c\uc758\ud5c8\ub9ac\ucf00\uc778. \uc0ac\uc2e4 \ud2b8\uc704\uce58\ub294 \uc740\uc2e0\uc73c\ub85c \ub4e4\uc5b4\uac00\uc11c \ubd99\uc5b4\uc11c \ub54c\ub9ac\uba74 \ub418\uae30 \ub54c\ubb38\uc5d0 \uc801 \uc0ac\uac70\ub9ac\uac00 \uae34 \uc870\ud569\uc5d0\uc11c\ub3c4 \ub8e8\ub09c\uc744 \uc548 \uac00\ub294 \ud310\uc740 \ub4dc\ubb3c\ub2e4\uace0 \ubcf4\uba74 \ub429\ub2c8\ub2e4.\n \n3\ucf54\uc5b4 \ubb34\ud55c\uc758\ub300\uac80 or \ub3c4\ubbf8\ub2c9\uacbd\uc758\uc778\uc0ac \uad6c\ub9e4. \uc77c\ubc18\uc801\uc73c\ub85c\ub294 \ubb34\ud55c\uc758\ub300\uac80. \uc0c1\ub300 \ud0f1\ucee4 \uc7a1\uae30\uac00 \ud798\ub4e4\uace0 \uc55e\ub77c\uc778\ubd80\ud130 \ub6ab\uc5b4\uc918\uc57c \ud558\uba74 \ub3c4\ubbf8\ub2c9\uacbd\uc758\uc778\uc0ac.\n \n4\ucf54\uc5b4 \ubb34\ud55c\uc758\ub300\uac80 \ub3c4\ubbf8\ub2c9\uacbd\uc758\uc778\uc0ac \ud53c\ubc14\ub77c\uae30 \uc2dc\ubbf8\ud130 \ub9ec\ubaa8\uc154\uc2a4\uc758\uc544\uadc0 \ub4f1 \uc0c1\ud669\uc5d0 \ub9de\ub294 \uc544\uc774\ud15c \uad6c\ub9e4.\n \n+ \uc801\ub2f9\ud55c \ucf54\uc5b4\uac00 \ub098\uc654\uc744 \ub54c \ud2f0\uc544\uba67 \ud558\ub098\ub9cc \uc0ac\uc8fc\ub294\uac70 \uad49\uc7a5\ud788 \uc88b\uc544\uc694.\n \n \n\ub05d.\n\n",renderers:{Link:o},escapeHtml:!1})]})})}}}]);
